/* ¡¡Cosas básicas!! */

html body {
    background-color: #121212;
}

a.bigusername font {
    text-shadow: #1D8B7F 0px 1px 3px;
}

td.alt2 {
    padding: 4px;
}

td.thead {
    font-size: 10px;
    color: #FF8933;
}

#cybtopstats_ltitle_area {
