css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ebash.at") {

/* Specially for toribash.ru \ ebash.at by BFG */

/* Основы основ */
body {background:rgb(70, 70, 70) !important; font-size:90% !important; color:rgb(240, 240, 240) !important;}
::-moz-selection {background:rgb(255, 160, 66); color:rgb(0, 0, 0)}
::selection {background:rgb(255, 160, 66); color:rgb(0, 0, 0)}
a {color:rgb(255, 160, 66) !important;}
strong {color:rgb(240, 240, 240) !important;}
div {background:rgb(70, 70, 70) !important; border-color:rgb(0,0,0) !important;}
.sticky {background:rgb(70, 70, 70) !important; color:rgb(255, 255, 255) !important;}
.brd-page{top:-100px !important;}
input[type="button"] {color:rgb(0, 0, 0) !important;}
input[type="text"] {color:rgb(255, 160, 66) !important; background:rgb(90, 90, 90) !important;}


/* Главная */
#brd-index {border-color:rgb(0,0,0) !important;}
#brd-visit {background:rgb(70, 70, 70) !important;}
#brd-head {visibility:hidden !important;}
#welcome {color:rgb(255, 128, 0) !important;}
#brd-navlinks {background:rgb(200, 120, 50) !important;} #brd-navlinks a {color:rgb(70, 70, 70) !important;}
#brd-announcement {background:rgb(100, 100, 100) !important; color:rgb(255, 140, 50); border-style:double !important; border-color:rgb(255, 128, 0) !important;}
div.content {background:rgb(100, 100, 100) !important;}
#brd-online {background:rgb(70, 70, 70) !important;}
#brd-stats {background:rgb(70, 70, 70) !important;}
/*span.modlist {visibility:hidden !important;}      Скрытие модераторов.*/


#brd-wrap{width:100% !important;}
#brd-crumbs-top {background:rgb(70, 70, 70) !important;}
#brd-pagepost-top {background:rgb(70, 70, 70) !important;}
div.main-head {background:rgb(90, 90, 90) !important;}

/* Таблицы */
li {color:rgb(240, 240, 240) !important; border-color:rgb(0,0,0) !important;}
li.info-forum {background:rgb(70, 70, 70) !important;}
li.info-replies {background:rgb(70, 70, 70) !important;}
li.info-topics {background:rgb(70, 70, 70) !important;}
li.info-posts {background:rgb(90, 90, 90) !important;}
li.info-lastpost {background:rgb(70, 70, 70) !important;}
div.item-subject {background:rgb(70, 70, 70) !important;}
p.item-summary {background:rgb(70, 70, 70) !important;}
.main-item {border-color:rgb(0,0,0) !important;}
em.closed {color:rgb(0, 0, 0) !important;}

/* Иконки */
span.icon.new {background:url("data:image/png;base64,") !important;}
span.icon {background:rgb(70, 70, 70) !important;}

/* В топике */
.post-ident {background:rgb(70, 70, 70) !important;}
.post-options {background:rgb(90, 90, 90) !important;}
.post-contacts {background:rgb(90, 90, 90) !important;}
#pun_bbcode_buttons input {color:rgb(0, 0, 0) !important;}
span.sumbit input {color:rgb(0, 0, 0) !important;} 
div.spoiler-header {background:rgb(90, 90, 90) !important;}
span.post-reputation-plus {color:rgb(0, 255, 0) !important;}
p.infraction {background:rgb(90, 90, 90) !important; border-color:rgb(255, 128, 0) !important;}
.infraction-title {color:rgb(200, 0, 0) !important;}
.infraction-comment {color:rgb(200, 200, 200) !important;}
.postfoot {background:rgb(90, 90, 90) !important;}
ul.author-info span {color:rgb(200, 200, 200) !important;}
div.posthead h3.hn {background:rgb(90, 90, 90) !important;}

/* Репутация */
td {background:rgb(90, 90, 90) !important;  border-color:rgb(0,0,0) !important;}
th.tc3 {background:rgb(90, 90, 90) !important;}

/* Профиль */
li.usertitle {color:rgb(255, 160, 66) !important;}
span {color:rgb(255, 180, 46)}
label {color:rgb(240, 240, 240) !important;}
h3.ct-legend.hn {color:rgb(255, 180, 46) !important;}
#brd-profile-about div.main-content.main-frm span {color:rgb(255, 180, 46) !important;} 
#brd-profile-avatar div.main-content.main-frm span {color:rgb(255, 180, 46) !important;}

/* Прочее */
th {background:rgb(90, 90, 90) !important;}
h3 {border-color:rgb(0,0,0) !important;}
label {background:rgb(70, 70, 70) !important;}
textarea {color:rgb(255, 255, 255) !important; background:rgb(70, 70, 70) !important;} 

/* Группы */
span.group_color_1 a {color:rgb(255, 0, 0) !important;}
span.group_color_2 a {color:rgb(0, 0, 0) !important;}
span.group_color_3 a {color:rgb(255, 160, 66) !important;}
span.group_color_4 a {color:rgb(255, 255, 255) !important;}
span.group_color_5 a {color:rgb(66, 170, 255) !important;}




.post-title {background:rgb(90, 90, 90) !important; border-color:rgb(0,0,0) !important;}
}
