css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(tarvalon.net)
{
    body * {
        font-family: monospace !important;
    }
    
    img {
        opacity: .3;
    }
    
    .tv-decobar {
        background: white !important;
    }
    
    #header-inner {
        display: none !important;
    }
    
    #primary {
        margin-top: 10px;
        margin-left: 0 !important;
    }
    
    #primary ul.links li a {
        background: none !important;
    }
    
    #primary ul.links li a:hover {
        background: none !important;
        color: inherit !important;
        text-decoration: underline !important;
    }
    
    #sidebar-left, #sidebar-left * {
        color: black !important;
        background: white !important;
        width: 0 !important;
        margin: 0 !important;
    }
    
    #sidebar-left-inner #block-accordion_menu-0,
    #sidebar-left-inner #block-menu-menu-members-menu {
        display: none !important;
    }
    
    #sidebar-left-inner #block-bookmarks-0 {
        
    }
    #sidebar-left-inner #block-bookmarks-0 .title {
        display: none;
    }
    #sidebar-left-inner #block-bookmarks-0 ul.menu {
        width: 800px !important;
        position: absolute !important;
        float: left !important;
        margin: 0 !important;
        margin-top: 10px !important;
        padding: 0 !important;
        list-style-type: none !important;
        list-style-image: none !important;
    }
    #sidebar-left-inner #block-bookmarks-0 ul.menu li {
        list-style-type: none !important;
        list-style-image: none !important;
        display: inline;
        border-right: 1px solid black;
        padding-right: 5px !important;
    }
    #sidebar-left-inner #block-bookmarks-0 ul.menu li a {
        font-size: 0.8em;
        text-decoration: none !important;
    }
    #sidebar-left-inner #block-bookmarks-0 ul.menu + div {
        display: none;
    }
    
    #phpbbforum-page {
        width: 100% !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }

    #phpbbforum-page .bg1,
    #phpbbforum-page .bg2,
    #phpbbforum-page .rules,
    #phpbbforum-page .panel,
    #phpbbforum-page .navbar,
    #phpbbforum-page .forumbg {
        background: white !important;
    }
    
    #phpbbforum-page ul.topiclist li.header * {
        color: black !important;
    }
    
    #phpbbforum-page .buttons span {
        display: none;
    }
    
    #phpbbforum-page .pagination * {
        background-color: white !important;
        color: black !important;
        border: 0 !important;
    }
    
    #phpbbforum-page a.top {
        background: none !important;
        color: black;
        overflow: visible;
    }
    
    #phpbbforum-page a.button1, 
    #phpbbforum-page input.button1,
    #phpbbforum-page input.button3, 
    #phpbbforum-page a.button2,
    #phpbbforum-page input.button2,
    #phpbbforum-page a.sendemail,
    #phpbbforum-page a.print {
        background: none !important;
    }
    
    #phpbbforum-page .icon-ucp,
    #phpbbforum-page .icon-faq,
    #phpbbforum-page .icon-members,
    #phpbbforum-page .icon-logout,
    #phpbbforum-page .icon-home,
    #phpbbforum-page .icon-unsubscribe,
    #phpbbforum-page .icon-bookmark {
        background: none !important;
    }
    
    #phpbbforum-page .headerbar {
        background: none !important;
        color: black !important;
    }
    
    #phpbbforum-page .forabg {
        background: none !important;
    }
    
    #phpbbforum-page ul.forums {
        background: none !important;
    }

    #phpbbforum-page .postprofile dt img {
        width: 35px !important;
        height: 55px !important;   
    }
    
    #phpbbforum-page .profile-icons {
        display: none;
    }

    #phpbbforum-page .postprofile {
        width: 10% !important;
    }
    
    #phpbbforum-page .postbody {
        width: 89% !important;
    }
    
    #phpbbforum-page .signature * {
        color: inherit !important;
    }
    
    #phpbbforum-page .signature img {
        display: none;
        max-width: 50px !important;
        max-height: 50px !important;
    }
    
    #phpbbforum-page #message-box {
        width: 95% !important;
    }
    
    #phpbbforum-page #smiley-box {
        display: none;
    }
}
