css:
#navigator-toolbox { padding-bottom: 0px !important; }

#browser { margin-top: -1px !important; }

#navigator-toolbox[iconsize="small"]:-moz-system-metric(windows-default-theme) #PersonalToolbar {
height: 26px !important;
max-height: 26px !important;
min-height: 26px !important;
margin-bottom:0px !important;
border-bottom: 1px solid #888 !important;
}

#navigator-toolbox[iconsize="large"]:-moz-system-metric(windows-default-theme) #PersonalToolbar {
height: 26px !important;
max-height: 26px !important;
min-height: 26px !important;
margin-bottom:0px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton {
  -moz-box-orient: horizontal !important;
  -moz-appearance: toolbarbutton !important;
  margin: 0 !important;
  padding: 0 5px !important;
  font-size:11px !important;
}

#navigator-toolbox:-moz-lwtheme #PersonalToolbar toolbarbutton {
font-weight:bold !important;
}

#navigator-toolbox:not([mode="icons"]) #PersonalToolbar > toolbarbutton > .toolbarbutton-icon,
#navigator-toolbox #PersonalToolbar > #personal-bookmarks toolbarbutton .toolbarbutton-icon:not([label=""]) {
margin-right: 2px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton .toolbarbutton-text {
padding-right: 1px !important;
}

#navigator-toolbox #PersonalToolbar .toolbarbutton-1,
#navigator-toolbox #PersonalToolbar .toolbarbutton-menubutton-button {
padding-right: 3px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton[type="menu-button"] > toolbarbutton {
padding-right: 0px !important;
}

#navigator-toolbox > #PersonalToolbar toolbarbutton[dragover="true"][open="true"] {
background-color: transparent !important;
background-image: none !important;
}

#navigator-toolbox > #PersonalToolbar > toolbarpaletteitem[dragover="right"] {
background-position: right 0 !important;
}

#main-window #navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarbutton .toolbarbutton-icon {
max-height: 16px !important;
max-width: 16px !important;
}

#navigator-toolbox[iconsize="small"] #PersonalToolbar toolbarbutton,
#navigator-toolbox[iconsize="small"] #PersonalToolbar toolbarbutton.chevron {
  height: 20px !important;
  max-height: 20px !important;
  min-height: 20px !important;
}

#navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarbutton,
#navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarbutton.chevron {
  height: 20px !important;
  max-height: 20px !important;
  min-height: 20px !important;
}

#navigator-toolbox[iconsize="large"] #nav-bar {
  overflow: hidden !important;
}

#navigator-toolbox:-moz-system-metric(windows-compositor):not(:-moz-lwtheme) #nav-bar, #PersonalToolbar:-moz-system-metric(windows-compositor):not(:-moz-lwtheme)  {
background-color:#e5e5e5 !important;
}

#main-window[tabsontop="true"] #appmenu-button 
{ 
     padding: 3px !important; 
}

#appmenu-button
{
    padding: 1px 5px 1px 5px !important;
    height: 19px !important;
    min-width: 43px !important;
    width: 43px !important;
}

#appmenu-button .button-text
{ 
    color: transparent !important;
    text-shadow: none !important;
    margin-left: -27px !important;
    margin-right: -33px !important;
    background: url("data:image/png;base64,") no-repeat 16px !important;
}

#appmenu-button dropmarker
{ 
    margin: 0 1px 0 0 !important; 
}


#appmenu-button-container
{
    position: fixed !important; 
    top: 1px !important;
}

#navigator-toolbox[tabsontop="true"] #TabsToolbar
{
box-shadow: none !important;
   background: transparent !important;
}

toolbar[iconsize="large"] #urlbar {
  padding: 1px !important;
}

toolbar[iconsize="small"] #urlbar {
  height:21px !important;
}

toolbar[mode="icons"][currentset*="urlbar-container"] #urlbar {
  border-radius: 2px 4px 4px 2px !important;
  border: 1px solid #888 !important;
  box-shadow: none !important;
  background:#ccc !important;
  opacity: 0.8 !important;
  font-family: "Segoe UI Semibold" !important;
  font-size:12px !important;
}


.searchbar-textbox {
  box-shadow: none !important;
  border-radius: 2px !important;
  background:#ccc !important;
  border: 1px solid #888 !important;
  padding:0 !important;
  opacity: 0.8 !important;
  font-family: "Segoe UI Semibold" !important;
  height:21px !important; 
  font-size:12px !important;
}


.searchbar-textbox:active {
  background:#eee !important;
}

#urlbar-container + #reload-button {
  -moz-image-region: rect(0 11px 12px 0) !important;
  padding: 0 !important;
}

#main-window[tabsontop="true"] #toolbar-menubar
{
margin-left: 45px !important;
}

#navigator-toolbox[tabsontop="true"]:-moz-system-metric(windows-default-theme) #TabsToolbar {
   margin-top:-8px !important;
}
#navigator-toolbox[tabsontop="true"]:-moz-system-metric(windows-compositor) #TabsToolbar {
   margin-top:1px !important;
}

#status-bar,statusbarpanel {
  padding-top: 1px !important;
}

.tabbrowser-tab:not([pinned]) {
  min-width: 40px !important;
}

.tabbrowser-tabs tab, .tabs-newtab-button {
background: rgba(242,242,242,0.3) !important;
-moz-border-image:none !important;
border-radius: 2px 2px 0 0 !important;
border: 1px solid #666 !important;
padding-top: 4px !important;
padding-left:3px !important;
padding-right:3px !important;
opacity: 1.0 !important;
font-size:11px !important;
}
.tabbrowser-tabs tab:-moz-lwtheme, .tabs-newtab-button:-moz-lwtheme {
border-bottom:none !important;
}
.tabbrowser-tabs tab:not([selected="true"]), .tabs-newtab-button {
min-height:22px !important;
max-height:22px !important;
margin-top:2px !important;
}
#TabsToolbar {
height:30px !important;
max-height:30px !important;
}
#TabsToolbar:-moz-system-metric(windows-compositor) {
height:26px !important;
max-height:26px !important;
}
#main-window[sizemode="maximized"] #TabsToolbar:-moz-system-metric(windows-compositor) {
height:24px !important;
max-height:24px !important;
}
#main-window[sizemode="maximized"] #TabsToolbar:not(:-moz-system-metric(windows-compositor)) {
height:24px !important;
max-height:24px !important;
}

.tabbrowser-tabs tab:not([selected="true"]):-moz-system-metric(windows-compositor):not(:-moz-lwtheme) {
color:#fff !important;
font-weight:bold !important;
text-shadow: 0 0 6px #887 !important;
}

tab[selected="true"]:not(:-moz-lwtheme) {
background: rgba(242,242,242,1.0) !important;
color: #228 !important;
font-weight:bold !important;
border-bottom: none !important;
border-top:2px solid #c78844 !important;
opacity: 1.0 !important;
text-shadow: 1px 1px 1px white !important;
}
#bookmarks-menu-button > .toolbarbutton-text {
  display: none !important;
}

#identity-box {
  background: transparent !important;
}
