css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.linux.org.ru/) {

body {
    max-width: 100% !important;
}

.boxlet {
    border-radius: 3px !important;
    border: 1px solid #3E4648 !important;
    width: 210px !important;
    padding: 8px 8px 8px 8px !important;
}

.infoblock {
    border-radius: 3px !important;
    border: 1px solid #3E4648 !important;
    padding: 8px 4px 8px 12px !important;
  
}

/* блок комментария */

.msg {
    border-radius: 3px !important;
    border: 1px solid #3E4648;
    margin-bottom: 8px !important;
    padding: 8px 8px 8px 8px !important;
}

/* заголовок ОП */

.msg h1 {
    margin-bottom: 20px !important;
}

/* заголовок блока комментария */

.msg .title {
    margin: -14px 0 8px 0px !important;
    display: inline !important;
    width: auto !important; 
    font-size: small !important;
}

.msg .title a {
    text-decoration: none !important;
    padding: 0 2px !important;
}

/* аватар */

.msg .userpic {
    float: right !important;
    text-align: center;
    padding: 0 0 0 8px !important;
    width: 100px !important;
    height: auto !important;  
}

.userpic img {
    float: right !important;
    width: 100px !important; 
    height: auto !important;
    border-radius: 3px !important; 
}

/* тело */

.msg_body, .message-w-userpic {
    display: block !important;
    padding: 10px 4px 10px 16px !important;
}

/* подпись и ссылка для ответа */

.sign {
    display: inline-block !important;
    width: auto !important;
    float: left;
    padding: 10px 12px 0 0 !important;
    margin: 0 !important;
    font-size: small !important;
}

.sign a {
    text-decoration: none !important;
}

.msg .reply {
    display: inline-block !important;
    float: none !important;
    width: auto !important;
    padding: 10px 0 0 0 !important;
    margin-bottom: -10px !important;
}

div.nav {
       padding: 10px 0 0 0 !important;
       font-size: small !important;
}

.msg .reply a, div.nav a {
    text-decoration: none !important;
    padding: 0 2px !important;
}

/* «звёзды» */

.sign img[alt="*"], .boxlet_content img[alt="*"] {
    border-radius: 4px !important;
    width: 0 !important;
    height: 0 !important;
    padding-top: 5px !important;
    padding-left: 5px !important;
    margin-right: 2px !important;
    border: #4e9a06 1px solid !important;
}

.sign img[alt="#"], .boxlet_content img[alt="#"] {
    border-radius: 4px !important;
    width: 0 !important;
    height: 0 !important;
    padding-top: 5px !important;
    padding-left: 5px !important;
    margin-right: 2px !important;
    border: #3E4648 1px solid !important;
}

/* код */

.code {
    margin-right: 120px !important;
    border-radius: 3px !important;
}


/* LORCode Tools */

#atag a {
    border: 1px solid #3E4648 !important;
    background-color: #272c2d !important;
    margin-left: 0 !important;
    padding: 2px !important;
    color: #babdb6 !important;
    font-size: 10pt !important;
}

#atag a:hover {
    border: 1px solid #3E4648 !important;
    background-color: #3E4648 !important;
}

/* название раздела чуть поменьше */

#navPath {
    font-size: medium !important;
}

/* ссылки и фильтры без псевдокнопок */

.nav li a {
    border: none !important;
    color: #729fcf !important;
    padding: 1px !important;
    text-decoration: none !important;
    background: none !important;
}

.nav li::before {
    content: "[";
}

.nav li::after {
    content: "]";
}

/* номера страниц без псевдокнопок */

.page-number {
    border: none !important;
    background: none !important;
    padding: 2px !important;
    text-decoration: none !important;
    font-size: small !important;
}

/* кнопки, списки */

select, input {
    font-size: x-small !important;
    color: #272c2d !important;
}

/* убираю какашку после юзернейма */

a[title="Выйти"]{
    text-decoration : none !important;
}

a[title="Выйти"]:before {
    content : "[x]" !important;
}

img[src="/img/logout.png"] {
    display: none !important;
}

/* теги в списке сообщений справа */

.message-table .tag {
    float: right;
    margin: 0 1px;
}

}
