css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asphalto.org") {

/* generali */
body {
  font-family: lucida grande, lucida sans, sans-serif !important;
/*  background-image: url(http://fc05.deviantart.net/fs70/f/2010/305/3/5/mac_background_by_xxkingpwnerxx-d31zfhr.jpg);
  background-repeat:no-repeat;
  background-attachment:fixed;
  background-position:center;
*/
}

body, textarea, #head table, .mainpost, 
#reply_post_info, .main_post_text,
.main_post_text_skip_sig, .summary,
.collapsed_list, .collapsed_list_info , .post_text,
.post_form td, .post_form tr {background-color: white !important; color: black !important;}

.unregistered_user, blockquote {color: #666 !important;}
input, select {
border-width: 0px 0px 1px 0px !important;
background-color: transparent !important;
font-weight:bold;
color: #666 !important;
}
textarea, input:focus {box-shadow: 0px 0px 10px #b3dced;}
a:not([style]) {color: #4290d0 !important;}
#right_column {display: none;}
#content_div {margin: 0px 0px 0px 145px !important;}
.section_links {background-color: white !important; color: #666 !important;}
.section_links_selected, .section_links_selected span {background-color: white !important; color: #666 !important;}
.mainpost {background-color: white !important; color: #666 !important;}
.post_form_element img {display:none;}

.collapsed_ignored, .collapsed_ignored_info {background: #eee !important; color: #333 !important; font-size:6pt !important; padding: 2px !important;}

/* pseudo titlebar */
  .main_title,
  .section_title_collapsed,
  .section_title {
  background: -moz-linear-gradient(top, #e6e6e6 5%, #bdbdbd 95%); border:0 !important;}
  /* main post */
    .mainpost .post_info {background: -moz-linear-gradient(top, #bdbdbd 5%, #aeaeae 100%) !important; border-bottom: 1px solid black; color:black !important;}
  /* replies */
    .reply {margin-bottom: 14px;}

/* tabelle arrotondate */
  table, .category_description {
    border-collapse:collapse !important;
    border-radius: 4px 4px 4px 4px;
  }    
  table tr:last-child td:first-child  {border-bottom-left-radius:4px;}
  table tr:last-child td:last-child   {border-bottom-right-radius:4px;}
  table tr:first-child td:first-child {border-top-left-radius:4px;}
  table tr:first-child td:last-child  {border-top-right-radius:4px;}
  /* replies menu */
    .post_text table {border-radius: 0 !important;}
    .post_text table td {border-radius: 0 !important;}
  /* left column */
    .link_list tr:only-of-type td a {position:relative; left:-115px;}
    .section_links .link_list td a {left:-95px !important;}
    .link_list tr:only-of-type th {padding-left: 10px !important;}

/* glowing */
  .mainpost, .reply, .post_form, .category_description, #actual_content>table {box-shadow: 0px 2px 10px #666;}
  .post_text table {box-shadow: none !important;} /* replies menu */

/* buttons */
  .post_info a:first-child, .post_info a[href*=post_versions], 
  .post_text .reply_post_info a[title~=leggiti] {
        border-radius: 0px !important;
        border: 0px !important;
        background: transparent !important;
  }
  .post_text .reply_post_info a[title~=leggiti] {color: #999 !important;}
  .post_info a, .post_text .reply_post_info a {
    padding: 1px 5px 1px 5px;
    border: 1px solid #777;
    border-radius: 4px 4px 4px 4px;
    background: -moz-linear-gradient(top, #EAEAEA 25%, #A0A0A0 100%);  
  }
  .post_info a img, .post_text .reply_post_info a img {position:relative; top:1px;}
  .post_info a:hover, .post_text .reply_post_info a:hover {
    background: -moz-linear-gradient(top,  #b3dced 5%, #29b8e5 50%, #bce0ee 95%);
  }


/* thread */
  .main_post_text {
    border: 0 !important;
    background:white !important;
    padding:10px !important;}
  .main_post_text .signature {display: none;}
  
  /* replies */
    .reply {
      border:0 !important;
    }
    #reply_post_info {
      border-right:1px solid #b4b4b4 !important;
      min-width:100px;
      max-width:100px;
      overflow:hidden;
      background: -moz-linear-gradient(top, #e5e9ee 1%, #d2d9e0 99%) !important;
      padding: 10px !important;
    }
    .reply_posted_on {color: black !important;}
    .post_text {border:0 !important;
      padding: 0px !important;
      margin: 0px !important;
      background: white !important;
    }
    .post_text table {
      border-bottom: 1px solid #d1d1d1;
      margin: 4px 0px;
    }
    .post_text table td {
      background: transparent !important;
    }
    .reply_title {
      padding-left: 10px !important;
      color: #758192 !important;
    }
    .post_text .reply_post_info {padding-right: 10px !important;}
    .post_text div[id] {padding: 0px 10px; width: 97% !important;}
    .signature {height:50px; overflow:hidden;}

    #reply_post_info.ignored_post, .ignored_post {
        background: #aaa !important;
        color: #666 !important;
        border: 0 !important;
        padding: 1px 10px !important;
    }

}
