css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnbeta.com"), domain("www.cnbeta.com") {}

@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {

  /* 隐藏不需要的元素 */
  #wrapper > *, #main > *, #news > *, #feedback > *, #news_content ins, .digbox, .gbox830 { display: none !important; }
  #main, #content, #news, #news_title, #news_content, #good { display: block !important; }

  /* 去边框 */
  #contentWrapper, #news { border: none !important; }

  /* 黑底 */
  body { background: #000 !important; opacity: 0.9; }

  /* 白背景色 */
  #wrapper, #main { width: 645px !important; background: #fff !important;  }

  /* 边距 */
  #wrapper { padding: 10px 20px !important; margin: 20px auto !important; }

  /* 消除浮动 */
  #newsBox, #contentWrapper { float: none !important; width: 100% !important; }

  /* 标题字体大小 */
  #news_title { margin-bottom: 20px !important; font-size: 24px !important; }

  /* 增大内容面积 */
  #news_content { width: 100% !important; font-size: 18px !important; line-height: 1.5em !important;
 }

  /* 热门评论 */
  #feedback { position: fixed; top: 3px; left: 0; width: 21% !important; height: 92% !important;overflow: auto!important; }
  #good { background-color: RGB(67,67,67) !important; border: none !important; font-size: 15px !important; line-height: 1.5em !important; 
color:RGB(245,245,245)!important;}

/*热门评论作者+时间*/
#g_content dl dt.re_author span, #n_content dl dt.re_author span{
border-bottom:0px solid RGB(0,0,0)!important;
color:RGB(170,170,170)!important;
font-size: 12px !important
}
/*热门评论标题*/
#g_title{
background: RGB(0,0,0)!important;
color:RGB(255,255,255)!important;
}

  #g_content dl { padding: 8px !important; border-color: #888 !important; }
  .re_detail { padding: 0 !important; line-height: 120% !important; }
/*普通评论*/
#n_content{
color:RGB(245,245,245)!important;
position: fixed; top: 27px; right: 0; width: 20% !important; height: 92% !important; overflow: auto;
padding: 8px !important; border-color: #888 !important;
background-color: RGB(67,67,67)!important; border: none !important; font-size: 14px !important; line-height: 1.5em !important;
}
#normal .subTitle{
position: fixed; top: 3px; right: 0; width: 20% !important; height:21px !important; 
background:  RGB(0,0,0)!important;
border: none !important;
color:RGB(255,255,255)!important;
}
#n_content dl{
border-bottom:1px dashed  RGB(157,157,157)!important;
margin:3px!important;
border-right:0px solid RGB(120,120,120)!important;
}
.feedBack .fb_title{
background: RGB(60,60,60)!important;
color:RGB(210,210,210)!important;
border:1px solid RGB(92,92,92)!important;
}
.feedBack{
border:1px solid RGB(92,92,92)!important;
}
#n_content dl dt.re_author span,#n_content dl dt.re_author strong{
font-size: 13px !important;
}
re_author
.feedBack{
border: none !important;
background: RGB(92,92,92)!important;
}
#g_content dl{
border-style:none none dashed!important;
}

#newsBox #mark{
 display: none !important;
}
#post .subTitle{
background: none!important;
border: none !important;
}
#newsBox #post{
font-size: 14px !important; 
border: none !important;

}
#post .subTitle,.replydiv label, #post label{
color:RGB(0,0,0)!important;
font-size: 15px !important; 
}
input#subject0.input,input#name0.input,input#page0.input,input#email0.input,textarea#comment0,input#valimg0.input{
background: #aaa !important;
border: none !important;
}
.replydiv .button, #post .button{
background: RGB(45,45,45) !important;
padding:0px 5px 5px 5px!important;
}
a:link, a:visited,a:hover{
color:RGB(45,45,45)!important;
}
*{
font:16px/19px "Segoe WPC","Segoe UI",Helvetica,Arial,"Arial Unicode MS",Sans-Serif!important;
}
#g_content dl dd.re_mark { 
color:RGB(25,25,25)!important;
background: RGB(67,67,67) !important;
padding:0px 0 0 0!important;
margin:2px 0 0 0px!important;
height:20px!important;

 }

#g_content .re_mark span.re_mark_right, #n_content .re_mark span.re_mark_right{
margin:1px -2px 3px 10px!important;
}
#n_content dl dd.re_mark{
background: RGB(67,67,67) !important;
padding:3px 0 0px 0!important;
font-size:16px!important;
}
#g_content .re_mark span.mark, #n_content .re_mark span.mark
{
color:RGB(25,25,25)!important;
}

}
