css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("salon.com") {

/* Giant SALON */
.logo {  display:none !important;
  }

/* Useless TopBar */
.topBar {
  background-color: #acc3f5 !important;
  }
.shareButtonBox {
  background-color:transparent !important;
  }
.shareCount {
  background-color:transparent !important;
  }

/*Top Navigation Bar*/
.mainNav {
  margin-top:10px !important;
  }

.flyOutMenu, .container {
  background-color: #c7ddff !important;
  }

.sectionMenu ul {
  background-color:transparent !important;
  color: black !important;
  }

.primaryNavigation a {
  color:lightblue !important;
  background-color:darkgreen !important;
   }
/* see very bottom for menu image*/
.headContainer li {
  color:green !important;
  background: none !important;
  }


/*All 3 Columns*/

.headline {
  background-color: transparent !important;
  }
p {
  color: black !important;
 }

/*Middle (Articles)*/
.sectionSeperator {
  border-top-width: 5px !important;
  border-top-color: #568a63 !important;
  }

/*Article Stuff*/
h1{
  color:#045a4f !important;
  }

.grid_10,.clearfix{background: #DBE8FF !important;}

a{color:darkblue !important;}

/* Date at top of each article head */
.toLocalTime,.dateline {
  color: #877a4e !important;
  }

/*bottom byline, topics, comment numbers */
.commentBubble a {
  background-color:blue !important;
  color: white !important;
  }



/* Outer Border*/
body {background:#96BEFF !important;}

/*Big White Article Space*/
.mainInner{background:#c7ddff !important;}

/*Box around article area*/
.grid_10{
  border-top: 3px solid #dfedd4 !important;
  border-left: 3px solid #dfedd4 !important;
  border-right: 3px solid #dfedd4 !important;
  }

/*Section Titles (Current Stories, etc.)*/
h2 {
  color:black !important;
  background: none !important;
  }

/* embedded images */

/* drop-down menu at top */
.headContainer {
  color:green !important;
  background: url("data:image/png;base64,") no-repeat scroll !important;
  }

}
