@-moz-document domain("lokalisten.de")

{

/*--------------------
Hintergrund
--------------------*/

body
{
     background: #636363!important;
     color:#444;
     font:100.1% "Verdana","Helvetica",Arial,sans-serif;
}

/*--------------------
WerbungsplÃ¤tze werden versteckt
--------------------*/

#BANNER_popup
{
     display: none;
}

#BANNER_sky
{
     display: none;
}

.tileBox
{
     display: none;
}

/*--------------------
SpaceNet Logo wird versteckt
--------------------*/

.sponsor
{
     text-align: center;
     margin: 8px 0 0 0;
     display: none;
}

/*--------------------
News by sueddeutsche Box wird versteckt
--------------------*/

.fixedBox rssBox
{

}

.fixedBoxHeader
{

}

div.notifyRss
{
     color: #333!important;
     font-size: 11px;
     font-weight: bold;
     padding: 3px 6px 6px 6px;
     margin: 3px 3px 2px 3px;
     display: none;
}

div.notifyRss p.desc
{
     font-weight: normal;
     padding: 6px 0 0 0;
     color: #444!important;
     line-height: 120%;
     display: none;
}

div.notifyRss p.desc b
{
     color: #333!important;
     display: none;
}

div.notifyRss a.more, p.newsLink a.more
{
     text-decoration: underline;
     color: #039;
     display: none;
}

div.notifyRss a.thumbPic
{
     padding: 6px 0 0 0;
     background: none;
     display: none;
}

div.taskBox div.fixedBoxHeader
{
     background: #690; 
     padding: 2px 6px 4px 6px;
     display: none;
}

div.taskBox div.fixedBoxHeader h3
{
     font-size: 11px;
     color: #fff;
     font-weight: normal;
     display: none;
}

div.fixedBox
{
     background: #fff;
     font-size:11px;
     border-top: #ababab 1px solid;
     border-bottom: #ababab 1px solid;
     margin:0 0 8px 0;
     display: none;
}

/*--------------------
Schatten um das Design
--------------------*/

div#l_outer
{
     width: 845px;
     margin: 0 auto;
     background: #fff;
     -moz-border-radius: 10px;
     padding-top: 10px;
     padding-bottom: 10px;
     padding-right: 10px;
     padding-left: 10px;
}


}
