@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://www.last.fm/user"), 
url-prefix("http://www.last.fm/settings"),
url-prefix("https://www.last.fm/settings"),
url-prefix("http://www.last.fm/music"),
url-prefix("http://www.last.fm/group"),
url-prefix("http://www.last.fm/tag"),
url-prefix("http://www.last.fm/forum"),
url-prefix("http://www.last.fm/wiki"),
url-prefix("http://www.last.fm/subscribe"),
url-prefix("http://www.last.fm/events"),
url-prefix("http://www.last.fm/api"),
url-prefix("http://www.last.fm/charts"),
url-prefix("http://www.last.fm/community"),
url-prefix("http://www.last.fm/search"),
url-prefix("http://www.last.fm/home/friendslistened"),
url-prefix("http://www.last.fm/home/friendsloved"),
url-prefix("http://www.last.fm/inbox"),
url-prefix("http://www.last.fm/festivals"),
url-prefix("http://www.last.fm/help"),
url-prefix("http://www.last.fm/download"),
url-prefix("http://www.last.fm/home/newreleases"),
url("http://www.last.fm/home"),
url("http://www.last.fm/home/friends"),
url("http://www.last.fm/home/freemp3s") {

/* Settings 
color: #21B0F3 !important; = blue text
background: #111 !important; = black background/border
background: #222 !important; = dark grey background
background: #333 !important; = grey background
background: rgb(170, 170, 170) !important; = grey shoutboxes
display: none !important; = for all elements to not show
max-width: 100% !important; = for full width

*/

/* Blocked items */
    .LastAd, 
    .home-module,
    aside
        {display: none !important;}

/* Black BG */
div.memo
    {background: #111 !important;}
    

/* Common elements */
    body,
    div#footer_content
        {background: #222 !important;}

    .fiflufi .leftCol { background: none !important;}
    
    #content,
    .content
    {
      border: 1px solid #111 !important;
      border-radius: 6px 6px 6px 6px !important;
      background: #222 !important;
      color: #BBB !important;
    }

    /* Headers colors */
        h1,
        h2,
        .h2,
            .h2Wrapper,
        h3,
        .h3,
           .lfmBlack #page h3, 
        h5,
        .h5,
        h2.heading,
        .rightCol .h2Wrapper,
        strong,
        .breadcrumb span,
        #userBadge .badgeHead h1,
        #page .scrobblesource,
        table.tracklist th, 
        table.tracklist td,
        ul.groupsSmall a strong,
        ul.journalsSmall a strong,
        ul.eventsSmall li small.location,
        table.wikiHistory td.summary 
            {
            color: #666 !important; /* Light grey text color since these are mostly static text */
            }   
    
        .badgeHead h1, 
        .summary, 
         
        .lfmBlack #page .h2Wrapper a,
        
        .lfmBlack #page h3 a,
        .catalogueHead h1,
        a, 
        .lfmMailButton span, 
        .lfmShoutboxButton span,
        #page a.lfmTagButton strong
            {
            color: #21b0f3 !important; /* Blue text color since these are mostly links */
            }

    /* Margins */
        .rightCol .h2Wrapper {
            padding-top: 5px !important;
            padding-left: 5px !important;
            }

        div.sic #content .fullWidth {
            padding-bottom: 45px !important;
            }

    /* Header borders */
        #page h2.heading {border-top: 1px  solid #111 !important;}
    
    /* Hover states */
        table.chart tbody tr:hover td,
        table.chart tbody tr.open td,
        tr:hover td,
        tr.open td
            {
            background: #444 !important;
            } /* Mouse over an artist/track-row will be less bright (#d0e4f0) */
        
        /* Found on chart-pages */
        .rankItem:hover {background: #333 !important;}
            
    
    /* Notifications and elements */
        a.ontour {border: 0px !important; background: #111 !important;}
    
        div.errorMessage span.messageWrapper, 
        div.dialogErrorMessage,
        span.quote,
        .clearit,
        .clearitItems li,
        #content, 
        #page {
            background: #222 !important; /* Dark grey background */
            }
    
        #page .video .messageBox, 
        #page .wiki .messageBox, 
        #page .similarMusic .messageBox,
        #subscribe,
        #subscribe #paymentFormContainer
            {
            border: 1px solid #333 !important;
            }
        
        #subscribe .leftColumn .wrapper {
            border-right: 1px solid #333 !important;
            }
            
        div.artistMessage span.messageWrapper, 
        div.artistMessageBlock,
        div.warnMessage span.messageWrapper, 
        div.warnMessageBlock,
        table.eventsMedium span.cancelled
            {background: #111 !important; /* Dark grey background */}
            
    /* Dialogue boxes */
        .dialogBox .dialogButtons,
        .dialogBox .dialogContent,
        .dialogBox h3,
        div.typeaheadHint
            {
            background: #333 !important;
            }
            
        .dialogBox h3 {border-bottom: 1px solid #333 !important;}
        
        .typeahead a.typeaheadSelected 
            {
            background: #222 !important;
            }
            
        div.resource {background: #111 !important;}
        
        .dialogBox fieldset.resource
            {
            background: #111 !important;
            border-color: #333 !important;
            }
        
        div.typeaheadHint,
        .typeahead
            {
            border-color: #444 !important;
            }
        
        .dialogBox .dialogButtons 
            {
            border-top: #444 !important;
            }
        
        p.typeaheadSuggestions span,
        div.typeaheadHint {
            color: #666 !important;
            }
        
        form.singleAction {
            background: #222 !important;
            border-color: #111 !important;
            }
    
    /* Pagination */
        div.pagination .selected {color: #666 !important;}
        
        div.pagination .selected,
        div.pagination a
            {border: 1px solid #111 !important; background: none !important;}
        
        div.pagination a.nextlink, 
        div.pagination a.nextlink:hover,
        div.pagination a.prevlink, 
        div.pagination a.prevlink:hover
            {background: none !important;}
    
    /* Shouts, Posts */
        .shouts .message p {
            color: #AAA !important;
            }
        
        .shouts h3
            {border-bottom:1px solid #111 !important;}

        /* Shouts and posts by ignored users */    
            .shouts .message .ignored, 
            ul.forumComments div.comment .ignored {display: none !important;}

        /* text entry field */
        input[type="text"],
        input[type="password"], 
        textarea {
            background: rgb(170, 170, 170) !important;
            }
            
        #editor .bbbuttons li,
        #editor .bbbuttons li span {background: #222 !important;}
        
        span.quote {border: 1px solid #333 !important;}
        
        #editor #editorPreviewPane {border: 1px solid #111 !important;}
    
    /* Journals */
        .journals .hentry {border-top: 1px solid #111 !important;}
    
    /* Track listings */
        table.tracklist tr.odd {background: #222 !important;}
        table.chart tr.odd td  {background: #333 !important;}
    
    /* Playlists */
        .playlists td
            {border-top: 1px solid #111 !important;}
    
    /* Recent Listening Trend */
        .statsModule {
            background: #333 !important;
            border: 1px solid #444 !important;
            }
        #scrobblesAndListeners .container span
            {
            color: #666 !important;
            }
        .statsModule .trackscrobblegraph,
        .statsModule .personalStats
            {
            border-top: 1px solid #333 !important;
            background: #333 !important;
            }
        #scrobblesAndListeners .listeners 
            {
            border-left: 1px solid #333 !important;
            }



    /* Artists pictures (no more white overlay) */
            .pictureFrame .overlay {background:none!important;}
    	   
        /* Artists albums (no more jewelcase) */
                span.coverMedium span.jewelcase,
                span.coverLarge span.jewelcase,
                span.coverMega span.jewelcase
                    {background-image:none!important;}

    /* Videos */
        span.videoStillYouTubeMedium span.play {background-image: none!important;}
        
        span.videoStill {border: 0px !important; padding: 0px !important;}

    /* Tags */
    
        /* Note: set this to none and the dismissRec-button on the Recommendations-pages disappear as well. */
        /* img.icon {background-image:none !important;} */
    
    /* Tag filters */
        .filterTags li a,
        .filterTags li a.selected,
        .filterTags li a span {background: #222 !important;}
    
    /* Horizontal options */
        .horizontalOptions {
            border-bottom: 1px solid #111 !important;
            }
        
        .horizontalOptions ul li.current {border-color:#111 !important; }
    
    /* Charts calendar */
        #page table.calendar,
        #page table.calendar thead tr th,
        #page table.calendar tbody tr td.marker,
        table.calendar tr td,
        table.calendar tr td a{
            background: #333 !important;
            }

/* Section elements */

    /* Header */
    #headerWrapper {background-color: #222 !important;}
    #headerPromo {display: none !important;}
    #headerLinks {color: #333 !important;}
    
    /* Search */
    #siteSearchAutocomplete td,
    #siteSearchAutocomplete, 
    #siteSearchAutocomplete li.selected a, 
    #siteSearchAutocomplete li.selected a strong, 
    #siteSearchAutocomplete li.selected a small,
    #siteSearchAutocomplete.sidegrouped p.header,
    #siteSearchAutocomplete.sidegrouped div.message,
    a.albumItem {
        background: #222 !important;
        }
    
    /* Secondary navigation */
    #secondaryNavigation {border-bottom: 0px !important;}
    #secondaryNavigation li {border-top: 0px !important;}
    #secondaryNavigation li a {
        background: #222 !important;
        border: 0px !important;
        }
    #secondaryNavigation ul {
        border-top: 0px !important;
        border-bottom: 0px !important;
        border-style: none !important;
        }
    #secondaryNavigation li a:hover, 
    #secondaryNavigation li.current a {
        color: #21B0F3 !important;
        }       
    
    /* Tertiary Navigation */
    div.tertiaryNavigation {
        border-bottom: 0px !important;
        }
    


    /* Content right colom */
    
        /* Events */
        ul.eventsSmall li.first,
        ul.eventsSmall li.last,
        ul.eventsSmall li {
            border-top: 1px solid #222 !important;
            border-bottom: 1px solid #222 !important;
            }
        
        /* News */
        .latestNewsEntry,
        .newsEntriesSmall li {
            border-bottom: 1px solid #222 !important;
            }

        /* Related Journals */
        ul.journalsSmall li,
        ul.journalsSmall li.last,
        ul.journalsSmall {
            border-top: 1px solid #222 !important;
            border-bottom: 1px solid #222 !important;
            }
        
        /* Groups */
        ul.groupsSmall li,
        ul.groupsSmall li.last {
            border-top: 1px solid #222 !important;
            border-bottom: 1px solid #222 !important;
            }

        /* Recent activity */
            ul.minifeedSmall li {
                border-bottom: 1px solid #222 !important;
                color: #666 !important;
                }


/* Profile */

    /* Main Content */
        /* Profile-pic gets a black border */
            .photo {border: 1px solid #111 !important;}
        
        /* Shouts */
            #userBadge .badgeInfo p.userActivity .highlight {background: #333 !important;}
            
            /* Posts */
                table.wikiHistory tr td
                    {border-top: 1px solid #111 !important;}
        
        /* Profile settings */
            .embedDialog .infoMessage, .modulePreferences .infoMessage {background: #333 !important;}
            
            div.infoBox/*, 
            p.infoBox, 
            ul.infoBox */
                {
                    background: #111 !important;
                    border-top: 0px !important;
                    border-bottom: 0px !important;
                }
    
        /* Recently listened tracks */
        table.tracklist td.highlight {
            border-top: 1px solid #111 !important;
            background: #222 !important;
            }
        

            
            table.tracklist th,
            table.tracklist td {
                border-bottom: 1px solid #111 !important;
                }
    
        /* Other peoples profile */
        .profileOptions {
            border-top: 1px solid #111 !important;
            }
    
    /* Top Albums */
    
    /* Taste */
    #tasteCocktail .wrapper {
        border: 1px solid #111 !important;
        background: #333 !important;
        padding: 5px 5px 6px 5px !important;
    }
    
        /* Library */
        #libraryNavigation .current,
        #libraryNavigation li,
        #libraryNavigation a,
        #pages .page {
            background: #222 !important;
            }
        
        #libraryNavigation {
            border-bottom: 0px !important;
            }
    
        #libraryAlbums h2 {
            border-bottom: 1px solid #333 !important;
            }
        table.tracklist td.border {
            border-left: 1px solid #333 !important;
            }
    
    /* Charts */
    
    /* Events */
    
    /* Friends */
    
    /* Neighbours */
    #neighboursList li .sharedArtists {background: #222 !important;}
    
    /* Groups */
    
    /* Journal */
        .journals .hentry
            {border-top: 1px solid #111 !important;}
    
    /* Tags */

    /* Settings */
        
    
    /* Inbox */
    div.successMessage span.messageWrapper {background: #333 !important;}
    
    table#pms tr.unread td {
        background: #333 !important; /* Unread messages get the not so dark grey background */
        }
    
    table#pms td {
        border-top: 1px solid #333 !important;
        }
    

/* Home */

    /* Overview */
        .home-group,
        #eventRecs .eventsMedium .recommended td {
            background: #222 !important;
            }
    
        /* See more */
            #home .module-footer {border-bottom: 1px solid #222 !important;}
            #home .module-header {border-top: 1px solid #222 !important;}       

        /* Free downloads | Do not edit this table since it's the same as your recent tracks-list */

        /* Your Friends */
            #home #nowPlaying li.first,
            #home #nowPlaying li,
            #home #nowPlaying li.last 
                {
                border-top: 1px solid #222 !important;
                border-bottom: 1px solid #222 !important;
                }

        /* Friends loved tracks */
            #home #friendsLoved li.first,
            #friendsLoved li,
            #home #friendsLoved li.last 
                {
                border-top: 1px solid #222 !important;
                border-bottom: 1px solid #222 !important;
                }

        /* Changes here will show in the result pages */
        
            /* Events */
            table.eventsMedium tr td {border-top: 1px solid #111 !important;}
            

    /* Results pages */
    
        /* New to your library */
        
        /* New album releases */
        #recs h1.heading,
        #newReleases .library {background: #222 !important;}
        #newReleases td.summary {border-top: 1px solid #111 !important;}
        
        .lfmBlack #newReleases th.weekheader {color: #666 !important;}
        
        /* Music recommendations */
        #recs #artistRecs div.similarArtists {background: #222 !important;}
        #recs #artistRecs li.recArtist {border-bottom: 1px solid #333 !important;}
        
        /* Event recommendations */
                table.eventsMedium tr.recommended td {background: #222 !important;}

        /* Friends listening now */

            /* Friends name */
            #friendsListeningNow li 
                {border-bottom: 1px solid #111 !important;}
                
                    /* Real name text */
                    #friendsListeningNow .realname {color: #666 !important;}

            /* Scrobble source */
            #friendsListeningNow .tracklist td.highlight, 
            #friendsListeningNow .tracklist tr.first td
                {border-top: 1px solid #111 !important;}
            
            /* Trackslist */
            #friendsListeningNow .tracklist th, 
            #friendsListeningNow .tracklist td 
                {
                border-bottom-color: #222 !important;
                }
        
            

        /* Loved by Friends */
            #friendsLoved li.first,
            #friendsLoved li
                {border-top: 1px solid #111 !important; border-bottom: 0px solid #111 !important;}

/* Artists */

    /* Main Content */
    
        /* Black background for artist pictures on artist pages */
         .moduleOptions {background: #222 !important;}
         #artistImages .images {border: 3px solid #333 !important;}
         #artistImages #firstImage {border: 0px !important;}
        
        /* Any notifications get a normal bg-color */
        div.infoMessage span.messageWrapper, 
        div.infoMessageBlock,
        #moderationBox {
            background: #222 !important; 
        }
            div.messageBox {border: 0px !important;}
        
        /* Add to library + Tag artist */

        
        /* Featured tracks */
        #featuredTracks {border: 0px !important;}
        
        #featuredTracks,
        #featuredTracks table tr.odd td, 
        #featuredTracks table td,
        #page a.lfmFreeDownloadButton 
            {background: #222 !important;}
        
        #featuredTracks .hasFreeDownloads .durationCell {border-right: 1px solid #333 !important;}
        #featuredTracks .durationCell {border-left: 1px solid #333 !important;}
        
        /* Shoutbox */
        .shouts h3 {border-bottom: 1px solid #333 !important;}
        

        

    

    /* Other artist pages */
    
         /* Who listens most */
        div.tertiaryNavigation ul li, 
        div.tertiaryNavigation ul li.current a, 
        div.tertiaryNavigation ul li a {
            background: #222 !important; 
            }
        
        /* Similar Artists */
        ul.artistsWithInfo div.matchmeter,
        .simArtistSearch {
            background: #222 !important;
            }

        /* Groups */
        ul.groupsMedium strong {
            border-bottom: 1px solid #111 !important;
            }

      
/* Music */

    /* Free downloads */
        .tagHead,
        #page div.rightCol div.recommendedContent h2.heading span.h2Wrapper,
        .freeTracks li.odd, 
        .topTracks li.odd,
        ul#freeTracks li.odd,
        #music-landing ul.artistList li.odd,
        #music-landing p.listfilter {
            background: #222 !important;
            }
        
        #page div.rightCol div.recommendedContent h2.heading {border-top: 0px !important;}

    /* By tag */
    #music-landing p.listfilter,
    #music-landing ul.artistList li,
    .tagHead,
    ul.tagList li
        {border-bottom: 1px solid #333 !important;}
        

/* Tag pages */
    #tagHead {
        background-color: #222 !important;
        border-style:none !important;
        }
    a.tag span,
    a.tag {
        background: #222 !important;
        }
        
    /* Free music downloads */
        .freeTracks li.first,
        .topTracks li.first,
        .freeTracks li,
        .topTracks li,
        ul#freeTracks li 
            {
            border-top: 1px solid #222 !important;
            border-bottom: 1px solid #222 !important;
            }
      

        

/* Radio */
    #subscribe,
    #row-subscription #subscribe h1 {background: #222 !important;}

/* Events */
    .eventToggle li span {
      background: #222 !important;
      color: #21B0F3 !important;
      text-shadow: none !important;
    }
    h2 img {
      display: none !important;
    }
    h2.presents {
      height: 45px !important;
    }
    h2.presents span {
      margin-left: 0px !important;
    }
   
    #eventTags {background: #222 !important;}
    html:not([dummy]) #festivalResults td.summary .title-venue {
      background: #222 !important;
    }
    #festivalResults tbody td {
      border-right: 0px !important;
    }
    #festivalResults thead td, #festivalResults thead a.sortby {
      background: #222 !important;
    }
    .eventometer {
      background: #222 !important;
      color: #21B0F3 !important;
    }

    /* Festivals */
    .fullWidth,
    #festivalResults tbody tr
        {border-bottom: 1px solid #111 !important;}
    
    #festivalResults thead td 
        {
        border: 1px solid #111 !important;
        }

/* Groups */

    /* Overview */
    div.fiflufi #content .fullWidth {border-top: 1px solid #333 !important;}
    
    /* Members */
    ul.usersMedium div.userContainer strong {border-bottom: 1px solid #111 !important;}
        ul.usersMedium div.userContainer p strong {border-bottom: 1px solid #222 !important;}


/* Charts */
.collapsibleBox-heading h3 {background: #222 !important;}

.js .collapsibleBox.s-closed .collapsibleBox-heading h3 
    {border-bottom: 1px solid #333 !important;}
    
.collapsibleBox-heading h3,
.collapsibleBox-body-inner2,
.collapsibleBox-heading-openclose a {
    border: 1px solid #333 !important;
    }

.collapsibleBox-heading-openclose a {
    background-color: #333 !important;
    }

.collapsibleBox-body-inner2 {
    background: none repeat scroll 0% 0% #333 !important;
    }

.rankItem-title {color:#21b0f3 !important;}

/* Community */
#community-info {background: #222 !important;}

/* Forum */
    #editor #editorPreviewPane,
    div.fiflufi #content .fullWidth,
    #forumsearch,
    table.forumtable tr.alt td,
    ul.forumComments li.odd {
        background: #222 !important;
        }
    
    /* Dark lines for tables */
    table.forumtable tr.alt td,
    table.forumtable td {
        border-top: 1px solid #333 !important;
        border-right: 1px solid #333 !important;
        border-bottom: 1px solid #333 !important;
        }
    
    /* Dark Lines for comments */
    ul.forumComments li.odd {
        border-top: 1px solid #333 !important;
        border-bottom: 1px solid #333 !important;
        }
    
    /* Grey for the new comment textarea */
    .forum textarea {background: rgb(170, 170, 170) !important;}

/* Search */
    /* Overview */
        div.results {
            border: 1px solid #222 !important;
            background: #222 !important;
            }

        .list-artist-small-vertical li.first,
        .list-artist-small-vertical li
            {
            border-top: 1px solid #222 !important;
            border-bottom: 1px solid #222 !important;
            }

            /* Changing this will also be shown on the results page */
                div#tagResults ul li,
                div#tagResults ul li.first
                
                    {
                    border-top: 1px solid #111 !important;
                    border-bottom: 1px solid #222 !important;
                    }
        
                div#labelResults ul li,
                div#labelResults ul li.first
                    {
                    border-top: 1px solid #111 !important;
                    border-bottom: 1px solid #222 !important;
                    }
        

    /* Results pages */        
        #artistResults ul.artistsWithInfo li.first,
        #artistResults ul.artistsWithInfo li {
            border-top: 1px solid #111 !important;
            border-bottom: 1px solid #222 !important;
            }
        
        div#trackResults table {width: 100% !important;}
        
        div#trackResults table td {
            background: #222 !important;
            }
        
        div#trackResults table td.track {
            border-right: 1px solid #333 !important;
            }
        div#trackResults table td.duration {
            border-left: 1px solid #333 !important;
            border-right: 1px solid #333 !important;
            }

/* Wiki pages */
    /* History */
        ul.wikiHistory li
            {border-bottom: 1px solid #222 !important;}

    /* Compare */
        

/* Help */
    #categories li,
    dl.faqCollapsedList dt,
    div#faqContent dl dd.faqExpanded 
        {background: #222 !important;}
    
    div.search {border: 1px solid #111 !important;}

    div#faqContent dl dd.faqExpanded,
    dl.faqCollapsedList dt
        {border-bottom: 1px solid #111 !important;}

    #categories li {border-bottom: none !important;}

/* Download */
    div#lfmDownload div#downloadBox form {background: #111 !important;}

    div.dismissMessage {
        border: 1px solid #111 !important;
        border-radius: 5px 5px 5px 5px !important;
        }

