css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("last.fm"), 
domain("lastfm.es"),
domain("lastfm.de"),
domain("lastfm.fr"),
domain("lastfm.jp"),
domain("lastfm.it"),
domain("lastfm.pl"),
domain("lastfm.com.br"),
domain("lastfm.se"),
domain("lastfm.tr"),
domain("lastfm.ru"),
domain("cn.last.fm"){

/* Common elements */
#page{max-width: 100% !important;} /* older container */

div.fixed,
body #page
    {width: 100% !important;} /* new container */

/* Header */
#header
    {width: 99% !important;}

/* Profile */
#taste ul.libraryItems {height: 155px !important;} /* 8 artists pictures' div needs less height */

#tasteCocktail {max-width: 100% !important;} /* Shortlist LT, Playlists and tags */

#newReleases td.summary {width: 1100px !important;} /* Shortlist New album releases */

/* Messages */
#messageContainer,
#composeform textarea {
    width: 1100px !important;
    }


/* tag.TopAlbums */
.content
    {max-width: 100% !important;}

/* Overview journals connected to artists */
.journals {max-width: 100% !important;}

/* Forum pages */
ul.forumComments div.comment .wrapper,
#editor, 
#editorPanel {
    width: 90% !important;
    }
    
.forum textarea {
    width: 100% !important;
    height: 200px !important;
    }

/* Events */
.eventsMedium tr {
    width: 50% !important;
    }

/* Free music Downloads */
div.tagBrowser {width: 900px !important;}
div.tagBrowser div.wrapper {width: 85% !important;}

/* Tag pages */
#page div.artistsLargeTwoRowsWrapper {height: 155px !important;}

/* Community */

/* The header is not compatible with full width, therefore disabled */
#content #community-header {
    display: none !important;
}
}
