css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hypem.com") {

/* Main Page */
body {
  background: #111 !important;
}
#container {
  width: 97% !important;
}
#content-wrapper {
  background: #222 !important;
}
.section {
  background: #222 !important;
  border-top: 1px solid #333 !important;
}
#message h1, #radio-blogs h1, #recently-posted h1 {
  color: rgb(127, 127, 127) !important;
}

#submenu-filter span {color: #666 !important;}

.paginator {
    border-top: 1px solid #333 !important;
    }
.paginator a,
.paginator span {
    border: 1px solid #333 !important;
    }

/* Tracks */
#content-left {
  width: 75% !important;
  margin-right: -330px !important;
}
#content-left .section p {
  width: 90% !important;
  color: #666 !important;
}
#content-left .section .buy{display:none!important;}

h3 a, h4 a {color: #666 !important;}

/* a.blog-fav-on */
.section p a.blog-fav-on, #content-left .section h3 .search_match {
  background: #222 !important;
}
/* h3.track_name */
h3 a.artist, .section h3 a.artist, .section.same h4 a.artist, #content-right #videos .section ul li a span, #content-right #current-hype h3, #content-right #current-hype .section h3 a:hover span, #content-left #list h2 span a:hover {
  color: rgb(127, 127, 127) !important;
}

/* Right column */
#content-right .section {
  border-color: #222 !important;
}
/* Blog tags */
#browse-genres li a {          /* eventueel #language li a, #directory -tags li a */
  background: #222 !important;
  border-bottom: 0px !important;
  border-right: 0px !important;
}
/* h2-headers */
#content-right h2, .badge-stickers h2 {
  color: rgb(127, 127, 127) !important;
  background: #222 !important;
}
#twitter-board li, #active-users li {display:none!important;}
#content-right #news ul li a, #content-right #radio-archive ul li a {
  display:none!important;
}
#content-right #tour-dates, #content-right #your-site, #content-right #news {
  display:none!important;
}

    /* Header */
     #header{background:#222!important;}

    /* Player show always */
     #player-timebar{display:block!important;}
     #player-progress-playing{background:#21B0F3!important;}
     
    /* Footer */
     #footer{display:none!important;}

}
