css:
@-moz-document domain("facebook.com") {
body.home, .timelineLayout, body.permalinkBody{
/*The URL below is the background*/
background-image: url(/*[[bg]]*/) !important;
background-size: 100% auto !important;
background-position: center top !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
}
.fbChatSidebar, .fbNubFlyoutBody{
/*The URL below is the background for contact list*/
background-color: transparent !important;
background-size: 100% auto !important;
background-position: center top !important;
background-repeat:no-repeat !important;
}
#pagelet_ticker, .fbChatSidebarBody, .fbNubFlyoutBodyContent>.conversationContainer,
.fbNubFlyoutBodyContent>.fbChatOrderedList{
/*Transparence of the contact list background*/
background-color: rgba(255,255,255,0.35) !important;
}
#mainContainer, #contentCol,
#contentArea, .uiListVerticalItemBorder, .fbStreamPermalinkHeader{
border: none !important;
}
#leftCol, .newsFeedComposer,
.rightColumnWrapper, #rightCol,
#headerArea, .attachmentSelectForm,
.noborder, .ci_module_list,
.fbTimelineTopSection, .fbStreamPermalinkHeader,
.fbTimelineStickyHeader>.stickyHeaderWrap>.back{
background-color: rgba(255,255,255,0.65) !important;
}
.fbTimelineTimePeriod, #contentCol,
#contentCurve, #pageFooter,
#contentArea, .fbTimelineUFI{
background-color: transparent !important;
}
.fbNubFlyoutTitlebar, .fbNubButton {
background-color: rgba(109,132,180,0.65) !important;
}
.mainWrapper, .timelineUnitContainer,
.questionsListItem>.UIImageBlock>.UIImageBlock_Content,
.noteDashboard>.notes_dashboard>.noteEntry,
#pagelet_pokes, .friendListFilterNullState,
.contentListWidth>.uiListItem>.content,
.timelineReportContainer, .noteDashboard>.uiPagerTopBorder>.summary{
background-color: rgba(255,255,255,0.65) !important;
}
.mainWrapper:hover, .timelineUnitContainer:hover,
.questionsListItem>.UIImageBlock>.UIImageBlock_Content:hover,
.noteDashboard>.notes_dashboard>.noteEntry:hover,
#pagelet_pokes:hover, .friendListFilterNullState:hover,
.contentListWidth>.uiListItem>.content:hover,
#footerContainer>.navigation:hover{
background-color: rgba(255,255,255,0.85) !important;
}
.uiUfiAddComment, .uiUfiComment, .uiUfiLike, .ci_module{
background-color: rgba(237,239,244,0.65) !important;
}
.threadRow {
background-color: rgba(235,238,244,0.65) !important;
}
.threadRow:hover {
background-color: rgba(235,238,244,0.85) !important;
}
}
