/*-------------------------------------------------------------------------------------------
-------------------------------- GMAIL LIGHT STYLE V1.3.1 -----------------------------------
-------------------------------------------------- by PrimeSU ---------------------------- */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/* ----------------- INDEX BOX ----------------------------------------------------------- */
div.mq,                                     /* hide top ads */
div.aeO, div.akc,			                /* hide chat & invite */
div.n6,										/* hide More */
div.aeG, 									/* hide footer */
col.y5, td.apU,					            /* hide stars in mail list */
td.aqM,                                     /* hide trash icon in line mail */
img.afM, 									/* hide arrow with type mailbox */
div[id=":s4"], div[id=":s5"], 				/* hide option selected or not by stars */
.SK div:nth-child(7), 				        /* hide more option mark by star */
/* ----------------- VIEW, WRITE MAIL BOX ------------------------------------------------ */
td.y3,						                /* hide right ads */
div.PS,						                /* hide bottom ads */
div.AT,                                     /* hide popup ads */
div.ov,						                /* hide bottom information about mail */
div.tq, 									/* hide information on mouse over mail address */
div.zd,									    /* hide star in view mail */
div[id=":ta"], div[id=":tb"], 				/* hide more option mark / unmark by star */
div.tx78Ic                                  /* hide translator bar */
{display: none !important;}

/* ----------------- OTHER --------------------------------------------------------------- */
tr.zA:hover {background-color: /*[[hovercolor]]*/ !important;} /* mails list hover */

/*[[loading_page_dark]]*/
}
