@-moz-document domain("evernote.com") {
/* 第１階層 */
.ennote ol>li
 { list-style-type: decimal; margin-top: 0.7em; } 
/* 第２階層 */
.ennote ol ol>li
 { list-style-type: lower-latin; margin-top: 0.5em; } 
/* 第３階層 */
.ennote ol ol ol>li 
 { list-style-type: lower-roman; margin-top: 0.3em; } 
/* 第４階層 */
.ennote ol ol ol ol>li 
 { list-style-type: katakana; margin-top: 0.1em; } 
/* 第５階層 */
.ennote ol ol ol ol ol>li 
 { list-style-type: disc; margin-top: 0.05em; } 
}
