css:
@-moz-document url-prefix(http://chat.deviantart.com/)

{

.damncrc-icon-roomname {	display: none !important; }

/* ---------- link and the hover colors ---------- */
.damncrc-chat a{ color: #628263 !important; }
.damncrc-chat a:hover{ color: #75a778 !important; }

/* ---------- Colors the links in the title and topic ---------- */
.damncr-title a{ color: #628063 !important; }
.damncr-title a:hover{ color: #3a4a3b !important; }
.damncrc-topic a{ color: #628063 !important; }
.damncrc-topic a:hover{ color: #3a4a3b !important; }

/* ---------- The color you see when people tab you ---------- */
.damncrc-chat .other-hl{ background-color: #b9fae5!important; }

/* ---------- The color you see when you are talking ---------- */
.damncrc-chat .self-hl{ background-color: #abbba6 !important; }

/* ---------- Not exactly sure what this does, but I got it somewhere, so I left it in :B ----------- */
.damncrc-chat .self-hl span{ background: transparent !important; }
.damncrc-chat .other-hl span{ background: transparent !important; }

/* ---------- This keeps the horizontal bar from showing if someone stretches, unless you hover over the chat ----------- */
.damncrc-chat-window { max-width:100%;overflow-x:hidden !important; white-space:normal!important;}
.damncrc-chat-window:hover { overflow:auto !important; } 

/* ---------- These are the privclass names and it's border and shading ----------- */
.sidebar dl.simple dt {
line-height:1em!important;
color:#000000 !important;
border: solid #000000;
border-width: 1px;
background-color: #858f85;
padding: 2px 2px 2px 2px!important;
background:url(data:image/png;base64,%3D)repeat;
top:-10px!important;
}

/* ---------- This is the style of the members in each privclass, I think ---------- */
dl.simple dd, dl.flat dd {
line-height:.7em!important;
padding-top:3px!important;
padding-left:0px!important;
font-size:.8em!important;
}

/* body.fullscreen #head {
padding-top:0px!important;
padding-bottom:0px!important;
} 


#bar-deviant {
line-height:.8em!important;
height:15px;
letter-spacing: 0px!important;
vertical-align:middle!important;
} */

/* ---------- This makes the text input rounded, and pads the cursor a bit on the left ---------- */
.damncrc-input input, .damncrc-input textarea {
-moz-border-radius-topright: 1em !important;
-moz-border-radius-topleft: 1em !important;
-moz-border-radius-bottomright: 1em !important;
-moz-border-radius-bottomleft: 1em !important;
padding: 0px 0px 0px 4px!important;
z-index:0!important;
}

/* ---------- The space on the left and right side of the members ---------- */
.damncr-members {
padding-left:2px!important;
padding-right:2px!important;
}

/* ---------- Probably just random stuff to try and tighten everything up, not quite sure ---------- */
body.fullscreen #body {
padding-top:0px!important;
padding-bottom:0px!important;
padding-right:0px!important;
padding-left:0px!important;
} 

.damn .alt2 {
padding-top:0px!important;
padding-bottom:0px!important;
padding-right:0px!important;
padding-left:0px!important;
} 

.output-primary ie-floatfix {
height:0px!important;
padding-top:0px!important;
padding-bottom:0px!important;
padding-right:0px!important;
padding-left:0px!important;
}

element.style { display:none!important; }

#body { margin-bottom:-50px!important; }

/* ---------- This keeps the size of the tabs smaller ---------- */
.tabbar a, .tabbar strong {
padding-left:6px!important; 
padding-right:2px!important;
}

/* ---------- This just makes the dAx button area a bit bigger, so I can get better spacing below the buttons ---------- */
.damncrc-iconbar { height:22px!important; }


#output {
padding-top:0px !important;
padding-left:25px !important;
padding-right:25px !important;
}


/* ---------- These were taken from electricnet's script, to get rid of the sides and things ---------- 
.bubbleview {margin-bottom:0 !important}

#top-middle,i.ttr,i.tr,i.tl,i.br,i.bl,#top span.edge {display:none !important}

.damnc-header h1 {font-size:13pt !important}

.damnc-header { top:2px !important;left:8px !important}*/

/* #top h1 {
display:none!important;
} 

.damnc-tabbar {
padding-left:6px!important;
} */

/* ---------- This tightens something up, But I forget ---------- */
body.chatroom #top { margin-bottom:-71px!important; }

/* ---------- More sizing on the chat tabs ----------- */
.damnc-tabbar strong, .damnc-tabbar a { margin-right:6px!important; }
.damnc-tabbar strong i, .damnc-tabbar a i { right:-3px!important; }

/* ---------- This hides the annoying X button to leave the chat.  Use /part people ---------- */
.damnc-tabbar strong img { display:none!important; }

/* ---------- This makes the top privclass line up with the topic.  It does sort of mess up and hide part of the scroll bar though ---------- 
.damn-lo-col-right { top:-11px!important; }
*/
/* ---------- This keeps the original border color from showing under the priv name, since moving it up makes a small border ---------- */
.alt0-border { border-color:#d0d9cf!important; }


.damnc-tabbar {
padding-left:6px!important;
}

.damnc-header h1 {
display:none!important;
margin:-20px;
}

/* ---------- This hides the rockdock ---------- */
#top-deviant { display:none !important; }
#top { display:none !important; }

.damncri-member {
white-space:normal!important;
}
}
