css:
@-moz-document domain("thepiratebay.se"), domain("thepiratebay.sx") {
body {font:normal 12px arial!important;}
#content {max-width:100%!important;}
#main-content {margin:0!important; padding:0!important;}
h2 {margin:10px 0!important; border:none!important; background: #F90!important; color:#FFF!important; font-size: 12px!important;}
.detDesc {color: black!important;}
input {border: 1px solid #CCC!important;}
input:focus, select:focus {outline:none!important; border: 1px solid #F00!important;}

table#searchResult th, table#searchResult td {border: 2px solid #FFF!important; display: table-cell!important;}
table#searchResult tr {background: wheat!important;}
table#searchResult tr:hover {background: #92E0E7!important;}
#browseContainer dt {font-size: 14px!important;}
#browseContainer dd {font-size: 12px!important;}
thead#tableHead th {background: #4F94CD!important; color: white!important;}
td[align=right] {text-align:center!important;}

td[colspan="9"] {background:#FFF!important;}

#fbanners, #icons, iframe, #addiv, .adsTitle, #ttl, #social {display:none!important;}

.img {background:url('http://goo.gl/jNALw'); width: 90px!important;
height: 90px!important; float: left!important;}
.img img {opacity:0!important;}

#foot {width:720px!important; display:block!important; margin: 0 auto!important;}
#foot br, #foot b {display:none!important;}
}
