css:
@-moz-document regexp("http://(www\\.?)?cos.name/cn/(.*)") {




/* 字体重设 */
body { font: 85% "Helvetica Neue", "Helvetica", "DejaVu Sans", "Tahoma", "Hiragino Sans GB", "STHeiti", "WenQuanYi Micro Hei", "Microsoft YaHei", sans-serif !important; }

/* 装饰超链接 */
a, a:link, a:visited, a:active { color: #778087 !important; text-decoration: none !important; }
a:hover { color: #4d5256 !important; text-decoration: underline !important; }



/* 去掉网站标题描述文字“统计学网站” */
#header p.description { display:none !important; }

/* 调整头部宽度 */
#header { width: 960px !important; }

/* 调整主体宽度 */
#main { width: 960px !important; }



/* 调整首页帖子列表宽度 */
#front-page #discussions { width: 960px !important; }



/* 调整单个帖子的交错背景色,缩小上下内边距*/
.threadpost { padding: 15px 1em 1px; !important; }


/* 调整回帖框宽度 */
.postform textarea { width: 925px !important; max-width: 925px !important; }

/* 调整回帖框标签框宽度 */
.postform #topic,.postform #tags-input { width: 925px !important; }

/* 去掉各处帖子列表上方表头 
#latest th, #forumlist th, #favorites th { display:none!important; } */
a.new-topic {
    color: white;
}
/*使首页新帖表格标题中间对齐*/
#latest th{text-align:center}
#latest th[width]{text-align:left; width:600px}

/* 去掉顶部无用线 */
#front-page #main h2, #forum-page #main h2, h2.post-form, #userlogin, #currentfavorites, #register, #passwordrecovery { border-bottom: 0px !important; }




/* 去掉过多的回帖提示 */
p#post-form-allowed-container { display: none !important; }


/* 调整贴子作者名称、几楼字号 */
 .threadauthor strong { font-size: 10px; } 

/* 去掉帖子左侧 用户级别 注册时间 发帖数 提升信噪比 */
p.post_count_plus{ display: none !important; }



/* R 代码字体*/
pre, code, .r_code, .r_data { font-family: "Menlo", "Monaco", "DejaVu Sans Mono", "Courier New", monospace !important; }


/* 隐藏帖子标题下方回复信息*/
.topicmeta { display: none; }
span#topic_posts { display: none; }
span#topic_voices { display: none; }



/* 使标题空间更紧凑 */
.infobox { border: 0px !important; padding: 2px 1px;}

/* 把每个帖子下方回复时间信息移到右边，同时变小*/
.poststuff {text-align:right;padding: 3px 0 0;margin: 0px 0 0;border:0px !important}
/*字体调小*/
.bbcrumb {font-size:10px;}

/* 加大搜索框*/
#header div.search { float: right !important; height: 33px !important; width:330px !important;}
#header div.search input.text {font-size: 30px !important; height: 33px !important; width: 280px !important;background-color:white;}
#header div.search input.submit {height: 33px !important;width: 40px !important;background-image:url(http://i.imgur.com/VQY3i.png) !important;background-color:grey}


/* 翻页，缩小，放右边*/
.nav{float: right !important; font-size:10px !important}



/* 标签字体变小，添加框放到右边*/
#tag-form { float: right; }
div#topic-tags 
{
font-size:4px;
width:300px;
border:0px;
text-align:right;
}
/*标签不分段*/
div#topic-tags li
{
display: inline-block;
}
/*不显示“标签”*/
div#topic-tags > p
{
display:none; 
}


}
