css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.nicovideo.jp/my/"),url("http://www.nicovideo.jp/my") {

  /*=================================================== 設定*/

  /* 表示・非表示（非表示はblockをnoneに、表示は逆） */

  /*ニコレポ：マイリスト等のコメント*/
  #nicorepo .log-comment {
    display:block !important;
  }
  /*ニコレポ：ニコる*/
  #nicorepo .log-user-nicoru-video  {
    display:block !important;
  }

  /* ニコレポ：カテゴリ別ライン色付け */

  .log-user-nicoru-video {
    /* ニコる */
    border-right:#FFA500 2px solid;
  }
  .log-community-video-upload {
    /* アップロード：コミュニティ */
    border-right:#C71585 2px solid;
  }
  .log-user-video-upload {
    /* アップロード：ユーザー */
    border-right:#CD5C5C 2px solid;
  }
  .log-user-video-review {
    /* 動画レビュー：ユーザー */
    border-right:#BC8F8F 2px solid;
  }
  .log-user-mylist-add {
    /* マイリス追加：ユーザー */
    border-right:#FFB6C1 2px solid;
  }
  .log-community-live-broadcast {
    /* 生放送開始：コミュニティ */
    border-right:#1E90FF 2px solid;
  }
  .log-user-live-broadcast {
    /* 生放送開始：ユーザー */
    border-right:#87CEEB 2px solid;
  }
  .log-user-video-ranking {
    /* ランクイン：ユーザー */
    border-right:#FF7F50 2px solid;
  }
  .log-user-app-install {
    /* アプリインストール：ユーザー */
    border-right:#F0E68C 2px solid;
  } 
  .log-community-live-reserve {
    /* 生放送予約：コミュニティ */
    border-right:#00FA9A 2px solid;
  }
  .log-user-live-reserve {
    /* 生放送予約：ユーザー */
    border-right:#00CED1 2px solid;
  }
  .log-user-video-round-number-of-view-counter {
    /* キリ番 */
    border-right:#F5DEB3 2px solid;
  }
  .log-community-action-info {
    /* お知らせ追加：コミュニティ */
    border-right:#40E0D0 2px solid;
  }
  .log-user-video-advertised {
    /* 宣伝：ユーザー */
    border-right:#B22222 2px solid;
  }

  /*=======================================================*/

  /*ヘッダー*/
  #header h1 {
    width:100% !important;
    margin:10px 0 0 -8px !important;
  }
  .miniSearchWrap {
    margin:10px 0 10px -4px !important;
  }
  /*マイページ　ユーザー詳細*/
  .userDetail {
    width:714px !important;
    height:70px !important;
    position:absolute !important;
    top:50px; left:50%;
    margin:0 0 0 -246px !important;
    background:#f3f3f3 !important;
    border:#ccc 2px solid !important;
    overflow:hidden;
  }
  .userDetail a {
    color:#555 !important;
  }
  .userDetail .avatar {
    width:70px !important;
    height:70px !important;
    padding-right:10px !important;
  }
  .userDetail .avatar img {
    width:70px !important;
    height:70px !important;
  }
  .userDetail .profile div {
    width:630px !important;
    margin:0 0 0 0 !important;
  }
  .userDetail .profile h2 small {
    display:none !important;
  }
  .userDetail .stats {
    background:none !important;
    margin:2px 0 0 -3px !important;
    padding:0 !important;
    font-size:10px !important;
  }
  .userDetail .stats li:hover {
    background:none !important;
  }
  .userDetail .accountEdit {
    margin:40px 6px 0 0 !important;
    width:160px !important;
    background: -moz-linear-gradient(top, #f3f3f3, #ccc) !important; 
    background: -webkit-gradient(linear, left top, left bottom, from(#f3f3f3), to(#ccc)) !important;
    text-align:left !important;
    padding:3px 5px 3px 10px !important;
  }
  .userDetail .accountEdit span {
    display:none !important;
  }
  .userDetail .accountEdit:hover {
    color:#888 !important;
  }
  .userDetail .pullout {
    width:178px !important;
    position:absolute !important;
    top:3px; right:2px;
  }
  .userDetail .pullout dt {
    border:#ccc 1px solid !important;
    background: -moz-linear-gradient(top, #f3f3f3, #ccc) !important; 
    background: -webkit-gradient(linear, left top, left bottom, from(#f3f3f3), to(#ccc)) !important;
    text-align:left !important;
    padding:3px 5px 3px 10px !important;
  }
  .userDetail .pullout dt a {
    background:none !important;
    padding:0 !important;
  }
  .userDetail .pullout dt a:hover {
    color:#888 !important;
  }
  .userDetail .pullout dt a:after {
    content:"->";
    padding-left:10px;
  }
  .userDetail .pullout dt a span {
    display:none !important;
  }
  .userDetail .pullout dd {
    width:400px !important;
    font-size:12px !important;
    margin-top:5px !important;
    padding:0 10px 5px 10px !important;
  }
  .userDetail .pullout dd:after {
    content: ""; 
    display: block; 
    clear: both;
  }
  .userDetail .pullout dd a {
    float:left;
    margin:0 10px 0 0 !important;
    padding:0 !important;
  }
  /*マイページ　ニコレポ*/
  #nicorepo,#mylist,#video,#seiga,#live,#stamp,#nico,#history,#favUser,#favChannel,#favCommunity,#favFriend {
    background:#f3f3f3 !important;
    border:#ccc 2px solid !important;
  }
  #nicorepo a,#mylist a,#video a,#seiga a,#live a,#stamp a,#nico a,#history a,#favUser a,#favChannel a,#favCommunity a,#favFriend a {
    color:#555 !important;
  }
  #nicorepo a:hover,#mylist a:hover,#video a:hover,#seiga a:hover,#live a:hover,#stamp a:hover,#nico a:hover,#history a:hover,#favUser a:hover,#favChannel a:hover,#favCommunity a:hover,#favFriend a:hover {
    color:#aaa !important;
    text-decoration:none !important;
  }
  #nicorepo h3,#mylist h3,#video h3,#seiga h3,#live h3,#stamp h3,#nico h3,#history h3,#favUser h3,#favChannel h3,#favCommunity h3,#favFriend h3 {
    background: -moz-linear-gradient(top, #f3f3f3, #ccc) !important; 
    background: -webkit-gradient(linear, left top, left bottom, from(#f3f3f3), to(#ccc)) !important;
    font-weight:normal !important;
    font-size:15px !important;
    color:#888 !important;
    text-shadow:0 0 3px #fff; 
  }
  #nicorepo .articleBody,#mylist .articleBody,#video .articleBody,#seiga .articleBody,#live .articleBody,#stamp .articleBody,#nico .articleBody,#history .articleBody,#favUser .articleBody,#favChannel .articleBody,#favCommunity .articleBody,#favFriend .articleBody
 {
    -webkit-border-radius:10px;
    -moz-border-radius:10px;
  }
  #nicorepo .log-comment {
    background:#f3f3f3 !important;
    padding:5px 5px 5px 25px !important;
  }
  .log {
    padding:10px 10px 0 0 !important;
    border-top:#BABABA 1px dotted !important;
  }
  .log.first {
    padding:0 10px 0 0 !important;
    border-top:none !important;
  }
  .log-community-live-broadcast .log-target-thumbnail,.log-user-live-broadcast .log-target-thumbnail {
    display:none !important;
  }
  .log-community-live-broadcast .log-target-info,.log-user-live-broadcast .log-target-info {
    margin:0 0 0 auto !important;
  }
  .log-community-live-broadcast .log-target-info:before,.log-user-live-broadcast .log-target-info:before {
    content:"番組名【";
  }
  .log-community-live-broadcast .log-target-info:after,.log-user-live-broadcast .log-target-info:after {
    content:"】";
  }
  .log-content .log-target-thumbnail,.log-content .log-target-thumbnail a,.log-content .log-target-thumbnail img {
    width:100px !important;
    height:77px !important;
  }
  .log-content .log-target-thumbnail {
    float:right !important;
  }
  .log-content .log-target-info {
    float:left !important;
    width:480px !important;
    margin:0 !important;
    padding:0 10px 0 0 !important;
  }
  .log-content .log-subdetails {
    float:left !important;
    width:450px !important;
    margin:10px 0 0 0 !important;
    padding:5px 10px 5px 10px !important;
    font-size:12px !important;
  }
  .log-content .log-subdetails .log-nicoru-msg {
    padding:3px 0 0 0!important;
  }
  .log-content .log-target-info a {
    text-decoration:none !important;
  }
  .log-content {
    width:600px !important;
    margin:0 0 0 auto !important;
  }
  .log-author,.log-author a,.log-author img {
    width:50px !important;
    height:50px !important;
  }
  .log-content .log-body,.log-content .log-target {
    margin:0 0 10px 0 !important;
    padding:0 !important;
  }
  .log-content .log-footer {
    margin:0 !important;
    padding:0 !important;
  }

  /*マイページ　サイドバー*/
  .sidebar a{
    color:#666 !important;
    font-size:13px !important;
    text-decoration:none !important;
    padding-bottom:5px !important;
  }
  .sidebar a:hover{
    color:#aaa !important;
  }
  .sidebar li.active {
    width:98% !important;
    margin:5px auto 5px 3px !important;
    text-indent:-5px !important;
    background:#fff !important;
    -webkit-border-radius:5px;
    -moz-border-radius:5px;
  }
  .sidebar li.active a small {
    background-position:0 -564px !important;
    margin-top:5px !important;
  }
  .sidebar span.live,span.timeshift {
    font-weight:normal !important;
  }
  .sidebar li.active a {
    width:98% !important;
    background:none !important;
  }
  .sidebar li.active a span {
    margin:5px 0 0 10px !important;
  }
/*マイページ　マイリスト*/
  #myNavMylist ul.child li a:hover span {
    text-decoration:none !important;
  }
  #myNavMylist ul.child li.active a small {
    display:none !important;
  }
  #myNavMylist ul.child li.active a span {
    display:block !important;
    margin:0 auto !important;
    text-align:center !important;
    text-indent:0 !important;
  }
  #myNavMylist ul.child li.active a {
    display:block !important;
    margin:0 auto !important;
    text-align:center !important;
    text-indent:0 !important;
  }
  .mylistVideo .metadata {
    background:#f3f3f3 !important;
    -webkit-border-radius:10px;
    -moz-border-radius:10px;
    font-size:12px !important;
    color:#666 !important;
  }
  #mylist .mylistComment {
    background:#f3f3f3 !important;
    -webkit-border-radius:10px;
    -moz-border-radius:10px;
    font-size:12px !important;
    color:#666 !important;
  }
  .mylistVideo .pullout {
    position:absolute !important;
    top:3px; right:2px;
  }
  .mylistVideo .pullout dt {
    border:#ccc 1px solid !important;
    background: -moz-linear-gradient(top, #f3f3f3, #ccc) !important; 
    background: -webkit-gradient(linear, left top, left bottom, from(#f3f3f3), to(#ccc)) !important;
    text-align:left !important;
    padding:3px 12px 3px 10px !important;
  }
  .mylistVideo .pullout dt a {
    background:none !important;
    padding:0 !important;
  }
  .mylistVideo .pullout dt a:hover {
    color:#888 !important;
  }
  .mylistVideo .pullout dt a span {
    display:none !important;
  }
  .mylistVideo .pullout dd {
    font-size:12px !important;
    margin-top:5px !important;
    padding:5px 10px 5px 10px !important;
  }
  .mylistVideo .pullout dd:after {
    content: ""; 
    display: block; 
    clear: both;
  }
  .mylistVideo .pullout dd a {
    margin:0 10px 0 0 !important;
    padding:0 !important;
  }
  #mylist .aside {
    background:none !important;
    font-size:12px !important;
    color:#666 !important;
  }
  #mylist .buttonOuter {
    border:#f3f3f3 2px solid !important;
    margin:11px 0 0 0 !important;
    line-height:10px !important;
  }
  #mylist .button {
    height:10px !important;
    line-height:10px !important;
  }
/*マイページ　投稿動画*/
  #video .dataOuter {
    background:#f3f3f3 !important;
    -webkit-border-radius:10px;
    -moz-border-radius:10px;
    color:#666 !important;
  }
  #video .metadata {
    font-size:12px !important;
    background:none !important;
  }
  #video .pullout {
    position:absolute !important;
    top:3px; right:2px;
  }
  #video .pullout dt {
    border:#ccc 1px solid !important;
    background: -moz-linear-gradient(top, #f3f3f3, #ccc) !important; 
    background: -webkit-gradient(linear, left top, left bottom, from(#f3f3f3), to(#ccc)) !important;
    text-align:left !important;
    padding:3px 12px 3px 10px !important;
  }
  #video .pullout dt a {
    background:none !important;
    padding:0 !important;
  }
  #video .pullout dt a:hover {
    color:#888 !important;
  }
  #video .pullout dt a span {
    display:none !important;
  }
  #video .pullout dd {
    font-size:12px !important;
    margin-top:5px !important;
    padding:5px 10px 5px 10px !important;
  }
  #video .pullout dd:after {
    content: ""; 
    display: block; 
    clear: both;
  }
  #video .pullout dd a {
    margin:0 10px 0 0 !important;
    padding:0 !important;
  }
  #video .aside {
    background:none !important;
    font-size:12px !important;
    color:#666 !important;
  }
  #video .buttonOuter {
    border:#f3f3f3 2px solid !important;
    margin:11px 0 0 0 !important;
    line-height:10px !important;
  }
  #video .button {
    height:10px !important;
    line-height:10px !important;
  }
/*マイページ　スタンプ*/
  #stamp .aside {
    background:none !important;
    font-size:12px !important;
    color:#666 !important;
  }
/*マイページ　履歴*/
  #history .metadata {
    background:#f3f3f3 !important;
    -webkit-border-radius:10px;
    -moz-border-radius:10px;
    font-size:12px !important;
    color:#666 !important;
  }
  #history .addMylist {
    border:#ccc 1px solid !important;
    background: -moz-linear-gradient(top, #f3f3f3, #ccc) !important; 
    background: -webkit-gradient(linear, left top, left bottom, from(#f3f3f3), to(#ccc)) !important;
    text-align:left !important;
    padding:3px 12px 3px 10px !important;
  }
  #history .buttonOuter {
    margin:8px -5px 0 0 !important;
    padding:0 !important;
    line-height:5px !important;
  }
  #history .button {
    height:5px !important;
    padding:10px !important;
    line-height:5px !important;
  }
}

/*　以下、原宿引き継ぎ　*/

@-moz-document
url-prefix("http://www.nicovideo.jp/user/"){
  .contents a {
    color:#666 !important;
  }
  .contents a:hover {
    color:#888 !important;
  }
  #myContHead
  {
    background-color:#f3f3f3 !important;
    background-image:url(http://uni.res.nimg.jp/img/bg/bg_common_c_head.gif) !important;
    border-bottom:none !important;
  }
  #myCont
  {
    border:#cccccc 2px solid !important;
    border-top:#cccccc 1px solid !important;
  }
  #myCont li,.spBox,.liveProgramList li
  {
    border-bottom:#cccccc 1px dotted !important;
  }
  .report
  {
    width:82% !important;
  }
  .repThumb,.repThumb a,.repThumb img
  {
    width:50px !important; height:38px !important;
  }
  .commentBtn a
  {
    background-color:#f3f3f3 !important;
    background-image:none !important;
    border:#cccccc 1px solid !important;
    font-size:10px !important;
  }
  .commentBtn a:hover
  {
    background-color:#fcfcfc !important;
  }
  .report h4 a.delBtn
  {
    background-color:#cc0000 !important;
    font-size:10px !important; color:#ffffff !important;
    text-decoration:none !important;
  }
  .report h4 a.delBtn:hover
  {
    text-decoration:none !important;
    background-color:#e00000 !important;
    border:none !important;
  }
  dt.active,li.active
  {
    background-color:#cccccc !important;
  }
  #myNavWrap a
  {
    width:100% !important;
    text-decoration:none !important;
  }
  #myNavWrap a:hover
  {
    color:#666666 !important;
  }
  .userSubNav
  {
    border-top:#cccccc 1px dotted !important;
    border-bottom:#cccccc 1px dotted !important;
  }
  .highlight,span.highlight a
  {
    color:#cc0000 !important;
  }
}
@-moz-document url-prefix("http://www.nicovideo.jp/"){
/*広告非表示（仮）*/
  #contentsWrap span.renewal,#contentsWrap span.new,
  #ads_undefined_Left,#ads_undefined_Right,
  .ads_468,.sidewallAd,.sideTxtAd,#web_pc_top,#web_pc_prime,#web_pc_premium,
  #web_pc_360,#web_pc_footer,#mainRanking em,#mainRanking span,
  #sideRanking em,#sideRanking span
  {
    display: none !important;
  }
}
