css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {




/* MAIN */

body {
background: #0f1213 url('/*[[mybackground]]*/') bottom left fixed no-repeat !important;
}

#tinymce {
background: none !important;
}

/*[[mylogo]]*/

#auto_pagination_loader, #content #sidebar_footer_nav.pinned_sidebar_footer .sidebar_nav {
visibility: hidden;
}

#dashboard_index #content a {
border-bottom: none !important;
}

#return_to_top .return_to_top_icon {
background: url('http://data.sainet.co.uk/medigel/scroll_to_top/scroll-to-top_standard-2.png') no-repeat !important;
}

#logged_out #logo .default {
visibility: hidden !important;
}




/* POST LABELS */

.new_post_label .new_post_label_icon {
background-image: url('/*[[myicons]]*/') !important;
background-size: 513px 190px !important;
}

#content #new_post .new_post_label {
color: rgba(0,0,0,0.8) !important;
font-weight: bold !important;
}




/* CONTENT */

#content {
background: #838383 !important;
-webkit-border-radius: 16px !important;
border-radius: 16px !important;
padding-top: 20px !important;
padding-bottom: 20px !important;
}




/* TAB NOTICE */

#new_post_notice_container.tab_notice {
box-shadow: none !important;
color: #ffffff !important;
background-color: #6695b0 !important;
letter-spacing: 1px !important;
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
border: solid 1px #b0b0b0 !important;
line-height: 12px !important;
padding: 3px !important;
}

#dashboard span.tab_notice_value {
color: #000000 !important;
}

#container .selection_nipple, #container .tab_notice_nipple {
visibility: hidden;
}




/* RADAR */

#content #right_column .radar {
background-color: #213e4f !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
padding-bottom: 5px !important;
}

#content #right_column .radar_avatar .radar_tumblelog_avatar {
box-shadow: none !important;
margin-left: 5px !important;
}

#content #right_column .radar .radar_footer {
margin-top: 4px !important;
margin-bottom: 1px !important;
}

#content #right_column .radar .radar_controls {
border-bottom: rgba(0,0,0,0.15) 1px solid !important;
}

#content #right_column .thumbnail_anchor {
box-shadow: 0pt 1px 0pt rgba(255, 255, 255, 0.25) inset, 0pt 1px 3px rgba(0, 0, 0, 0.25);
}

#content #right_column .radar_content .post {
background: transparent !important;
}

#content .radar_post {
border: none !important;
}

#content .radar_tumblelog_label {
width: 160px !important;
}




/* RIGHT COLUMN */

#content #right_column li {
background-color: #6695b0 !important;
}

#content #right_column li:hover {
box-shadow: rgba(255,255,255,0.05)  5px 0px 15px inset !important;
}

#content #right_column::after {
background: none !important;
}

#content #right_column .small_links a{
color: rgba(0,0,0,0.7) !important;
}

#content #right_column a, #content .publish_on_day, #content .publish_on_time, #content #right_column small,#content #right_column .link_arrow, #content #right_column #tumblr_radar .radar_footer span, #content #right_column .count, #content #right_column .mark_date, #content a.username, #content #page_heading {
color: #ffffff !important;
}

#content .post_avatar.flat.lighter_blue, #right_column a.send_fan_mail, #content #MissingEdraftQueueTools.controls_section li, #content #MissingEmassDeleter.controls_section li {
background-color: #3c657d !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
}

#content #right_column .likes {
color: #ffffff !important;
background-color: #213e4f !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
}

#content #right_column .spotlight {
color: #ffffff !important;
background-color: #6695b0 !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
}

#xfollowers_button.followers, #content #right_column .following {
color: #ffffff !important;
background-color: #3c657d !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
}

#content #right_column .likes {
border-radius: 6px 6px 0px 0px !important;
-webkit-border-radius: 6px 6px 0px 0px !important;
}

#content #right_column .spotlight {
border-radius: 0px 0px 6px 6px !important;
-webkit-border-radius: 0px 0px 6px 6px !important;
}

#content #right_column .likes:hover, #content #MissingEdraftQueueTools.controls_section li:hover, #content #MissingEmassDeleter.controls_section li:hover, #right_column a.send_fan_mail:hover, #xfollowers_button.followers:hover, #content #right_column .following:hover, #content #right_column .spotlight:hover {
box-shadow: rgba(255,255,255,0.1) 5px 0px 15px inset !important;
}

#content #right_column .open_blog {
color: #ffffff !important;
background-image: none !important;
background-color: #213e4f !important;
border-radius: 6px 6px 0px 0px !important;
-webkit-border-radius: 6px 6px 0px 0px !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.05) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
}

#content #right_column .open_blog:hover {
box-shadow: rgba(255,255,255,0.09) 5px 0px 25px inset !important;
}

#content #right_column .open_blog a {
padding-left: 0px !important;
overflow: hidden !important;
}

#content #right_column .open_blog .hide_overflow {
margin-left: 23px !important;
}

#content .open_blog .sub_control.link_arrow {
top: 11px;
left: 11px;
width: 15px !important;
height: 15px !important;
}

#xfollowers_button.followers .hide_overflow {
overflow: visible !important;
}

#xfollowers_button.followers {
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}

#content #right_column .no_push, #content #right_column .no_push.selected, #content #right_column .selected, #content #right_column .controls_section {
border-color: transparent  !important;
}

#content #right_column .no_push {
border: none !important;
padding-top: 0px !important;
}

#content #right_column .no_push.selected {
border: none !important;
}

#content #right_column .controls_section {
background-color: transparent !important;
}

#content #right_column #MissingE_marklist.controls_section li {
background-color: #213e4f !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.05) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
}

#content #right_column #MissingE_marklist.controls_section li, #content #right_column #MissingEdraftQueueTools li, #content #right_column #MissingE_marklist.controls_section li {
box-shadow: none !important;
}

#content #right_column .radar #MissingE_marklist.controls_section .MissingE_marklist_title.recessed {
background: none !important;
box-shadow: none !important;
}






/* POST STYLE */

#content .arrow {
background: url('http://data.sainet.co.uk/medigel/nipple/nipple_f0f0f0.png') no-repeat transparent scroll!important;
}

#content #posts .post {
background-color: #f0f0f0 !important;
}

#content .post_permalink {
background: transparent url('http://data.sainet.co.uk/medigel/dogear/dogear_fenris.png') !important;
margin-top: -1px !important;
}

#content p.read_more_container a.read_more {
color: #6695b0 !important;
text-decoration: none !important;
font-style: italic !important;
}

#content .post_info a {
color: #213e4f !important;
}

#content .full_answer_container_wrapper {
border-top: 1px solid rgba(0,0,0,0.05) !important;
}

#content .no_posts_found {
color: rgba(255,255,255,0.2) !important;
}

#content #posts .post .post_content, #content #posts .post .post_text_wrapper {
background: transparent !important;
background-image: none !important;
}

#content .full_answer_container.clearfix {
background: transparent !important;
}

#content .footer_links a, #content .footer_links.with_tags a, #content .footer_links .source_url {
color: rgba(0,0,0,0.7) !important;
}

#content #posts .post_footer_links a.tag {
color: rgba(0,0,0,0.4) !important;
}

#content .post_info {
color: rgba(0,0,0,0.9) !important;
}

#content .post .tags a {
color: rgba(0,0,0,0.8) !important;
}

#content .conversation_lines {
border: 1px solid rgba(0,0,0,0.08) !important;
border-radius: 4px !important;
}

#content .conversation_lines .chat_line {
background-color: rgba(0,0,0,0.05) !important;
border-color: rgba(0,0,0,0.08) !important;
color: rgba(0,0,0,0.8) !important;
}

#content #posts .tags {
line-height: 16px !important;
}




/* ASKS */

#content #posts .post_question {
background: rgba(0,0,0,0.1) !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
border: none !important;
color: rgba(0,0,0,0.7) !important;
}

#content #posts .post .post_question_nipple {
background: url('http://data.sainet.co.uk/medigel/general/master_sprites_rep.png') transparent no-repeat scroll -400px -290px !important;
opacity: 0.1 !important;
}




/* NOTIFICATIONS */

#content .notification {
font-family: HelveticaNeue, Helvetica, Arial, sans-serif !important;
background-color: #97b04b !important;
color: rgba(255,255,255,0.6) !important;
}

#content .notification a {
color: rgba(255,255,255,0.95) !important;
}

#content .notification blockquote {
border-color: rgba(255,255,255,0.6) !important;
}

#content .notification .notification_right .preview_frame {
border: 1px solid rgba(0,0,0,0.2) !important;
}

#content .notification .notification_right .preview_frame.regular {
background-color: rgba(0,0,0,0.25) !important;
}




/* FANMAIL */

#content #posts .post.fan_mail .message {
font-size: 16px !important;
background: none !important;
line-height: 19px !important;
font-family: sans-serif, helvetica, tahoma, arial !important;
}

#posts .controls a, .controls_link {
color: rgba(0,0,0,0.6) !important;
}

#content .controls_link.reply_link {
color: rgb(0,0,0) !important;
background-image: none !important;
background-position: top center !important;
background-repeat: no-repeat !important;
}

#content .controls_link.reply_link:hover {
opacity: .9 !important;
}

.fan_mail_read_more {
background-color: rgba(0,0,0,0.2) !important;
margin-top: 10px !important;
}

#dashboard_messages_inbox #posts div, #dashboard_submissions #posts div {
background-image: none !important;
}

#content .post_text_wrapper, #content .post_content.clearfix {
background-color: #ffffff !important;
}

#right_column a.send_fan_mail {
background-image: none !important;
text-shadow: none !important;
box-shadow: none !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}

#content #posts .post.fan_mail, .post.fan_mail .before {
background-color: #6695b0 !important;
color: rgba(0,0,0,0.7) !important;
background-image: none !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
border: none !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.46) !important;
}

#content #posts .post.fan_mail a {
color: rgba(0,0,0,0.4) !important;
font-weight: bold !important;
}

#content .post.fan_mail .arrow {
background: url('http://data.sainet.co.uk/medigel/nipple/nipple_fenris.png') no-repeat scroll !important;
}






/* POST EDITOR */

#content .photo_post_box, #the_as_link, #the_as_links a, #dashboard_editing #content .tabs li {
color: rgba(255,255,255,0.9) !important;
background-color: #97b04b !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}

#content #tag_editor {
-webkit-border-radius: 3px !important;
border-radius: 3px !important;
padding: 5px !important;
background-image: none !important;
border: none !important;
background-color: rgba(0,0,0,0.15) !important;
border-bottom: rgba(255,255,255,0.08) 1px solid !important;
border-top:  rgba(0,0,0,0.11) 1px solid !important;
color: rgba(0,0,0,0.9) !important;
}

#content #tag_editor .token {
background: none !important;
border: none !important;
}

#content .photo_post_box div {
color: rgba(0,0,0,0.7) !important;
}

#content .remove_photo {
margin-left: -20px !important;
opacity: 0.3 !important;
}

#content .photo_post_box input div {
color: #000000 !important;
}

#dashboard_editing #content .inline_text_field {
border-bottom: rgba(0,0,0,0.7) 1px solid !important;
}

#dashboard_editing #content h1, #dashboard_editing #content #slug_url, #dashboard_editing #content #tag_editor_input, #dashboard_editing #content .tag, #dashboard_editing #content #embed_video_description,  #dashboard_editing #content span.optional, #dashboard_editing #content a, #dashboard_editing #content h2, #dashboard_editing #content label, #dashboard_editing #content .post_question_asker, #dashboard_editing #content .post_question {
color: rgba(0,0,0,0.7) !important;
}

#dashboard_editing #content #right_column .text_field {
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
box-shadow: 0px 1px 1px rgba(0,0,0,0.2) !important;
}

#dashboard_editing #content .tabs li a {
color: rgba(0,0,0,0.5) !important;
}

#content_top, #content_bottom {
visibility: hidden;
}

.advanced_option_divider {
visibility: hidden !important;
}

.advanced_option {
border-bottom: transparent !important;
border-top: transparent !important;
}




/* AVATARS */

.avatar_frame, .post_avatar, .avatar {
background-color: transparent !important;
border-color: transparent !important;
}

#content #posts .notification .avatar_frame {
border: 0px solid !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.46);
}




/* PAGINATION */

#content .short_post_labels .short_label a {
border: none !important;
text-shadow: none !important;
color: #ffffff !important;
font-weight: bold !important;
}

#content .short_post_labels .short_label {
border: none !important;
}

#content #new_post.short_new_post.post.new_post {
background: none !important;
background-image: none !important;
border: none !important;
text-shadow: none !important;
box-shadow: none !important;
}

#content #dashboard_controls span.dashboard_controls_arrow_first_page {
background-image: url("http://data.sainet.co.uk/medigel/general/dashboard_controls_arrow_first_page_white.png") !important;
}

#content #dashboard_controls span.dashboard_controls_arrow_right {
background-image: url('http://data.sainet.co.uk/medigel/general/dashboard_controls_arrow_right_white.png') !important;
}

#content #dashboard_controls span.dashboard_controls_arrow_left {
background-image: url("http://data.sainet.co.uk/medigel/general/dashboard_controls_arrow_left_white.png") !important;
}

#content #left_column #dashboard_controls a, #content #left_column #dashboard_controls span.control {
text-shadow: none !important;
color: #ffffff !important;
}

#content #pagination #previous_page_link {
background-color: transparent !important;
color: rgba(0,0,0,0.75) !important;
}




/* MISCELLANEOUS */

#main_help .chrome.big.clear.help_contact_support_button {
color: #ffffff !important;
background-color: rgba(255,255,255,0.8) !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
background-image: none !important;
box-shadow: none !important;
}

#main_column {
width: 600px !important;
}

.MissingE_bookmark_text, .MissingE_bookmark_line {
background: none !important;
color: rgba(0,0,0,0.8) !important;
border: none !important;
}

#no_results_message {
color: rgba(255,255,255,0.4) !important;
}

#main_help #results.help_results.clearfix {
margin-bottom: 20px !important;
background: none !important;
}

#main_help #content a, #main_help #content span{
color: rgba(255,255,255,0.9) !important;
}

#help_footer {
background: rgba(255,255,255,0.4) !important;
}

#main_help #help_search.help_search {
margin-top: -30px !important;
}

#main_help #content {
margin-top: 9px !important;
}

#main_about #nav_about, #main_help #nav_help, #main_contact #nav_contact, #main_apps #nav_apps, #main_tips #nav_tips, #main_causes #nav_causes, #main_press #nav_press, #main_team #nav_team, #main_jobs #nav_jobs, #main_developers #nav_developers, #main_sponsors #nav_sponsors, #main_policy .terms #nav_terms, #main_policy .privacy #nav_privacy, #main_policy .community #nav_community, #main_policy .impressum #nav_impressum {
background: none !important;
}

#main_doc_page #developers span.title, #main_doc_page #developers, div.white_pane a, body#dashboard_followers #content h1 {
color: rgba(0,0,0,0.9) !important;
}

#main_doc_page p#developers {
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
box-shadow: 0pt 1px 5px rgba(0, 0, 0, 0.46);
}

#main_doc_page form#help_search {
box-shadow: 0pt 1px 5px rgba(0, 0, 0, 0.46);
}

#main_doc_page #content div#results a {
color: rgba(255,255,255,0.9) !important;
}

#dashboard_following #content, #dashboard_settings #content, #dashboard_tumblelog_settings #content {
background-color: #e4e4e4 !important;
}

#dashboard_following .grey_note {
background: rgba(0,0,0,0.4) !important;
text-align: center;
padding: 5px !important;
}

#dashboard_block #content #left_column.clearfix {
background-color: #ffffff !important;
color: #000000 !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
}

#dashboard_block #content #left_column.clearfix a {
color: #000000 !important;
}

#dashboard_block #content #left_column.clearfix .description {
color: #000000 !important;
}

#dashboard_lookup_contacts #content #finder_container {
background: #e4e4e4 !important;
}

#dashboard_directory #content {
width: 100% !important;
background: #e4e4e4 !important;
}

#content .answer_container {
background-color: #ffffff !important;
box-shadow: 0pt 1px 5px rgba(0, 0, 0, 0.46);
color: #000000 !important;
}

#content .answer_container a {
color: #000000 !important;
background-color: #ffffff !important;
}

#tumblr_lightbox {
background-color: rgba(0,0,0,0.5) !important;
}

#dashboard_settings .right_column,  #dashboard_tumblelog_settings .right_column {
background: transparent !important;
}




/* POPOVER */

#content #popover_blogs {
background: #213e4f !important;
}

#content .popover_inner {
box-shadow: none !important;
background: none !important;
}

#content #popover_blogs .popover_menu_item {
box-shadow: 1px 0px 1px rgba(0,0,0,0.1) inset !important;
background: none !important;
border: transparent !important;
}

#content #popover_blogs .popover_menu_item:hover {
background-color: rgba(255,255,255,0.05) !important;
}

#content #popover_blogs .blog_icon {
box-shadow: none !important;
border: none !important;
}




/* FOOTER */

#footer, #footer a {
color: rgba(0,0,0,0.8) !important;
border: none !important;
}




}
