css:
@-moz-document domain('plus.google.com') {
/* ============== */
/*  横幅を広げる  */
/* ============== */
div#contentPane div:not([token^='photos/']):not([token$='/posts']) div[role='navigation'] + div.OT {
	/* メインストリームの横幅・親要素 */
	width			: 100% !important;
	margin-top		: 20px !important; /* 右カラムに被らないように少し下にずらす */
	padding-left 	: 0px !important;
	padding-right	: 0px !important;
	left			: 20px !important;
}

div.Sea {
	/* 共有フキダシの親要素 */
	padding-left	: 40px !important;
	left			: -40px !important;
}
div[role='navigation'] + div div > div[guidedhelpid='sharebox'] {
	/* 共有フキダシの横幅 */
	width	: 100% !important;
}

div[role='navigation'] + div div > div[guidedhelpid='sharebox'] div.orZBjd {
	/* 共有フキダシ内テキストボックスの横幅 */
	width	: 100% !important;
}
div[role='navigation'] + div div > div[guidedhelpid='sharebox'] div[guidedhelpid='toolbar'] {
	/* 共有フキダシ内各アイコンの位置調整 */
	padding-left	: 22px !important;
}

div[guidedhelpid='streamcontent'] {
	/* メインストリームの横幅・親要素 */
	width	: 100% !important;
}
div.Bsb {
	/* 検索画面でのメインストリームを右カラムに被らないように少し下にずらす */
	/*  クラス名指定はアレなのでMEMO  */
	margin-top	: 20px !important;
}
div.OT, div.wFb {
	/* 検索画面でのメインストリームの横幅・親要素 */
	/*  クラス名指定はアレなのでMEMO  */
	width	: 100% !important;
}
div.FFb {
	/* 検索結果上部にある関連度の高いユーザなど？表示部 */
	/*  クラス名指定はアレなのでMEMO  */
	margin-right	: 42px !important;
}
div.uFb {
	/* 新着順|関連順、一時停止ボタン */
	/*  クラス名指定はアレなのでMEMO  */
	margin-right	: 42px !important;
}
div#contentPane > div:not([token$='/posts']) div[guidedhelpid='streamcontent'] > div > div[id^='update-'] {
	/* 各ポストの横幅 */
	width			: 100% !important;
	margin-left		: 0px !important;
	padding-left	: 108px !important;
	left			: -40px !important;
}
div.Ag {
	/* ポスト内の写真 */
	margin				: auto !important;
	background-color	: transparent !important;
}
div[guidedhelpid='streamcontent'] > div:nth-last-child(2) {
	/* 「もっと見る」ボタン */
	margin-right	: 42px !important;
}

/* ================== */
/*  右カラムの情報欄  */
/* ================== */
div#contentPane > div div[role='navigation'] + div + div {
	/* 通常時は上の方に折りたたんでおく */
	padding-right		: 10px !important;

	border-radius		: 0 0 10px 10px !important;

	top					: 35px !important;
	right				: 40px !important;
	position			: absolute !important;

	/* Blue Themeのオプション扱いなので青 */
	background-color	: #BEF !important;

	max-height			: 50px !important;
	overflow			: hidden !important;

	-o-transition		: all 0.3s ease-in-out !important;
	-webkit-transition	: all 0.3s ease-in-out !important;
	-moz-transition		: all 0.3s ease-in-out !important;
	transition			: all 0.3s ease-in-out !important;
}
div#contentPane > div:not([token$='/posts']) div[role='navigation'] + div + div:hover {
	/* ホバー時にびよーん */
	max-height			: 700px !important;
	border-radius		: 0 0 10px 10px !important;

	/* Blue Themeのオプション扱いなので青 */
	background-color	: #EFF !important;

	overflow-y			: scroll !important;

	-o-transition		: all 0.2s ease-in-out !important;
	-webkit-transition	: all 0.2s ease-in-out !important;
	-moz-transition		: all 0.2s ease-in-out !important;
	transition			: all 0.2s ease-in-out !important;

	box-shadow			: 0 0 10px #666 !important;

	z-index				: 1 !important;
}
div#contentPane > div:not([token$='/posts']) div[role='navigation'] + div + div > div:first-child {
	/* １つめの要素を固定で下げる */
	/* メインストリームと検索画面などでカラムの開始高さが違う事への対応 */
	padding-top	: 60px !important;
}

/* ============== */
/*  ハングアウト  */
/* ============== */
body > div > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) {
	/* チャットの幅を広げる（ビデオ部分を狭める） */
	width	: 65% !important;
}
} /* moz-documentブロック終 */
