css:
@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {
#videoHeader #videoHeaderDetail h2 {
color:#fff;
text-shadow:0 0 4px #ff0, 0 0 20px #f90;
font-size:20px !important;
}
#videoHeader #videoHeaderDetail .videoDetailExpand {
height:auto !important;
}


.handler,
.commentOuter,
.commentInner {
border-radius:0;
bottom:-40px;
box-shadow:none;
opacity:0 !important;
-webkit-transition: all 200ms linear;
-moz-transition: all 200ms linear;
-ms-transition: all 200ms linear;
-o-transition: all 200ms linear;
transition: all 200ms linear;
}

.handler,
.commentOuter {
background:none;
}

.handler {
border-radius:0;
color:#fff;
text-align:center;
border-bottom:solid 2px #fff;
cursor:pointer;
}
.handler:hover {
font-size:30px;
border-color:#08f;
}
.handler span {
font-weight:bold;
text-shadow:0 0 2px #000, 0 0 10px #000;
}

#playerContainer:hover .handler,
#playerContainer:hover .commentOuter,
#playerContainer:hover .commentInner {
  bottom:64px;
  opacity:1 !important;
}


.commentOuter input{
color:#fff;
  text-shadow:0 0 2px #000, 0 0 5px #000;
  font-weight:bold;
  border-bottom:solid 2px #fff;
}

.commandInput input {
  font-size:12px;
}

.commentInputContainer,
.commentInput,
.commandInput {
  border:none;
  height:36px;
}

/*
#videoHeader #videoHeaderDetail {
  top:0 !important;
}
#videoHeader #videoTagContainer {
  position:static !important;
}
#videoHeader #videoTagContainer.default{
  height:auto !important;
}
#videoHeader #videoTagContainer .filter {
  display:none;
}
*/

#textMarquee,
#ichibaPanel,
#playerCommentPanelOuter {
  opacity:0.2;
}

#textMarquee:hover,
#ichibaPanel:hover,
#playerCommentPanelOuter:hover {
  opacity:1;
}


#playerContainer {
Xmargin-bottom:74px;
}


.wrapper {
background:#333;
}
#tabMenu li{
background:#ddd;
}


#playlistInner ol li {
max-width:100px !important;
max-height:75px !important;
top:0 !important; left:0 !important;
}
#playlistInner ol li .playlistActiveInfo .playlistInformation {
bottom:80px !important;
}


#textMarquee {
margin-top:4px;
}
#textMarquee .textMarqueeOuter,
#textMarquee .textMarqueeInner {

height:12px;
line-height:12px;
}

#textMarquee .textMarqueeInner {
font-size:12px;
}

.commentOuter.oldType {
display:block !important;
bottom:0 !important;
}

.commentOuter.oldType,
.commentOuter.oldType .commentInner {
opacity:1 !important;
}
.commentOuter.oldType .commentInner {
box-shadow:inset #888 0 0 0 1px;
}
.commentOuter.oldType input {
text-shadow:none;
color:#fff;
}
}
