css:
@-moz-document domain('www.fuska.nu'), url-prefix('http://fuska.nu') {
/* Original design made by Knewt and FIQ

Dark Theme by DarknessSky (themeister.se) */





/* Copyright */

#footer .left p:before { display: block !important; content: "Theme by DarknessSky." !important; position: absolute; margin-left: 180px; }

/* End Copyright */

/* Main */

#topBarMenu { background: #222 !important; }

.presInfoList, .presInfoList b { color: #B0B0B0 !important; }

#content { background: rgb(41,41,41) !important; }

#content a { color: rgb(205,205,205) !important; }

#content a:hover { color: rgb(150,150,150) !important; }

#content p { color: #B0B0B0 !important; }

.listSmall li span { color: #B0B0B0 !important; }

.subheading { background: rgb(100,100,100) !important; color: #FFF !important; }

.main { color: rgb(235,235,235) !important; }

#content h1 { color: #FFFFFF !important; }

body { background: #222 !important; }

#menuWrapper { background: none !important; }

/* Sök */

#search input[type=text], #search input.search { background-color: rgb(100,100,100) !important; box-shadow: none !important; text-shadow: none !important; }

#search input.search { height: 29px !important; }

ul.game-list li:hover { background: rgb(60,60,60) !important; }

.search-options { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: 1px solid #333 !important; border-radius: 4px; border-top: 1px solid #111 !important;
border-right: 1px solid #111 !important;
border-left: 1px solid #111 !important; }

.search-options ul li a, .search-options ul { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: none !important; }

/* Startsidefråga  */

h4.side-options { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: 1px solid #333 !important; border-radius: 4px; border-top: 1px solid #111 !important;
border-right: 1px solid #111 !important;
border-left: 1px solid #111 !important; text-shadow: none !important; color: #DDD !important; }

.side div.side-options { background: rgb(41,41,41) !important; border: none !important; color: rgb(235,235,235) !important; box-shadow: none !important; }

.fraga_ram { border: 1px solid rgb(200,200,200) !important; }

.fraga_innehall { background-color: rgb(220,220,220) !important; border: none !important; }

a.selected, .heading li a:hover { background: rgb(41,41,41) !important; text-shadow: none !important; }

/* Spelsidor */

.game-info .info { background: rgb(60,60,60) !important; }

.game-info .info strong { color: rgb(220,220,220) !important; }

.game-info .info li { color: rgb(180,180,180) !important; }

div.side-options .gameRating h5 { text-shadow: none !important; color: rgb(215,215,215) !important; }

form .comment { background: rgb(100,100,100) !important; }

form .comment strong { color: rgb(240,240,240) !important; }

/* Input varianter */

textarea, input[type=text] { background: rgb(60,60,60) !important; color: rgb(200,200,200) !important; border: 1px solid rgb(230,230,230) !important; }

select { background: rgb(60,60,60) !important; color: rgb(240,240,240) !important; border-radius: 4px; }

/* Forum */

ul.breadcrumb li.current a {
 background: none !important;
}

ul.forum-list li { background: rgb(41,41,41) !important; border: none !important; border-bottom: 1px dotted rgb(100,100,100) !important; }

ul.forum-list { border: none !important; }

p.recent { background: rgb(80,80,80) !important; text-shadow: 1px 1px #000 !important; border: none !important; }

.info h2 a { text-shadow: 1px 2px #000 !important; }

.info span { color: rgb(225,225,225) !important; }

.info h4 { color: rgb(170,170,170) !important; }

.info { color: rgb(190,190,190) !important; }

.textRight { background: rgb(60,60,60) !important; }

div.quote { background: rgb(160,160,160) !important; color: rgb(40,40,40) !important; }

ul.breadcrumb { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: none !important; }

ul.breadcrumb li a { background: url(http://themeister.se/fnu/breadcrumb-seperator-dark.png) no-repeat top right !important; }

.forum-heading { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: 1px solid #333 !important; border-radius: 4px; border-top: 1px solid #111 !important;
border-right: 1px solid #111 !important;
border-left: 1px solid #111 !important; }

.forum-heading h3, .forum-heading span.floatR { text-shadow: 2px 2px #333 !important; color: rgb(235,235,235) !important; }

.page-nav li { background: rgb(51,51,51) !important; }

.page-nav li a { text-shadow: none !important; }

.page-nav li.current { border-color: #F0F0F0 !important; box-shadow:0 0 10px #F0F0F0 !important; }

p.signature { border-top: 2px dotted #B0B0B0; }
  
ul.options { margin-top: -3px; background: rgb(60,60,60); }

/* Topbar */

#topBar ul#topBarMenu li a { border: none !important; }

#topBar, #topBarMenu { background: url(http://fuska.nu/images/headingBg.png) !important; text-shadow: none !important; }

.profile, .profile span, .profile a, ul#topBarMenu li ul, ul#topBarMenu ul li, ul#topBarMenu li ul a { background: #222 !important; text-shadow: none !important; border: none !important; }

#topBar, ul#topBarMenu li, #topBar li { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: none !important; }

#topBar ul li b { color: #FFF !important; }

#topBar ul li a:hover, #topBar ul li a:visited { background: #222 !important; border: none !important; }

ul#topBarMenu li ul li i { color: rgb(150,150,150) !important; }

ul#topBarMenu li ul li a:hover { color: rgb(120,120,120) !important; }

/* Klubbar */

.header {

	display: block;

	overflow: hidden;

	background: rgb(100,100,100);

	color: #FFF;

	padding: 8px !important;

	margin-top: 5px !important;

	margin-bottom: 5px !important;

	border: 0;

	-webkit-border-radius: 4px;

	-moz-border-radius: 4px;

	border-radius: 4px;

	font-weight: bold;

}

/* Moderatorsfix */

.sidan { color: rgb(150,150,150) !important; }

.rubrik { background: url(http://fuska.nu/images/headingBg.png) repeat-x !important; border: 1px solid #333 !important; border-radius: 4px; border-top: 1px solid #111 !important;
border-right: 1px solid #111 !important;
border-left: 1px solid #111 !important;
font-size: 16px;
letter-spacing: 1px;
text-shadow: 1px 1px 1px black;
padding: 9px 5px 0 11px; }

.rubrik {
	background: url(http://fuska.nu/images/headingBg.png) repeat-x !important;
	display: block !important;
	color: #ddd !important;
	margin: 0 !important;
	height: 37px !important;
	border-top: 1px solid #111 !important;
	border-right: 1px solid #111 !important;
	border-left: 1px solid #111 !important;
	-webkit-border-radius: 4px !important;
	-moz-border-radius: 4px !important;
	border-radius: 4px !important;
	overflow: hidden !important;
}

/* Popups */

.popup form div { color: rgb(230,230,230) !important; background: rgb(100,100,100) !important; }

.popup form strong, .popup form, .popup { color: rgb(220,220,220) !important; }

.popup form div.error { background: #222 !important; }

.submit {
	color: #eee !important;
	text-decoration: none !important;
	text-shadow: 0 -1px 0 #000;
	border: 1px solid #333;
	box-shadow: 0 1px 1px #888;
	background: url(http://fuska.nu/images/btn-black.png);
padding: 7px 14px 8px 14px;
-webkit-border-radius: 20px;
	-moz-border-radius: 20px;
	border-radius: 20px;
}
input[type=submit]:hover, .submit:hover {
	background: url(http://fuska.nu/images/btn-black-hover.png);
} 

form { color: rgb(190,190,190) !important; }

}
