css:
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://kawatarou.info/bookmark/){
/*
===== CSS Information ====================================

Name         ShishiB ï¼ˆhttp://kawatarou.info/bookmark/ï¼‰
creat        2008/04/17 - maplebread
lastupdate   2008/04/18 - maplebread
Ver          1.0
URL          http://d.hatena.ne.njp

æ›´æ–°å±¥æ­´
Ver 1.0ï¼šåˆç‰ˆ

==========================================================
*/

*{
margin:0 !important;
padding:0!important;
}

#header a[href="http://kawatarou.info/bookmark/"]{
height:40px !important;
width:240px !important;
font-size:0em !important;
display:block !important;
background-image:none !important;
color:#234a9e !important;
}

h1{
background-image:none !important;
}

#header #navigation {
position:absolute !important;
top:40px !important;
right:10px !important;
}

#header #navigation a{
font-size:12px !important;
color:#fff !important;
font-weight:normal !important;
margin:0 2em !important;
}

form#search{
position:absolute !important;
top:15px !important;
left:280px !important;
}

form#search:after{
content:"æ¤œç´¢" !important;
border:1px solid !important;
font-size:10px !important;
}


p#sort {
background-color:#5279e7 !important;
border-bottom:1px solid #06289b !important;
width:100% !important;
font-size:12px !important;
color:#fff !important;
font-weight:normal !important;
padding-left:15px !important;
}

p#sort a{
color:#fff !important;
font-weight:normal !important;
margin:0 1em !important;
background-color:#5279e7 !important;
}

div#header{
height :40px !important;
background-color:#234a9e !important;
background-image:url(http://maplebread.googlepages.com/shishib-top.gif) !important;
background-repeat:no-repeat !important;
background-position:left !important;
border-bottom:1px solid #06289b !important;
}

div.autopagerize_page_element{
margin:2em 0 10px 5% !important;
padding-right:28% !important;
}

p.tags{
margin:2em 0 10px 5% !important;
padding-right:28% !important;
width:auto !important;
}

ol#bookmarks{
width:auto !important;
}

li.xfolkentry{
border :none !important;
line-height:1.2em !important;
margin-bottom:20px !important;
}

div.address{
color:gray!important;
margin-left:2em !important;
}

div.description{
color:green !important;
margin-left:2em !important;
margin-top:5px !important;
}

div.meta {
color:black !important;
margin-left:2em !important;
}

div.meta a{
color:blue !important;
}

div#sidebar{
top:5em !important;
}

div#sidebar h2{
font-size:1.2em !important;
margin-top:1.5em !important;
color:#000 !important;
}

div#sidebar h2 a{
color:black !important;
}

#sidebar #linked{
background:none !important;
border:none !important;
}

#sidebar #linked a{
color:#000 !important;
}

#sidebar #linked a:before{
content:">" !important;
}

#sidebar #related {
background-color:#dddddd !important;
background-image:none !important;
border:none !important;
}

#sidebar #related a{
color:blue !important;
}

#sidebar #popular ,#sidebar #recent{
background-image:none !important;
border:none !important;
}

#sidebar #popular a, #sidebar #recent a{
color:blue !important;
}

.autopagerize_insert_before{
text-align:center !important;
color:#000 !important;
background-color:#fff!important;
}

.autopagerize_insert_before a{
margin:0 1em !important;
color:blue !important;
background-color:#fff!important;
}

}
