css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hordes.fr") {

#modules{
top:5px;
margin-left:30px;
margin-top:24px;
position:absolute;
z-index:4;
padding-left:5px;
padding-right:5px;
background-color:#5c2b20;
-moz-border-radius:0px;
outline:1px solid black;
border:1px solid #f0d79e;
}

div.bartitle{
float: left;
}
div.bartitle span { /* correction pour FF linux */
vertical-align: 2px;
}

div.mmitem {
margin-top: 0px;
float: left;
display: none;
}

div.mmfill{
display: none;
}

div.mmitem div.mmtitle {
-moz-border-radius: 3px 3px 0px 0px;
/*border: 1px solid #b37c4a;
border-bottom: none; */
border-top: 1px solid #b37c4a;
border-right: 1px solid #b37c4a;
border-left: 1px solid #b37c4a;
margin: 1px;
margin-bottom: 0px;
margin-top: 2px;
padding: 0px;
padding-left: 3px;
padding-right: 2px;
height: 15px; /* en test */
color: #D9C28F !important; /* légèrement plus foncé que #f0d79e , en test */
}

div.mmcontent{
display: none;
padding-top: 5px;
margin-left: 5px;
position:absolute; left: 0px; top:18px;
font-family:"Century Gothic","Arial","Trebuchet MS",Verdana,sans-serif;
font-variant: normal;
font-size: 10pt;
letter-spacing: normal;
}
div.bartitle div.mmcontent{ /* div d'effacement */
width: 300px; /* TODO à revoir quand dynamique CALC essayer 100% */
/*font-size:1pt;*/
background-color: #5c2b20; 
height: 1px;
margin-top: 0px; /* DEBUG */
/*display: none !important;*/ /* DEBUG */
}

/* ligne de séparation */
#modules:hover .modmantitre{ 
border-bottom:1px solid #b37c4a;
margin-bottom:-1px; /* en test */
}
#moduleshelp .modmantitre{ 
border-bottom:1px solid #b37c4a;
margin-bottom: 5px !important;
}

div.mmitem div.mmcontent{
/*background-color:#693428; *//* à voir: plus clair */
background-color: #59281d; /* à voir: plus foncé */
min-height: 115px;/* TODO à ajuster pour cacher les <p> */
min-width: 300px;/* TODO à ajuster pour cacher les <p> */
-moz-border-radius: 0px 0px 3px 3px;
border-bottom: 1px solid #b37c4a;
border-right: 1px solid #b37c4a;
border-left: 1px solid #b37c4a;
padding-right: 4px; /* compensation */
}

div.mmitem:hover div.mmcontent {
display: block;
}

div.mmitem:hover div.mmtitle {
color:white !important;
/*background-color:#693428;*/ /* à voir */
background-color: #59281d; /* à voir: plus foncé */
}

/******************************************/

div.modmantitre {
font-size:8pt;
font-variant:small-caps;
/*height:auto;*/ /* le truc surement coupable par rapport à la différence win/linux */
height: 17px;
margin:0;
padding:0;
text-transform:none;
color:#f0d79e !important;
font-family:"Trebuchet MS","Arial",Verdana,sans-serif;
letter-spacing:1px;
/*line-height:15px;*/ /* 17 avant, semble sans effet */
text-align:left;
}

#moduleshelp{
padding-top:8px;
position:absolute;
width:100%;
z-index:100;
display:none;
}

#moduleshelp div.helpboxcontent{
margin-left:230px;
width:450px;
position:absolute;
z-index:9;
padding:5px;
color:#f0d79e;
background-color:#5c2b20;
-moz-border-radius:0px;
outline:2px solid black;
border:1px solid #b37c4a;
}

div.modmantitre img {
vertical-align:-9%;
}

#modules p,
/*#modules ul,*/
#modules #mmbar span,
#modules #mmbar div.modhelp,
#modules div.mmback{
display:none;
}

#modules #mmbar span{
margin-right: 8px;
}
#modules #mmbar div.modhelp img{
opacity: 0.7;
cursor: pointer;
}
#modules #mmbar div.modhelp img:hover{
opacity: 1; /* hover de img, l'image de l'aide se renforce au survol */
}

#modules img.modoptions{
opacity: 0.6;
cursor: default; /* pour changer */
width: 10px !important; /* réduit , =taille nouvelle icone */
}
#modules img.modoptions:hover{
opacity: 1;
}

#modules:hover p,
#modules:hover .mmfill,
#modules:hover div.bartitle div.mmcontent,
#modules:hover div.mmback/*,
#modules:hover ul*/{
display:block;
}
#modules:hover #mmbar span,
#modules:hover div.mmitem,
#modules:hover #mmbar div.modhelp{
display:inline;
}

#modules #mmbar div.modhelp {
float: right;
margin-top: 2px;
}
#modules div.modon,
#modules div.modoff{
display:block;
width:150px;
height:18px;
overflow:hidden;
text-decoration:none;
}
#modules div:active, #modules div:focus {
outline: 0;
}

#modules ul div {
-moz-border-radius: 2px;
color: #DDAB76;
cursor: pointer;
}

#modules ul div.modactive {
/*font-weight: bold;*/
color: #FFDCB6 ; /* avant : #DDAB76 */
}

#modules ul div.modon span{
text-decoration:underline;
}
#modules ul div.modoff span{
text-decoration:line-through;
color: #999999;
}
#modules ul div.modon:hover{
background-color:#696486;
}
#modules ul div.modoff:hover{
background-color:#534E6A;
color: #DDDDDD;
}
#modules ul div.modon img{
width:16px;
height:16px;
vertical-align:-25%;
}
#modules ul div.modoff img{
width:16px;
height:16px;
vertical-align:-25%;
opacity: 0.3;
}

#modules div.core {
display:block;
width:150px;
height:18px;
overflow:hidden;
text-decoration:none;
font-weight: bold;
color: lightgrey;
cursor: default;
}

#modules ul{
margin:0px ;
padding : 0px;
float:left;
position:relative;
margin-bottom:8px;
list-style:none;
}
#modules ul li{
margin:0px ;
margin-left: 5px ; /* séparation entre les colonnes */
padding : 0px;
}
#modules p,
#moduleshelp p{
margin:0px ;
padding : 0px;
font-size:9pt;
line-height:11pt;
text-align:justify;
}

#modules p{
margin-bottom:10px;
width:305px;/* TODO vérifier si nécessaire */
}
#modules:hover div.mmback { /* !!!! complété dynamiquement par calcul !!!! CALC */
margin-top: 6px; /* marge pour le 1er paragraphe à cause du div d'effacement */
min-height: 120px; /* TODO vérifier si nécessaire */
}
#modules p i,
#moduleshelp p i {
color: #BB0000;
}

#moduleshelp .modmantitre.modheader{
margin-top: 20px ;
}
#moduleshelp img.modheader{
margin-right: 5px ;
}
#moduleshelp div.moddoc{
margin-top: 8px;
max-height:380px; /* en réglage */
overflow: auto; /* pour le scroll si besoin */
background-color:#59281d; /* légèrement plus foncé, trouver le bon ton */
/*padding:5px;
color:#f0d79e;
outline:2px solid black;*/
border:1px solid #b37c4a; /* en réglage */
}
#moduleshelp div.moddoc span.nomoremodules {
color:#59281d; /* la même que le background-color de div.moddoc */
}
#moduleshelp div.moddoc p{
padding-right:5px; /* sinon touche la bare de scroll */
}
#moduleshelp div.moddoc p,
#moduleshelp div.moddoc img.modheader {
padding-left:5px; /* pas coller à la zone de scroll*/
}

#moduleshelp a.button{
float:right;
}

.creditsHide{
display:none ;
}

.creditShow{
display:block;
margin-top: 5px;
font-size: smaller;
font-style: italic;
padding-right:5px;
padding-left:5px;
}


﻿
#RepEclairId{
float: right;
}

#RepEclairId div{
float: right;
cursor: pointer;
}

#RepEclairId div img {
padding-top: 5px;
padding-bottom: 3px;
opacity: 0.6;
}
#RepEclairId div:hover img {
opacity: 1;
}


#parboxid{
top: 180px;
padding-top:8px;
position:absolute;
width:100%;
z-index:100;
display:none;
}

#parboxid div.parboxcontent{
margin-left:230px;
width:550px;
position:absolute;
z-index:9;
padding:5px;
color:#f0d79e;
background-color:#7E4D2A;
-moz-border-radius:0px;
outline:2px solid black;
border:1px solid #b37c4a;
}

/*
#parboxid h1{
margin:0px ;
padding : 0px;
height:auto;
font-size:8pt;
text-transform:none;
font-variant:small-caps;
background:none;
}*/

#parboxid h2{
background-image: none !important;
}

#parboxid p{
margin:0px ;
padding : 0px;
font-size:9pt;
line-height:11pt;
text-align:justify;
}

#parboxid p {
width: 330px;
float: left;
}

#parboxid h1.eclairheader{
margin-top: 20px ;
}
#parboxid img.eclairheader{
margin-right: 5px ;
}


#parboxid a.button{
float:right; 
margin-right: 4px !important;;
width: 180px !important;
}

#parboxid form {
margin-top: 12px !important;
width: 530px !important;
}

#parboxid label{
/*margin-top: 14px;*/
width: 120px !important;
}
#parboxid input.partxt {
width: 260px !important;
}
#parboxid input.parlab {
width: 100px !important;
}
#parboxid input.parurl {
width: 390px !important;
}

}
