css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body{
    background: #D1D4DF !important;
}

.watch-branded, 
.watch{
    background: #FFF !important;
    width: 77% !important;
    padding: 0px 10px 0px 10px !important;
}

#masthead-utility{
    width: 96% !important;
}

#footer{
    width: 98% !important;
    padding: 0px 10px 0px 10px !important;
}

#watch-headline-title, 
#footer{
    background: url(http://img.photobucket.com/albums/v632/niclildee/PROFILES/BGS/stripe.png?t=1270163620) !important;
    color: #FF3F3F !important;
}


#masthead-container{
    background: #FFF !important;
    padding: 0px 10px 0px 10px !important;
}

#baseDiv{
    background: #FFF !important;
    padding: 5px 10px 0px 10px !important;
}

#active_sharing_div, 
#promotedVidsContainer, 
#ad300x250, 
#channelBrandDiv, 
#channelBrandCap, 
#watch-channel-brand-div, 
#watch-channel-brand-cap, 
#copyright, 
.spacer-lg, 
.side-announcement-box, 
.img-yellow-message-top-cap, 
.img-yellow-message-bottom-cap, 
.chrome-link, 
.ad-div, 
.alignC, 
.hpYellowBlock, 
.hpYellowBlockBot, 
.hpSideBlock, 
.wsHeading, 
.action-button-leftcap, 
.action-button-rightcap, 
.hpPurpleBlockBot, 
.expand-panel .expand-header .arrow, 
img[src*="pic_subsbox_"], 
.homepage-yellow-block, 
.homepage-yellow-block-bot, 
#feed_promoted, 
.feedmodule-promoted, 
.handle, 
#PRO-titlebar, 
img[src*="http://s.ytimg.com/yt/img/ad_ytlive_ticker_1_875x25-vfl64191.png"], 
img[src*="http://s.ytimg.com/yt/img/ad_ytlive_ticker_3_875x25-vfl64191.png"], 
#ytlive-promo-ugc, 
#search-pva, 
.action-button-dropcap, 
#chrome-link, 
.feedmodule-data#PRO-data, 
.img-blue-bottom-cap, 
.feedmodule-upper-left-right-border-blue#feed_promoted-content{
    display: none !important
}

a:link, 
a:visited, 
a:active{ 
    color: #7A84A6 !important;
}

.edit-link, 
.eLink, 
.watch-comment-action a, 
div[id*="reply_comment_form_id"], 
.search-button-text, 
.friend_name{
    color: #7A84A6 !important;
    border-bottom: 0px !important;
}
 
h2, 
h3, 
h4, 
h5, 
h6, 
#vidTitle,
#right-column, 
#catIndexGridHeading, 
#footer 
.hpBlockHeadingGray, 
.name, 
.title,  
.wsHeading, 
.header, 
.links 
th, 
.headerTitleLite, 
.yte-text, 
.fm2-title, 
.yt-uix-button{
    color: #FF3F3F !important;
}

.statModule-title, 
#watch-vid-title, 
.smallLabel, 
h1{
    color: #AFAFAF !important;
}

.watch-action-text, 
.largeText{
    color: #FF6F6F !important;
}

.expand-panel .expand-header{
    color: #FF6F6F !important;
    font-size: 14px !important;
}

.action-button .action-button-text{
    color: #AFAFAF !important;
    text-decoration: none !important;
    background: #EEEEEE !important;
    border: 1px solid #CFCFCF !important; 
}

.yt-button-urgent, 
.yt-dropdown-menu{
    color: #AFAFAF !important;
    text-decoration: none !important;
    background: #EEEEEE !important;
    border: 1px solid #CFCFCF !important; 
}

.watch-comment-pagination {
    color: #FF3F3F !important;
    background-color: #E6EBEF !important;
}

#watch-channel-vids-div{
    background-color: #EFF3FF !important;
    border: 0px !important; 
}

#watch-other-vids{
    border-top: 1px solid #CCC;
    border-bottom: 1px solid #CCC;
    background-color: #EEEEEE !important;
}


.watch-body{
    background-color: #EEEEEE !important;
    border: 0px !important; 
}

.pagingDiv{
    color: #FF3F3F !important;
    background-color: #E6EBEF !important;
}

.channelIconWrapper, 
.wsBody, 
.user-thumb-medium, 
.vimg90, 
.v90WrapperOuter, 
.vimg120, 
.v120WrapperOuter, 
.vimg50, 
.v50WrapperOuter, 
.user-thumb-large,  
.vimg75, 
.v75WrapperOuter, 
#manageContent{ 
    background: #FFF !important; 
    border: 0px !important; 
}

.watch-module-head:hover, 
.watch-expander-head:hover, 
a.video-list-item-link:hover,
.rental-info-box{
    background: #E6EBEF !important;
    background-color: #EAEAEA !important
}

.input-focused textarea, 
.search-term-focus{
-moz-appearance:none!important;
border: 2px solid #FF6F6F !important;
}


}
