@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

/*
 * Extended playlist menu
 * Extended Favorites menu
 * lights out
 * New design fix
 * Bigger player sizes
 * Enhancer fix halfcut screen
 */


/* Remove Grey Background */
body {
background: #fbfbfb !important;
}

#watch-container {
padding-top:4px !important;
}
#masthead-container {
border-bottom:1px solid #333333 !important;
}

#masthead {
background:inherit !important;
}



/* Remove Right-Sidebar (Recommended Videos and Adverts) */
#video-sidebar {
display:none !important;
}

/* Widen Feed Section */
#feed {
margin-right:0 !important;
width:770px !important;
background-color: #333333 !important;
}
.feed-item .feed-item-visual h4 {
width:100% !important;
}

.feed-item-outer {
padding:14px 15px !important;; 
}
/* Relocate Feed Author / Details */
.feed-item-main .feed-item-author {
position:absolute !important;
float:right !important;
left:680px !important;
top:0px !important;
}

/* For Announcement Entries */
.feed-item-main .feed-item-thumb .feed-item-author {
margin-left:90px !important;
}

.feed-item-content {
margin-left:120px !important;
background:none !important;
min-height:60px !important;
}

.feed-item h3 {
color:#999999 !important;
}

[data-filter-type="u"] .feed-item-content .feed-item-actions-line {
height:0px !important;
margin-top:0px !important;
line-height:0px !important;
font-size:0px !important;
}

/* Resize Author's Avatar */
.feed-item-main .feed-item-author span.clip,
.feed-item-main .feed-item-author .video-thumb,
.feed-item-main .feed-item-author .profile-centering-wrap {
width:88px !important;
height:88px !important;
position:static !important;
}
.feed-item-main .feed-item-author img {
width:88px !important;
height:88px !important;
z-index:0 !important;
}

.feed-item-main .feed-item-time {
background-color:#fbfbfb !important;
z-index:1 !important;
color:#333 !important;
min-width:60px !important;
text-align:right !important;
}

/* Footer Fixes */
#feed-background {
background:inherit !important;
}
#footer {
padding-bottom:0 !important;
}

/* OPTIONAL FEATURES */

/* Hide Adverts on Left Sidebar (Popular Videos/Channels) */
.guide .guide-section:nth-child(n+3) { display:none !important; }

/* Expand all Subscriptions Buttons */
.yt-uix-expander-collapsed .guide-item-container.hideable {
display:block !important;
}
.guide-show-more-less { display:none !important; }

/* Hide Add Channel */
#guide-builder-promo {
display:none !important;
}
#feed .feed-header {
border-top-right-radius:0 !important;
}

.feed-header.before-feed-content {
background:#262626 !important;
}

/* END */

#logo{
	background: url("data:image/png;base64,") no-repeat !important;
	background-position: 5pt -40px !important;}

#feed-background {
	background: #333333 !important;}

.feed-item-container {
	border-color: #262626 !important;}

.feed-item-main a:not(:hover),
.html5-compatibility-table *,
.yt-alert-message {
	color: #EEEEEE !important}

#playlist-bar-bar{
	background-color: #262626 !important;
	border-color: black !important;}

#playlist-bar-bar-container, #playlist-bar-info .playlist-bar-group,
.live-comments-setting,
.feed-item-main h4 {
	border-color: #666666 !important;}

.watch-sparkbar-likes {
	border-color: #999999 !important;}

.watch-wide #watch-video-container,
.watch-branded #watch-container,
.ux-thumb {
	background: black !important;}

/*.com/videos*/ .browse-videos .browse-content {
	border-left: 1px solid rgba(51,51,51,.45)!important;
	background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAMMAAAABCAYAAABjXxoVAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAACuoAAArqAVDM774AAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAAElJREFUOE/t0k0KgCAURWHTskz7BUEI2v8uX2dwF9HAwceZPi7PmZnr+gY//YGBu7wEOmKSSGcskuiKLIVu2OWgJy65aUXDg/cDZfZIINfqXnUAAAAASUVORK5CYII=") repeat-y scroll left top transparent !important;}
.navigation-menu .menu-item {
	border-top-color: rgba(51,51,51,.45) !important;
	border-bottom-color: rgba(0,0,0,.45) !important; }
.navigation-menu .menu-item a {
	color: #CCCCCC !important;
	text-shadow: none !Important;}
.navigation-menu .menu-item a:hover {
	background: rgba(28,28,28,.8) !important;
	border-right: 4px solid grey !important;
	text-shadow: none !Important;}
.navigation-menu .menu-item a.selected {
	background: rgba(28,28,28,.8) !important;
	text-shadow: none !Important;}
/**/

body, 
#masthead-container,
.watch-branded #watch-sidebar,
.watch-branded #watch-main-container, 
.watch-branded #watch-main-sidebar,
#watch-description .expand, #watch-description .collapse{
	background: url("data:image/png;base64,") repeat !important;}

body, 
#masthead-container,
.watch-branded #watch-main-container, 
.watch-branded #watch-main-sidebar{
	color: white !important;}

#default-language-box a {
	color: rgb(0,51,102) !important;}

#masthead-search-terms,
.yt-uix-form-input-text, .yt-uix-form-input-textarea,
.comments-post-alert, .comments-textarea-container textarea,
#default-language-box {
	border-color: #333333 !important;
	background: #666666 !important;
	box-shadow: 0 1px 2px #333333 inset !important;}

.comments-textarea-container textarea{
	border-color: #B9B9B9 !important;}

#masthead-expanded-container, 
#language-picker, #region-picker, #safetymode-picker {
	border-color: #333333 !important;
	background: #CCCCCC !important;
	box-shadow: 0 1px 2px #333333 inset !important;}

#toolbelt-container{
	background: -moz-linear-gradient(rgba(255,255,255,.12), rgba(255,255,255,.01),transparent) !important;
	box-shadow: 0 5px 5px rgba(255,255,255,.08) inset !important;
	padding: 10px 8px 18px !important;}

#picker-container {
	background: transparent !important;}

#masthead-user-expander .yt-uix-expander-head, #masthead-gaia-user-wrapper, #masthead-gaia-photo-wrapper,
#watch-description .expand, #watch-description .collapse {
	border-color: black !important}

.yt-uix-button-text, .yt-uix-button-text[disabled]{
	text-shadow: none !important;
	border-color: #333333 !important;
	box-shadow: 0 1px 2px #333333 inset !important;
	outline: none !important;}

a.related-video:hover, a.video-list-item-link.yt-uix-sessionlink:hover,
.result-item.yt-uix-tile.yt-tile-default:hover,
.related-playlist:hover {
	background-image: -moz-linear-gradient(#333333, #222222) !important;
	border-color: #202020 #202020 #191919 !important;
	border-radius: 2.5px !important;
	box-shadow: none !important;
	outline: none !important;}

#masthead-user-button, 
.watch-sparkbars,
.comments-section h4,
#watch-description-toggle,
#watch-context,
#watch-context .context-head {
	border-color: #333333 !important;}

#watch-sidebar .video-list-item .title,
.yt-tiledefault.video-list-item a .title,
.comments-section h4, .comment-text {
	color: #666666 !important;}

.yt-uix-button-primary ,
a.title.yt-uix-sessionlink,a.yt-uix-redirect-link, a.yt-user-name {
	color: #EEEEEE !important;}

#watch-sidebar .video-list-item .title:hover, 
.yt-tiledefault.video-list-item a .title:hover, 
#video-sidebar .video-list-item-link:hover .title, #video-sidebar .recommended-videos-link:hover,
a.title.yt-uix-sessionlink:hover {
	color: rgb(51,102,204) !important;}

body, button, input, textarea,
#search-header h2,
#eow-description, 
.watch-branded #watch-main-container, .watch-branded #watch-main-sidebar,
#masthead-nav a, #masthead-user a, #masthead-nav a:hover, #masthead-user a:hover, #masthead-nav a:active, #masthead-user a:active,
.yt-uix-pager .yt-uix-button, .yt-uix-pager .yt-uix-link,
.yt-uix-button-default, a.yt-uix-button-default .yt-uix-button-content{
	color: #CCCCCC !important;}

.title, #video-sidebar .recommended-videos-link, 
#watch-sidebar .video-list-item .title{
	color: #999999 !important;}

#masthead-search-terms input,
#watch-headline h1, #eow-title-input,
.watch-view-count, span.video-count, 
#footer-links-primary a,#footer-links-secondary a,#footer .pickers a, #picker-loading {
	color: #EEEEEE !important;
	text-shadow: none !important;}

.yt-uix-button-default, .yt-uix-button-default:focus,
body .yt-uix-button-default[disabled],
a.playlist-visual {
	background-color: #454545 !important;
	background-image: -moz-linear-gradient(center top , #474747 0, #2B2B2B 100%) !important;
	border-color: #333333 #333333 #262626 !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.45) !important;
	box-shadow: 0 1px 0 rgba(255,255,255,.45) inset !important;
	outline: none !important;}

.yt-uix-button-default:hover, .yt-uix-button-text:hover,
a.playlist-visual:hover {
	background-image: -moz-linear-gradient(center top , #262626 0, #333333 100%) !important;}

.yt-uix-button-default:active, .yt-uix-button-text:active, .yt-uix-button-default.yt-uix-button-toggled, .yt-uix-button-text.yt-uix-button-toggled, 
a.playlist-visual:active{
	border-color: #333333 #333333 black !important;
	box-shadow: 0 1px 2px black inset !important;
	background-image: -moz-linear-gradient(center top , #333333 0, black 100%) !important;}

#watch-description-collapse .yt-uix-button:not(:hover),
#watch-description-expand .yt-uix-button:not(:hover) {
	background: -moz-linear-gradient(rgba(0,0,0,.45), rgba(0,0,0,.2)) !important;
	border-color: rgba(51,51,51,.45) !important;
	box-shadow: 0 1px 0 rgba(0,0,0,.45) inset !important;}

.disco-video-list .album-row,
.disco-video-list .album-row-odd {
	background: none !important;
	box-shadow: none !important;}
.disco-video-list .album-row:hover,
.disco-video-list .album-row-odd:hover {
	background: -moz-linear-gradient(rgba(0,0,0,.45), rgba(0,0,0,.2)) !important;
	border-color: rgba(51,51,51,.45) !important;}

* h1,
#artist-discography.ytg-fl *,
#artist-videos-header.artist-module-header h1,
#new-releases-header.artist-module-header h1{
	color: #BBBBBB !important;}

.context-link {
	color: #CCCCCC !important;
	text-shadow: 0 0 0 rgba(255,255,255,0), 0 1px 0 black !important;
	background-image: -moz-linear-gradient(center top , #333333 0, #202020 100%) !important;
	box-shadow: 0 1px 2px rgba(255,255,255,.25), 0 0 3px #333333 inset !important;}

.context-body {
	background: rgba(221,221,221,.12) !important;
	box-shadow: 0 1px 3px black inset !important;}

.yt-horizontal-rule {
	border-top-color: rgba(51,51,51,.45) !important;
	border-bottom-color: rgba(0,0,0,.45) !important;}

.yt-vertical-rule-corner-top-flip, .rtl .yt-vertical-rule-corner-top{
	background: -moz-radial-gradient(right bottom , circle , rgba(221,221,221,.08), transparent 30px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-vertical-rule-main-flip, .rtl .yt-vertical-rule-main {
	background: -moz-linear-gradient(right center , rgba(221,221,221,.12), rgba(221,221,221,.08) 1px, rgba(221,221,221,.08) 1px, transparent 30px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-vertical-rule-corner-bottom-flip, .rtl .yt-vertical-rule-corner-bottom {
	background: -moz-radial-gradient(right top , circle , rgba(221,221,221,.08), transparent 30px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-vertical-rule-corner-top, .rtl .yt-vertical-rule-corner-top-flip {
	background: -moz-radial-gradient(left bottom , circle , rgba(221,221,221,.08), transparent 30px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-vertical-rule-main, .rtl .yt-vertical-rule-main-flip {
	background: -moz-linear-gradient(left center , rgba(221,221,221,.12), rgba(221,221,221,.08) 1px, rgba(221,221,221,.08) 1px, transparent 30px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-vertical-rule-corner-bottom, .rtl .yt-vertical-rule-corner-bottom-flip {
	background: -moz-radial-gradient(left top , circle , rgba(221,221,221,.08), transparent 30px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-horizontal-rule .first {
	background: -moz-radial-gradient(right bottom , circle , rgba(221,221,221,.08), transparent 80px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-horizontal-rule .second {
	background: -moz-radial-gradient(left bottom , circle , rgba(221,221,221,.08), transparent 80px, transparent 100%) repeat scroll 0% 0% transparent !important;}
.yt-horizontal-rule .third {
	background: -moz-linear-gradient(center bottom , rgba(221,221,221,.12), rgba(221,221,221,.08) 1px, rgba(221,221,221,.08) 1px, transparent 80px, transparent 100%) repeat scroll 0% 0% transparent !important;}

#footer{
	padding-bottom: 1em !important;}

/*Iconos*/
.yt-uix-slider-prev:hover .yt-uix-slider-prev-arrow{
	border-right-color: #CCCCCC !important;}
.yt-uix-slider-next:hover .yt-uix-slider-next-arrow {
	border-left-color: #CCCCCC !important;}
#masthead-search .search-btn-compontent .yt-uix-button-content {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -163px 120px !important;}
.yt-uix-button .yt-uix-button-icon-watch-like {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -75px -102px !important;}
.yt-uix-button .yt-uix-button-icon-watch-unlike {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -112px -14px !important;}
.unliked .yt-uix-button-icon-watch-unlike,
.yt-uix-button:hover .yt-uix-button-icon-watch-unlike {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -18px -37px !important;}
.yt-uix-button .yt-uix-button-icon-watch-flag {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -96px -119px !important;}
.active .yt-uix-button-icon-watch-flag,
.yt-uix-button:hover .yt-uix-button-icon-watch-flag {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -75px -66px !important;}
.yt-uix-button-icon-addto {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") 0 -66px !important;}
.yt-uix-button .yt-uix-button-icon-watch-insight {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -154px -207px !important;}
img.comments-rating-thumbs-up {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -90px 0 !important;}
img.comments-rating-thumbs-down {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -132px -208px !important;}
.comment .yt-uix-button .yt-uix-button-icon-watch-comment-vote-up,
.comment .yt-uix-button:hover .yt-uix-button-icon-watch-comment-vote-up {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -61px -66px !important;}
.comment .yt-uix-button .yt-uix-button-icon-watch-comment-vote-down,
.comment .yt-uix-button:hover .yt-uix-button-icon-watch-comment-vote-down {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -33px -66px !important;}
.yt-uix-button:hover .yt-uix-button-icon-watch-insight {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -235px -199px !important;}
#footer-logo #footer-divider {
	background: url("http://s.ytimg.com/yt/img/earthhour/www-refresh-vflnJTVZv.png") -235px -66px !important;}


#watch-video-container #watch-video #watch-player {
	width: 864px !important;
	height: 516px !important;
	left: 0px !important }
#watch-sidebar {
	left: 70px !important }
#watch-video-container #watch-video.medium #watch-player {
	width: 1280px !important;
	height: 750px !important;
	left: 0px !important }
#watch-video{width:auto!important;}
body{overflow-x:hidden!important}

#shared-addto-menu .playlists ul {
        height: 294px;
    }
    #shared-addto-menu {
        height: 400px;
    }

#shared-addto-menu{height:392px !important;} /* Original height:184px */
#shared-addto-menu .playlists ul{height:286px !important;} /* Original height:78px, Must be 106px less than #shared-add-to-menu */
}
