css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.nicovideo.jp/watch/)
{
#textMarquee .textMarqueeInner {
font-size: 12px !important;
color: gray !important;
}
#videoHeader #videoHeaderDetail h2 {
font-size: 150% !important;
}
#videoHeader #videoHeaderDetail {
background-color: rgba(255, 255, 255, 0.1) !important;
border-radius: 7px;
padding: 0 10px !important;
}
.dic {
opacity: .8;
}
#textMarquee {
margin: 60px auto 0 !important;
}
#videoTagContainer {
height: 56px !important;
}
#videoHeader #videoTagContainer ul#videoHeaderTagList li {
padding: 0 !important;
}
#videoHeader #videoTagContainer.active {
background: none !important;
}
#videoTagContainerPin,
#videoInformationWrapper {
display: none !important;
}
.commentOuter {
bottom: -60px !important;
background: none !important;
display: block !important;
box-shadow: none !important;
border-radius: 0 !important;
opacity: 1 !important;
left: 31% !important;
}
#playlist {
margin: 50px auto 0 !important;
}
.commandInput {
border-right: 1px solid rgb(60,60,60);
}
#playlist #playlistContainer #playlistInner ol li a img.itemThumbnail {
width: 78px !important;
height: 48px !important;
}
#playlist #playlistContainer #playlistInner ol li .playlistActiveInfo .playlistInformation {
bottom: 80px !important;
}
#playlist #playlistContainer #playlistInner ol li {
border: none !important;
}
.commentInner {
background: rgba(255,255,255,.1);
border-radius: 0 !important;
height: 17px !important;
box-shadow: none !important;
padding: 0 0 17px 0 !important;
border: 1px solid rgb(80,80,80);
}
.commandInput input,
.commentInput input {
font-size: 14px !important;
line-height: normal !important;
color: #fff !important;
}
.handler {
display: none !important;
}
#ichibaPanel {
display: none !important;
}
#videoHeader #videoHeaderDetail .videoDetailExpand {
cursor: default !important;
padding: 10px 4px 10px 4px !important;
height: auto !important;
border-bottom: rgb(80,80,80) 1px solid !important;
}
#videoHeader #videoHeaderDetail .videoDetailExpand h3,
#videoHeader #videoHeaderDetail .videoDetailExpand:hover h3,
#videoHeader #videoHeaderDetail .videoDetailExpand.active h3{
	display: none !important;
}
#videoHeader #videoHeaderDetail .videoDetailExpand:hover h2, #videoHeader #videoHeaderDetail .videoDetailExpand.active h2 {
border-right: none !important;
}
#videoInformationWrapper {
/* display: none !important; */
}
#videoHeader .videoMenuContainer {
display: block !important;
zoom: 0.75;
}
#videoHeader #videoHeaderDetail .videoDetailExpand:hover, #videoHeader #videoHeaderDetail .videoDetailExpand.active {
background: transparent !important;
filter: none !important;
margin-bottom: 0px !important;
cursor: default !important;
}
#playerContainer[style^="width: 672px"] {
padding-right: 270px !important;
}
#videoHeader .videoMenuWrapper {
background: none !important;
height: 32px !important;
position: relative !important;
left 200px !important;
bottom: -301px !important;
}
#videoHeader #videoMenuTopList li.nicoruButton {
display: none;
}
#videoHeader #videoMenuTopList li {
float: none !important;
margin-bottom: 7px;
}
#videoHeader #videoMenuTopList {
background: none !important;
top: -400px !important;
left: 1110px !important;
box-shadow: none !important;
}
#videoHeader #videoMenuTopList li a,
#videoHeader #videoMenuBottomList li a {
text-align: left !important;
padding-top: 2px;
padding-left: 48px;
}
#videoHeaderMenu {
height: auto !important;
}
#nicoIchiba {
display: block !important;
}
#videoTagContainer .filter,
#tabMenu,
#videoHeader .videoMenuToggle {
display: none;
}
#playlist #playlistContainer #playlistInner ol li {
width: 78px !important;
height: 58px !important;
top: 0 !important;
left: 0 !important;
}
#playlist #playlistContainer #playlistInner ol li a {
-webkit-transition: -webkit-transform 100ms linear;
}
#playlist #playlistContainer #playlistInner ol li a:hover {
-webkit-transform: scale(1.3);
-moz-transform: scale(1.3);
-o-transform: scale(1.3);
-ms-transform: scale(1.3);
transform: scale(1.3);
}
#videoHeader #videoMenuBottomList {
background: none !important;
top: -235px !important;
left: 1110px !important;
width: auto !important;
box-shadow: none !important;
}
#videoDetailInformation {
display: block !important;
}

}
