/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("addons.mozilla.org") {

html { background: #000 !important; }
body, div, ul, li, table, tbody, th, tr, td, h1, h2, h3, p, form, dl, dt, dd, cite, code, header, footer, article, section, nav, aside, fieldset,
:before, :after, .control, /* collections */ .separated-listing blockquote { background: none !important; }
a, span, strong, em { background-color: transparent !important; }

option, blockquote, pre, /* popups */ .install-note, .popup, .modal, .hovercard:hover, .menu-nav > ul > li > ul, /* on complete themes*/ .extras > ul,
/* homepage */ .addons li > a, #monthly > div, #go-mobile > div,
/* search popup */ #site-search-suggestions .wrap,
#lightbox .content, #lightbox .controls { background: #181818 !important; }
.island, .notice, #tabzilla-panel, /* top-left */ .version .info, /* sidebar */ .secondary,
/* collections */ .object-lead, .table-listing .item, .blocks .item, .listing .item, .separated-listing .item { background: #222 !important; }
.hovercard, .screenshot, .site-balloon { background: #303030 !important; }
.error { background: #533 !important; }
/* progress-bars */ .bar { background: #555 !important; }
span.no-restart { background: #852 !important; }
span.featured { background: #060 !important; }

/*#tabzilla-wrapper*/ .amo-header { background-image: linear-gradient(rgba(255,255,255,.2), rgba(125,125,125,.2), rgba(0,0,0,.3)) !important; }

* { color: inherit !important; border-color: #555 !important; box-shadow: none !important; text-shadow: none !important; }
html, /* home */ .toplist small, p.desc { color: #ccc !important; }
hr { height: 0 !important; border: 0 solid #555 !important; border-width: 1px 0 0 !important; }
a { color: #4be !important; text-decoration: none !important; }
a:hover { color: #7df !important; text-decoration: underline !important; }
.banner.featured { color: #090 !important; }
/* .install-shell */ .notavail, .notsupported { color: #f55 !important; }
/* .hovercard */ .adu, .updated { color: #5f5 !important; }
/* homepage underlines */ .collection.hovercard a:hover, .addons li > a:hover { text-decoration: none !important; }

::-moz-selection { background: #184880 !important; }
::selection { background: #184880 !important; }

input[type="submit"], input[type="button"], input[type="file"], input[type="reset"], button,
.button { color: #ccc !important; border: 1px solid #555 !important; background: #111 !important;
border-radius: 7px !important; background-image: linear-gradient(rgba(255,255,255,.2),rgba(125,125,125,.2),rgba(0,0,0,.3)) !important; }

input[type="submit"]:hover, input[type="button"]:hover, input[type="file"]:hover, input[type="reset"]:hover, button:hover,
.button:hover { border-color: #158 !important; background-color: #158 !important; color: #fff !important; text-decoration: none !important; }

button, input, select, textarea { -moz-appearance: none !important; }
input[type="radio"], input[type="checkbox"] { color: #4be !important; background: #222 !important; border-color: #999 !important; }

input, select, textarea, input[type="text"] { background: #181818 !important; color: #ccc !important; padding: 2px !important; border: 1px solid #555 !important; }
input[type] { border-width: 1px !important; border-style: solid !important; }
textarea:hover, input:hover, select:hover, textarea:focus, input:focus, select:focus { border-color: #47b !important; }

select > button { background-color: transparent !important; border: none !important; }
option:hover, option:focus, option:active, option:checked { background: linear-gradient(#158,#158) !important; }
.search-button:before { content: ">" !important; }

button.good, .button.good, .button.add { background-color: #050 !important; border-color: #050 !important; }
button.good:hover, .button.good:hover, .button.add:hover { background-color: #070 !important; }
button[disabled], .button.disabled, .button.concealed, .button.caution.concealed { background-color: #333 !important; border-color: #555 !important; }
button[disabled]:hover, .button.disabled:hover, .button.concealed:hover, .button.caution.concealed:hover { background-color: #222 !important; border-color: #555 !important; }

/* screenshots */
.screenshot, .control { transition-property: none !important; }
img:not([id^="preview"]):not(:hover), #tabzilla { opacity: .7 !important; }
.control:focus { outline: none !important; }

/* sidebar */
.secondary { border-radius: 5px !important; border: 1px solid #555 !important; padding: .3em !important; }
aside.secondary { padding: .6em !important; }

/* search suggestions */
#site-search-suggestions a:hover, #site-search-suggestions a.sel, #site-search-suggestions.sel a.sel:hover { background: #047 !important; }

}
