@-moz-document url("http://linkedin.com"), url("https://linkedin.com"), url("http://www.linkedin.com"), url("https://www.linkedin.com"), url("http://www.linkedin.com/"), url("https://www.linkedin.com/"), url("http://www.linkedin.com/home?trk=hb_home"), url("https://www.linkedin.com/home?trk=hb_home")  {

/** Hide LinkedIn Rubbish **/
  .bar{position: fixed !important; margin-top: -40px !important;}
  A, #footer, .hp-nus-wrapper, .bar #nav-primary-home, .bar #nav-primary-what, .bar #nav-primary-join{display:none !important;}

/** Add logo **/
  .logo{position: fixed !important; width:440px !important; height:119px !important; top: 35% !important; left: 50% !important; margin-left: -220px !important; margin-top: -60px !important; z-index: -1 !important; background-image:url(data:image/jpeg;base64,) !important;}

/** Centralise login options **/
  .nav-signin-form{position: fixed !important; width: 388px !important; height: 21px !important; min-width: 0 !important; top: 50% !important; left: 50% !important; margin-top: -10px !important; margin-left: -194px !important; z-index: -1 !important;}
  .btn-secondary{position: fixed !important; width: 150px !important; height: 27px !important; min-width: 0 !important; top: 55% !important; left: 50% !important; margin-left: -75px !important; z-index: -1 !important;}

  HTML[lang="en"]{overflow:hidden !important} 
  
}
