css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://twitter.com/home") {
 a {
  text-decoration : none !important;
 }
 
 /*
 #friends,
 */
 #navigation li[style^="background"],
 div.section-header,
 div.badgebox,
 #send_via_form,
 #user_search_form,
 label.doing,
 .entry-meta img[src*="/reply.png"],
 .entry-meta img[src*="/icon_red_lock.gif"],
 #footer{
  display : none !important;
 }

 .entry-content {
  line-height : 1.6em !important;
 }

 #content {
  margin-top  : 0 !important;
  padding-top : 0 !important;
 }
 #content #doingForm #chars_left_notice {
  display  : inline !important;
  position : inherit !important;
 }
 .update-button{
  background-color : #DDD !important;
  border : none !important;
 }
 #content .tabMenu {
  text-align : right !important;
  padding : 4px 0 !important;
  margin-bottom : -2px !important;
 }
 #content .tabMenu li a {
  background   : #DDD !important;
  border-left  : 2px solid white !important;
  border-right : 2px solid white !important;
  border-top   : 0px solid white !important;
  border-bottom: 1px solid white !important;
  color : white !important;
  padding : 4px 1em !important;
 }
 #content .tabMenu li.active a {
  background : #EEE !important;
 }
 #content #doingForm .info {
  text-align : left !important;
 }
 #content #doingForm textarea {
  border : 2px solid #EEE !important;
  height : 2.5em !important;
  margin : 1em 0 !important;
 }
 #content .doing td {
  border-bottom : 1px solid #EEE !important;
  vertical-align : top !important;
  padding : 0.5em !important;
 }
 #content .doing .thumb {
  padding : 0.7em 0 !important;
 }

 .entry-meta{
  float : right;
 }
 .entry-meta,
 .entry-meta a{
  color : white !important;
 }
 .entry-meta a.entry-date{
  color : #EEE !important;
 }
 span.entry-meta img{
  -moz-opacity: 0.3 !important;
 }
 #content .tab {
  border-top : 1px solid #EEE !important;
 }
}
