css:
@-moz-document url-prefix("http://www.google"), url-prefix("https://www.google") {
/* 
    Ozyart Google Theme
 */

img#hplogo,
div#hplogo,
div#pmocntr2 {
    display: none;
}
div#lga {
    margin-top: 5em !important;
    width: 0 !important;
    height: 115px !important;
    padding-left: 280px !important;
    background-image: url("https://www.google.com/logos/classicplus.png") !important;
    background-repeat: no-repeat !important;
}
body {
    background-repeat: repeat !important;
    background-image: url("https://s31.postimg.org/qzpqfk57v/matrix_animated_image.gif") !important;
}
em,
cite {
    color: white !important;
    text-shadow: black 0px 0px 1px !important;
}
a,
spam,
p {
    color: #a8ffa8 !important;
    text-shadow: black 0px 0px 1px !important;
}
div[id="leftnav"],
div#appbar,
div#topabar,
div#gb {
    background: #000000 !important;
    opacity: 0.9 !important;
}
span[id="gbi4t"] {
    color: black !important;
    text-shadow: black 0px 0px 1px !important;
}
li,
div {
    color: #55aa55 !important;
    text-shadow: black 0px 0px 1px !important;
}
DIV#flci a {
    background: black;
    color: green !important;
    font-weight: bold;
    text-shadow: black 0px 0px 1px !important;
}
#fsr:after {
    content: " Google Matrix by Ozyart";
    margin-left: 30px !important;
}
.gbit {
    color: black !important;
    text-shadow: black 0px 0px 1px !important;
}
#hdtbSum {
    background-color: black !important;
    opacity: 0.9 !important;
}
.fbar {
    background: rgb(0, 0, 0)!important;
}
.gb_nb {
    background: rgba(21, 65, 13, 1)!important;
}
#hdtb {
    background: rgba(21, 65, 13, 0.5)!important;
}

div#lga {
    margin-top: 195px !important;
}

.sfbgg, .rl_item, #fbar {
    background-color: #000000!important;
}

.sbqs_c{
    color: green !important; 
    text-shadow: black 0px 0px 0px !important;
}


}
