css:
@namespace url(http://www.w3.org/1999/xhtml);

/* v 1.3
 *   
 * Author		: ceek (ceek@163.com)
 * Updated		: http://userstyles.org/styles/6516 ,8/15/09
 * Description		: A simple style redesign of the appearance and feel for tianya.cn bbs
 * Usage		: Use with the Stylish Firefox extension or import to usercss in your profile
 * 
 * 
 */


@-moz-document domain("tianya.cn") {
body,a,div,select,input,textarea,td,p,.lb12{font-family:Tohoma,Airal,sans-serif !important;}
u,u:link,u:visited{color:#C0C0C0 !important;text-decoration:none !important }
u:hover{padding-top:2px !important;background:#eeeeee !important; text-decoration:underline !important}
table[valign=top]{display:none !important}
}


@-moz-document url-prefix(http://www.tianya.cn/new) ,url-prefix(http://www.tianya.cn/New),url-prefix(http://www.tianya.cn/publicforum/articleslist), url-prefix(http://www.tianya.cn/techforum/articleslist), url-prefix(http://bbs.city.tianya.cn/new){
table{width:100% !important}
a:link,a:visited{text-decoration: none !important}
a:hover{text-decoration: underline !important}

/*a{line-height:22px !important;padding:2px !important;padding-left:0px !important;}*/
.tdfont{font-size:12px !important;color:#666666 !important;text-align:center !important}
td[width="40"] {text-align:center !important}

div#adContentDiv,div#marqueeBox,table[bgcolor="#ecf1f7"],table[height="80"],td[class="font12"],iframe,#tianyaSense1,#tianyaSense2,#tianyaSense3,#tianyaSense4,#randPanel,#responsebanner,.wdk_user_status_span,.myplace
{display:none !important}

.lb12{font-size:14px !important}
font[color="blue"][face="wingdings"]{color:#3366FF;display:none; !important}
font[color="#cc00cc"]{color:#3B5998 !important}
font[color="red"][size="2"]{color:#3B5998 !important;font-weight:normal !important}
font[color="red"]{color:#CC0000 !important}
td[bgcolor="#6699cc"] font[color="red"]{color:white !important}

#forumWrapper,
#topNavWrapper,#bottomNavWrapper,#bottomNav,#topNav,#topNavWrapper,#bottomNavWrapper,
#mainDiv,#forumContentDiv,#content
{width:100% !important;}

.body,.wrapper,#tech_body,#conArea{width:100% !important;margin: 0px !important;}

/*用户如果发现帖子列表页面不能占满屏幕，请调整这个值，此为1024分辨率*/
#forumContentDiv table,#content table{width:86% !important;margin:4px !important;}

}


@-moz-document url-prefix(http://www.tianya.cn/leftlist) {
a{padding-left:4px !important}
a:link,a:visited{ color:#FFFFFF !important  }
a:hover{ color:#FFFFCC !important ; text-decoration:underline !important ;background:none !important }
span,font,TD{color:#FFFFFF !important}
}


@-moz-document url(http://www.tianya.cn/Refreshnew.html), url(http://www.tianya.cn/Refresh_new.shtml), url-prefix(http://ref.bbs.tianya.cn/refreshnew.htm){
a:link,a:visited,a:hover,font{color:#FFC !important;font-size:12px !important  }
span,body{color:#ffc !important}
}


@-moz-document url-prefix(http://www.tianya.cn/publicforum/content),url-prefix(http://www.tianya.cn/techforum/content) {

/*宽屏显示器帖子宽度请调整数值*/
body{width:100%;margin:2px !important}.body{width:96% !important}

iframe,#tianyatopguide_nav,img#googleLogo,div[id="randPanel"],span[id="tianyaSense1"]{display:none !important}
.content embed{display:block !important}
td embed[width="680"]{display:none !important}
#pageDiv,#pageDiv1{height:22px !important;width:400px !important}
textarea{color:#3366FF !important;width:760px !important}
font[color="green"]{color:#6699cc !important}
center{padding-bottom:2px !important}
div#DisplayLoginInfoDiv font[color="red"]{color:#3399FF !important;padding-left:6px !important}

/*clear bottom post tip*/
.tianyatopguidenavlogo,.tianyatopguidenavl,.tianyatopguidenavr,.friend,.explain{display:none !important}
/*clear bottom copy annoucement*/
#tianyafootercopyArea{display:none !important}

}


@-moz-document url-prefix(http://search.tianya.cn) {
.t_list,iframe{display:none !important}
}


@-moz-document url-prefix(http://focus.tianya.cn) {
.banner,iframe,#FOCUS_ADRIGHT_1{display:none !important}
a:hover{background:#3399FF !important;color:#FFFFFF !important; text-decoration: none !important;padding-top:2px !important;padding-bottom:2px !important}
}
