/*shitlist*/
a[href*="dailymail.co.uk"].title:before, a[href*="thesun.co.uk"].title:before, a[href*="mirror.co.uk"].title:before, a[href*="dailystar.co.uk"].title:before, a[href*="foxnews.com"].title:before, a[href*="freerepublic.com"].title:before, a[href*="perezhilton.com"].title:before, a[href*="gawker.com"].title:before, a[href*="instagr.am"].title:before, a[href*="cnet.com"].title:before {background-position:-32px -128px;}

/* unassigned icons - for future use */
spam_icon {background-position:-32px -336px;}
cross_icon {background-position:-16px -352px;}
sad_icon {background-position:-32px -352px;}
happy_icon {background-position:-48px -352px;}
star_icon {background-position:-64px -352px;}
stop_icon {background-position:-80px -352px;}
