css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pogdesign.co.uk") {

    body {
    
        background-color : #2B2B2B !important ;
    }

    .month_name {
    
        background-color : #FFFFFF !important ;
        color : black !important ;
        border-width : 1px !important ;
        border-color : #2F9400 !important ;
        border-radius : 25px !important ;
        box-shadow: 5px 5px 5px black;
    }
    
    .prev-month a , .next-month a {
    
        color: black !important ;
    }
    
    .thisday {
    
        text-shadow: 5px 5px 5px black;
    }
    
    td.day {
    
        background-color: #FFFFFF !important ;
        color: black !important ;
        border-style : solid !important ;
        border-width : 1px !important ;
        border-color : #2F9400 !important ;
        border-radius : 10px !important ;
        box-shadow: 5px 5px 5px black;
    }
    
    td.today {
    
        background-color: #E4F2DF !important ;
        color: black !important ;
        border-style : solid !important ;
        border-width : 1px !important ;
        border-color : #2F9400 !important ;
        border-radius : 10px !important ;
        box-shadow: 5px 5px 5px black;
    }
    
    td.day strong {
    
        background-color: transparent !important ;
        color : black !important ;
        text-align : center !important ;
        font-size : 105% !important ;
    }
    
    td.today strong {
    
        background-color: transparent !important ;
        color : black !important ;
        text-align : center !important ;
        font-size : 105% !important ;
    }
    
    td.day strong a {
    
       display : none !important ;
    }
    
    td.today strong a {
    
        display : none !important ;
    }
    
    #month_box td.today p > *:first-child {
    
        color: black !important ;
    }

    td.day .t2 {
    
        background-color: #E8E8E8 !important ;
    }
    
    td.today .t2 {
    
        background-color: #CCE6C3 !important ;
    }

    td.day p, td.day a {
    
        color: black !important ;
    }
    
    td.today p, td.today a {
    
        color: black !important ;
    }
    
    td.day p.firstep, td.day p.firstep *  {
    
        color : black !important ;
        text-shadow : 0px 0px 5px yellow !important ;
    }
    
    d.day p.lastep, td.day p.lastep *  {
    
        color : black !important ;
        text-shadow : 0px 0px 5px #FF9999 !important ;
    }
}
