css:
@-moz-document url-prefix("http://2ch.hk/"), url-prefix("http://2ch.cm/"), url-prefix("http://2ch.yt/"), url-prefix("https://2ch.hk/"), url-prefix("https://2ch.cm/"), url-prefix("https://2ch.yt/") {
html, body {background: whiteSmoke!important}
a{text-decoration: none!important;}
.theader, .passvalid {
background: #EAEAEA!important;
text-align: center!important;
padding: 2px!important;
color: #444!important;
clear: both!important;
font-weight: bold!important;
margin-bottom: .5em!important;
border: none!important;
border-radius: 0px!important;
-moz-border-radius: 0px!important;
-webkit-border-radius: 0px!important;
}
.passcode-banner, .board-banner {
display: none!important;
}
.rmenu {font-size: 12px!important;}
.omittedposts {font-size: .9em!important}
.post-wrapper { margin: 4px 0; }
.cpost .reply, table td.reply, .post-wrapper .reply {
background: #EAEAEA!important;
padding: .5em 1em .5em 1em!important;
border: none!important;
border-radius:0px!important;
-moz-border-radius: 0px!important;
-webkit-border-radius: 0px!important;
}
.reply {
background: #EAEAEA!important;
padding: .5em 1em .5em 1em!important;
border: 1px solid #CCC!important;
border-radius:0px!important;
-moz-border-radius: 0px!important;
-webkit-border-radius: 0px!important;
}
input[type="file"] {border: 0 !important}
span.spoiler {background: #DDD!important;color: #DDD!important}
span.spoiler:hover {color: #333!important}
.logo {padding: 9px!important}
.thread .oppost { color:#333!important }
.thread .oppost input[type="checkbox"], .thread .reply input[type="checkbox"] {display: none!important}
input[type="text"], input[type="password"], textarea {margin: 1px 0px!important}
td, td img {vertical-align: inherit!important}
#postform td input#recaptcha_response_field {padding: 2px 4px 3px!important;width: 242px!important}
.reflink a {font-size: 12px!important;font-weight: bold!important}
input[type*="submit"] {
margin: 0 5px;
height: 24px;
    -webkit-box-shadow: inset 0 1px 0
rgba(255, 255, 255, 0.2), 0 1px 2px
rgba(0, 0, 0, 0.05);
    border-radius: 4px;
    border: 1px solid #CCC;
    background-color: whiteSmoke;
    background-image: -webkit-linear-gradient(top, white, #E6E6E6);
    cursor: pointer;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.75);
    -webkit-transition-property: background-image, background-color;
    -webkit-transition: 0.3s;
}

input[type*="submit"]:hover {background-image: none;background-color: #E6E6E6}
input[type*="submit"]:active {background-image: none;background-color: #E6E6E6}
.postername img, .postername span {display: none!important}
.postername div{display: inline-block!important;font-weight: bold!important}
a[href="/vg/"],a[href="/tv/"],a[href="/b/"],a[href="/me/"],a[href="/web/"] {
font-weight: bold!important; color: #06F!important
}
.post-wrapper:before { content: "" !important; }
}
