css:
@-moz-document domain("batoto.net") {
input, textarea, select {background-color: #656361;-moz-appearance: none !important;}
INPUT[style='border: 1px solid rgb(126, 157, 185); padding: 2px; background: url("http://www.google.com/cse/intl/en/images/google_custom_search_watermark.gif") no-repeat scroll left center rgb(255, 255, 255);'] {background-color: #656361 !important;}
html, body {color: #F7ECD6 !important;}
a {color: #F1DFD1 !important;}
a:hover {color: #DEC1A3 !important;}
.ipsType_pagetitle, .ipsType_subtitle {color: #F9F9F9 !important;}
.ipsType_pagedesc {color: #E9E9E9 !important;}
.ipsType_textblock {color: #EAEAEA !important;}
#content {background-color: #656361 !important;}
.row1, .post_block.row1 {background-color: #656361 !important;}
.row2, .post_block.row2 {background-color: #2D3D49 !important;}
.unread {background-color: #2C444C !important;}
.unread .altrow, .unread.altrow {background-color: #848B8B !important;}
.highlighted, .highlighted .altrow {background-color: #A0A39B !important;}
.ipsBox {background-color: #7F9696 !important;}
.ipsBox_notice, .ipsBox_highlight {background-color: #5E757D !important;}
.bar {background-color: #313D44 !important;}
.bar.altbar {background-color: #A0A39B !important;}
.header {background-color: #A0A39B !important;}
.post_block {background-color: #656361 !important;}
.post_body .post {color: #EAEAEA !important;}
.date, .poll_question .votes {color: #CACACA !important;}
.tab_bar {background-color: #223446 !important;}
.ipbmenu_content, .ipb_autocomplete {background-color: #2D3E4F !important;}
body#ipboard_body fieldset.submit, body#ipboard_body p.submit {background-color: #313D44 !important;}
body#ipboard_body.redirector {background-color: #656361 !important;}
#user_link_menucontent #links li {text-shadow: 0px 1px 0pt rgb(0,0,0) !important;}
#user_link.menu_active {background-color: #656361 !important;color: #F9F9F9 !important;}
#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {background-color: #656361 !important;}
#user_navigation #register_link {text-shadow: 0px 1px 2px rgba(0,0,0,0.3) !important;}
#primary_nav a {text-shadow: 0px 1px 1px rgba(0,0,0,0.5) !important;}
#primary_nav a:hover, #primary_nav a.menu_active {text-shadow: 0pt 0pt 3px rgb(0,0,0) !important;}
#primary_nav .active a {text-shadow: 0pt 0pt 3px rgb(0,0,0) !important;}
#more_apps_menucontent a {text-shadow: 0px 1px 1px rgba(0,0,0,0.5) !important;}
.breadcrumb {color: #DDDDDD !important;}
.breadcrumb a {color: #DDDDDD !important;}
.ipsHeaderMenu {background-image: -moz-linear-gradient(center top , rgb(84,84,84) 0%, rgb(81,81,81) 70%, rgb(78,78,78) 100%) !important;background-repeat: repeat !important;}
#footer_utilities a.menu_active {background-color: #2D3E4F !important;}
#copyright {color: #D9D9D9 !important;text-shadow: 0px 1px 0px rgb(0,0,0) !important;}
#copyright a {color: #D9D9D9 !important;}
#ipsDebug_footer {text-shadow: 0px 1px 0px rgb(0,0,0) !important;}
.ipsUserPhoto {background-color: #656361 !important;}
.general_box {background-color: #2E2E2E !important;}
.general_box h3 {background-color: #A0A39B !important;}
.ipsBox_container {background-color: #656361 !important;}
#ajax_loading {background-image: -moz-linear-gradient(center top , rgb(149,199,21) 0%, rgb(123,166,13) 100%) !important;background-repeat: repeat !important;}
.progress_bar {background-color: #656361 !important;}
.maintitle a {text-shadow: 0pt 1px 1px rgb(0,0,0) !important;}
.desc, .desc.blend_links a, p.posted_info {color: #DDDDDD !important;}
.forum_data {color: #D7DAD3 !important;}
.ipsForm_submit {background-image: -moz-linear-gradient(center top , rgb(148,148,148) 0%, rgb(133,133,133) 100%) !important;background-repeat: repeat !important;}
.ipsSteps ul li {color: #F9F9F9 !important;}
.ipsSteps .ipsSteps_active {text-shadow: 0px -1px 0pt rgba(0,0,0,0.7) !important;}
.ipsVerticalTabbed_tabs li {background-color: #6C7C87 !important;}
.ipsVerticalTabbed_tabs li.active a {background-color: #656361 !important;}
.ipsLikeBar_info {background-color: #7A3E3B !important;}
.ipsLikeButton.ipsLikeButton_enabled {background-image: url("data:image/png;base64,") !important;background-repeat: no-repeat !important;}
.ipsLikeButton.ipsLikeButton_disabled {background-image: url("data:image/png;base64,") !important;background-repeat: no-repeat !important;}
.ipsTag {background-image: url("data:image/png;base64,") !important;background-repeat: repeat !important;text-shadow: 0pt 1px 0pt rgb(0,0,0) !important;}
.ipsTagBox_hiddeninput {background-color: #656361 !important;}
.ipsTagBox_tag {background-color: #7A3E3B !important;}
.ipsTagBox_tag.selected {background-color: #464646 !important;}
.ipsTagBox_dropdown {background-color: #656361 !important;}
.ipsFilterbar li a {text-shadow: 0px 1px 0px rgb(13,39,62) !important;}
.ipsFloatingAction {background-color: #656361 !important;}
.input_text, .ipsTagBox_wrapper {border-top-color: #D9D9D9 !important;background-color: #656361 !important;}
.message h3 {color: #F9F9F9 !important;}
.message.unspecific {background-color: #7C7C7C !important;color: #DCDCDC !important;}
.popupInner {background-color: #656361 !important;}
.popupInner h3 {text-shadow: 0pt 1px 2px rgba(0,0,0,0.3) !important;}
.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {text-shadow: 0pt -1px 0pt rgb(25,25,25) !important;}
.topic_buttons li.non_button a {color: #DDDDDD !important;}
.topic_buttons li.disabled a, .topic_buttons li.disabled span {color: #E9E9E9 !important;}
.ipsButton_secondary {background-image: -moz-linear-gradient(center top , rgb(160,160,160) 0%, rgb(149,149,149) 100%) !important;background-repeat: repeat !important;}
.ipsButton_secondary.important {background-image: -moz-linear-gradient(center top , rgb(159,42,0) 0%, rgb(129,34,0) 100%) !important;background-repeat: repeat !important;}
.ipsButton_extra {color: #D7DAD3 !important;}
ul.post_controls li.multiquote.selected a {background-image: -moz-linear-gradient(center top , rgb(161,220,0) 0%, rgb(123,166,13) 100%) !important;background-repeat: repeat !important;}
.pagination .back a, .pagination .forward a {background-color: #3F403A !important;color: #F7ECD6 !important;}
ul.mini_pagination li a {background-color: #656361 !important;}
.author_info .group_title {color: #F7ECD6 !important;}
.user_controls li a {background-image: -moz-linear-gradient(center top , rgb(160,160,160) 0%, rgb(149,149,149) 100%) !important;background-repeat: repeat !important;}
#board_stats .value {background-color: #464646 !important;color: #FFFFFF !important;}
.post_block h3 {background-color: #A0A39B !important;}
.post_ignore {color: #DDDDDD !important;}
.poll_question h4 {background-color: #A0A39B !important;}
#attachments li {background-color: #223446 !important;}
#attach_wrap {background-color: #202E3D !important;}
.status_update {background-color: #525252 !important;}
#mymedia_toolbar {background-image: -moz-linear-gradient(center top , rgb(219,228,239) 0%, rgb(199,212,228) 100%) !important;background-repeat: repeat !important;}
.media_results li:hover {background-image: -moz-linear-gradient(center top , rgb(249,249,249) 0%, rgb(237,237,237) 100%) !important;background-repeat: repeat !important;}
.media_results li:active {background-image: -moz-linear-gradient(center top , rgb(237,237,237) 0%, rgb(249,249,249) 100%) !important;background-repeat: repeat !important;}
a#logo:hover {text-shadow: 0pt 0pt 4px rgb(0,0,0) !important;}
.genre_buttons {background-color: #656361 !important;}
.calendar_date_select {border-top-color: #DDDDDD !important;border-right-color: #DDDDDD !important;border-bottom-color: #DDDDDD !important;border-left-color: #DDDDDD !important;}
.calendar_date_select thead th {border-top-color: #DDDDDD !important;border-bottom-color: #DDDDDD !important;}
.calendar_date_select td {background-color: #656361 !important;}
#outerImageContainer {background-color: #656361 !important;}
#imageDataContainer {background-color: #656361 !important;}
hr.bbc {border-top-color: #DDDDDD !important;}
div.bbc_spoiler_wrapper {border-top-color: #DDDDDD !important;border-right-color: #DDDDDD !important;border-bottom-color: #DDDDDD !important;border-left-color: #DDDDDD !important;}
pre.prettyprint {background-color: #B3B3B3 !important;}
p.citation {background-image: -moz-linear-gradient(center top , rgb(160,160,160) 0%, rgb(149,149,149) 100%) !important;background-repeat: repeat !important;}
div.blockquote {background-color: #4F4F4F !important;}
._sharedMediaBbcode {background-image: -moz-linear-gradient(center top , rgb(160,160,160) 0%, rgb(149,149,149) 100%) !important;background-repeat: repeat !important;}
#ipboard_body {background-color: #0E202E !important;}
html, body {background-color: #0E202E !important;}
#primary_nav .active a {background: #09151F !important;}
.ipsSideBlock h3 {color: #F1DFD1 !important; background-color: #0E202E !important;}
.ipsSideBlock {background-color: #656361 !important;}
#search_options {background-color: #1E415A !important;}
.poll_question ol {background-color: #2E2E2E !important;}
.input_submit.alt {color: #F1DFD1 !important; background: #2E2E2E !important;}
.topic_buttons li.disabled span {background: transparent !important;}
DIV[style='margin-top: 5px; margin-bottom: 5px; width:728px; margin-left: auto; margin-right:auto; text-align:center; height: 90px; overflow:hidden;']
{height: 1px !important;}

.ipsHeaderMenu {background: -webkit-gradient(linear, right bottom, left top, color-stop(100%,#2D2B29), color-stop(0%,transparent))}
.ipsButton_secondary {background: -webkit-gradient(linear, right bottom, right bottom, color-stop(100%,#A4A2A0), color-stop(100%,transparent))}
p.citation {background: -webkit-gradient(linear, right bottom, right bottom, color-stop(100%,#A4A2A0), color-stop(100%,transparent))}
}

@-moz-document url("http://www.batoto.net/myfollows") {
.ipb_table {background-color: #646468 !important;}
}

@-moz-document url("http://www.batoto.net/chat") {
html body#ipboard_body div#ipbwrapper div#content.clearfix div {background-color: #2D3D49 !important;}
html body#ipboard_body div#ipbwrapper div#content.clearfix div#secondary_navigation.clearfix {background-color: #656361 !important;}
html body#ipboard_body div#ipbwrapper div#content.clearfix div#footer_utilities.main_width div.clearfix {background-color: #656361 !important;}
html body#ipboard_body div#ipbwrapper div#content.clearfix div#footer_utilities.main_width {background-color: #656361 !important;}
}

