css:
@-moz-document domain("facebook.com") {
/* This CSS is for update notifications
.ogAggregationHeaderTitle:after{font-size:11px; position: relative; bottom: 0px; content:" Â· Ver 3.0";} */

/* Restyle Like/Share text */

.ogAggregationListContainer {margin-top: 15px !important;}

.ogAggregationPanelUFI {
  position: relative;
  bottom: 23px;
  padding: 0px 0px 3px 10px;
  margin-top: 0px;
  margin-bottom: 0px;
  border-top: 1px solid #E9E9E9;
  border-left: 1px solid #E9E9E9;
  border-right: none !important;
  border-bottom: 1px solid #E9E9E9;
  background: #F7F7F7;
  width: 79px;
  height: 19px;
  overflow: hidden;
  opacity: 0.3;
  transition: opacity 0.5s ease-in;
    -webkit-transition: opacity 0.5s ease-in;
    -moz-transition: opacity 0.5s ease-in;
    -o-transition: opacity 0.5s ease-in;
 }

.ogAggregationPanelUFI:hover {
  opacity: 1;
 }

/* Put UI on a diet */

.ogAggregationPanel {margin-bottom: -23px !important;}

/* Ensure "See * more" is clickable */

.ogAggregationPeekCollapsed {z-index: 20 !important; position: relative; bottom: -2px;}
}
