css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.last.fm/home/recs") {

/* Common elements */
    body {background: #222 !important;}

    #content {
      border: 0px !important;
      background: #222 !important;
      color: #BBB !important;
    }

    /* Headers colors */
        .badgeHead h1, 
        .summary, 
        .h2Wrapper, 
        .lfmBlack #page .h2Wrapper a,
        .lfmBlack #page h3,
        .lfmBlack #page h3 a
        .catalogueHead h1, 
        h1, 
        h2, 
        h3,
        a, 
        .lfmMailButton span, 
        .lfmShoutboxButton span,
        ul.artistsSquare strong 
            {
            color: #21b0f3 !important; /* Blue text color */
            }

    /* Header */
    #headerWrapper {background-color: #222 !important; }
    #headerPromo {display: none !important;}
    #headerLinks {color: #333 !important;}
    div.fixed #content {border: 1px solid #111 !important;}

    /* Pagination */
        div.pagination .selected,
        div.pagination a
            {border: 1px solid #333 !important;}
        
        div.pagination a.nextlink, 
        div.pagination a.nextlink:hover,
        div.pagination a.prevlink, 
        div.pagination a.prevlink:hover
            {background: none !important;}

/* Page */
.fullWidth div.pagehead, .leftCol div.pagehead {border: 1px solid #111 !important;}

#recs h1.heading {background: #222 !important;}
div.tertiaryNavigation ul li a{background: #222 !important; margin: 0px !important;}
div.tertiaryNavigation {border-bottom: 0px !important; }

.filterTags{border: 1px solid #111 !important;}

.filterTags li a span,
.filterTags li a,
.filterTags li a.selected span,
.filterTags li a.all span,
#recs #artistRecs div.similarArtists {
    background: none !important;
    }

    /* Artists pictures (no more white overlay) */
            .pictureFrame .overlay {background:none!important;}
            
        /* Similar Artists */
            ul.artistsSquare a img {
                border: 1px solid #444 !important;
                background: none !important;
                }
/*
img.icon {background-image: none !important;}
*/

/* Dismiss-button */
.recommended .dismissRec button {visibility: visible !important;}


#recs #artistRecs li.recArtist {
    border-bottom: 1px solid #111 !important;
    }

.undo {background: #222 !important;}

#inLibraryIndicatorForArtist {color: rgb(187, 0, 0) !important;}
    
}
