css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://localhost:9091/transmission/web/") {


/* :: :: Backgrounds :: :: */
.torrent_global_menu,.torrent_global_details,.torrent_footer,#torrent_filter_bar,#inspector_tabs {
    background: -moz-linear-gradient(to bottom , #333, #111) !important;
    border-color: black !important;
}

#torrent_inspector,#torrent_container {
    background: #222 !important;
    border-color: black !important;
    color:#aaa !important;
}

#torrent_inspector {
    background:
        -moz-linear-gradient(left, #444,#222 5%,#222 95%,#000 100%)
    !important;
}

/* menu text colors */
.torrent_global_menu ul li > div {
    color:#aaa !important;
    text-shadow: 0 0 3px #aaa !important;
}

.torrent_global_menu ul li.disabled > div {
    color: black !important;
    text-shadow: 1px 1px 1px #aaa !important;
}

/* detailsbar text color*/
.torrent_global_details > div {
    color:#aaa !important;
    text-shadow: 0 0 3px #999 !important;
}


/* filterbar link colors */
#torrent_filter_bar a {
    color: black !important;
    text-shadow: 0 0 2px #aaa !important;
}

div#torrent_filter_bar ul li.selected,
div#torrent_filter_bar ul li.selected a,
div#torrent_filter_bar ul li:active,
div#torrent_filter_bar ul li:active a {
    background: transparent !important;
    color: #009 !important;
    text-shadow: 0 0 2px #0ff !important;
}

div#torrent_filter_bar ul li:hover,
div#torrent_filter_bar ul li:hover a {
    background: transparent !important;
    color: black !important;
    text-shadow: 0 0 2px #0ff !important;
}

/* filter textfield */
div#torrent_filter_bar input#torrent_search {
    border-color: black !important;
    background: -moz-linear-gradient(to bottom, #111,#444) !important;
    border-radius: 10px !important;
    width:140px !important;
    padding: 1px 5px !important;
}

.torrent {
    background: -moz-linear-gradient(to bottom, #111,#333) !important;
    border-color: black !important;
}

.torrent.selected {
    background: -moz-linear-gradient(to bottom, #012,#036) !important;
}

.torrent > *{
    color: #aaa !important;
}

.torrent.selected>* {
    color: #6cf !important;
}

.torrent_progress_bar {
    background: -moz-linear-gradient(top, #000, #222, #555 ) !important;
    border: none !important;
    height: 7px !important;
    margin: 3px 0 0 0 !important;
}

.torrent_progress_bar.complete {
    background: -moz-linear-gradient(top, #000, #020,  #090 ) !important;
}

.torrent_progress_bar.in_progress {
    background: -moz-linear-gradient(top, #069, #003 ) !important;
}

/* inspector area */
#inspector_tabs {
    position: absolute !important;
    left:0; right:0;
    width: 100% !important;
    height: 25px;

    margin:0px !important;
    padding:0px !important;
    text-align: center;
}

.inspector_tab {
    border: none !important;
    background: none !important;
    float: left !important;
    margin-left: 25px !important;
}

#inspector_header {
    margin-top: 26px !important;
}

.inspector_label {
    color: #aaa !important;
}

#torrent_inspector_size,
div.inspector_row>div {
    color: #0af !important;
    text-shadow: 0 0 5px #00f;
}

div.inspector_row>div.inspector_label {
    color: #aaa !important;
    text-shadow: none !important;
}

.inspector_tracker_entry {
    background: -moz-linear-gradient(top,#444,#111) !important;
    color: #aaa !important;
}
.tracker_host {
    color: #0af !important;
    text-shadow: 0 0 3px #00f !important;
}

/* footer buttons */
div.torrent_footer ul#settings_menu li#button,
div.torrent_footer div#turtle_button
 {
    border-radius:4px;
    height: 18px !important;
    margin-top:2px !important;
}

div.torrent_footer ul#settings_menu li#button {
    background-position: left -77px !important;
}

div.torrent_footer ul#settings_menu li#button.active {
    background-position: right -77px !important;
}

.turtleDisabled {
    background-position: left -99px !important;
}
.turtleDisabled:active {
    background-position: right -99px !important;
}

.turtleEnabled {
    background-position: left -121px !important;
}
.turtleEnabled:active {
    background-position: right -121px !important;
}

}
