css:
/* AGENT_SHEET */
/*
 * Dark CyberNations
 *
 * Author      : Orioni
 * Updated     : April 19th, 2008
 * Website     : http://www.cybernations.net/
 * Description : Modifies the colors, logos, and font
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 * Thanks to   : Tamurin for helping find/fix bugs
 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.cybernations.net") {

body{
	background:#000 url(http://aardling.com/wp-content/uploads/TFD/cn-logo.gif) no-repeat center top !important;
	padding-top:75px !important
	}
table,
tbody,
td,
tr{
	background:#000 !important;
	color:#0f0 !important;
	font-family:"Lucida Console","Courier New",Courier,Arial,Helvetica !important
	}
table,
#table17,
#table28{
	border:none !important
	}
b{
	font-weight:normal !important
	}
i{
	font-style:normal !important
	}
::selection{
	background:#0f0 !important;
	color:#000 !important
	}
::-moz-selection{
	background:#0f0 !important;
	color:#000 !important
	}



/* Sidebar */

#table17,
#table28{
	background:#000 !important
	}
#table17 img,
#table28 img,
#table33 img,
#table50 img{
	display:none !important
	}
p font b{
	color:#0f0 !important;
	text-transform:uppercase !important
	}



/* Tabs */

.shadetabs li a{
	background-color:#000 !important;
	background-image:none !important
	}
.shadetabs li a:hover{
	background-color:#00f !important;
	color:#fff !important
	}



/* Main */

td img{
	background:#fff !important
	}
tr td b font{
	color:#0f0 !important;
	text-transform:uppercase !important
	}
input,
option,
select,
textarea{
	background:#000 !important;
	color:#0f0 !important;
	font-family:"Lucida Console","Courier New",Courier,Arial,Helvetica !important
	}
option a:hover{
	background:#0f0 !important;
	color:#000 !important
	}
textarea{
	font-size:11px !important
	}
.buttons{
	color:#00f !important;
	font-weight:bold !important;
	letter-spacing:1px !important;
	padding:5px 10px !important
	}
.buttons:hover{
	background:#00f !important;
	color:#fff !important
	}



/* Links */

a{
	color:#00f !important;
	cursor:pointer !important;
	outline:none !important;
	text-decoration:none !important
	}



/* Remove ads */

b img,
iframe,
#table25,
#table51,
#table54{
	display:none !important;
	visibility:hidden !important;
	width:0 !important
	}



/* End */
}
