css:
@-moz-document domain(twitter.com) {

/* font */
body,body.ms-windows {font-family:sans-serif}

/* hide useless boxes */
.wtf-module,.wtf-module.has-content,.trends,.Footer,.ProfilePreviewBanner {display:none!important}

/* grid */
.wrapper {background:#fff; padding:56px 15px 15px}
.wrapper,.dashboard {width:590px!important}
.dashboard,.content-main,.profile-card.profile-header {float:none!important; margin:0 auto!important}

/* profile */
.profile-card.profile-header {margin-bottom:10px}
.DashboardProfileCard-tweetbox {width:564px}
.DashboardProfileCard-avatarLink,.DashboardProfileCard-userFields,.DashboardProfileCard-bg {display:none!important}

/* nav */
.global-nav {background:#fff}
.global-nav-inner {width:590px; margin:0 auto}
.nav>li>a .text,.bird-topbar-etched,.topbar-tweet-btn,.translate-button {display:none!important}
.global-nav .form-search {width:auto}
.global-nav .search-input {width:150px}
.global-nav .settings {margin-right:0!important; border-radius:0}
.global-nav .settings:before {display:none!important}

/* removing border radius */
.module .flex-module:first-child,.tweet-user,.with-media-forward .cards-base .media-forward,.profile-banner-footer,.module,.permalink,
.conversation-module .conversation-tweet-item .stream-item-header .avatar {border-radius:0!important}
.permalink {border:0}

/* tweet textarea */
.home-tweet-box {background:#fff!important}
.top-timeline-tweetbox .timeline-tweet-box {border-color:#e1e8ed!important}
.timeline-tweet-box {border-width:0 1px 1px!important; margin-top:-20px; margin-bottom:20px}
.top-timeline-tweet-box-user-image {display:none}
.top-timeline-tweetbox .timeline-tweet-box .t1-form {margin-left:0}

/* hide timelime title */
.header-inner {display:none}

/* tweet */
.tweet {padding-left:0; padding-bottom:5px}
.stream-item {border-color:#e1e8ed #fff; border-width:1px 0!important; margin:0!important; border-radius:0!important}
.stream-item.open {border-color:#fff; border-radius:0}
.tweet .tweet-geo-text {display:none}
 
/* name */
.stream-item-header .account-group {position:relative}
.avatar {border-radius:0}
.stream-item-header .fullname {color:#8899a6; font-weight:normal; font-size:12px; position:absolute; top:0; left:100%; margin-left:5px; white-space:pre}
.stream-item-header .username s {display:none}
.stream-item-header .username b {color:#292f33; font-weight:bold; font-size:14px}
.tweet .time {position:absolute; top:5px; right:12px}
.tweet .time:before {display:none}
.tweet .time a {color:#fff}
.tweet:hover .time a {color:#8899a6}

/* tweet footer */
.stream-item-footer *,.bottom-tweet-actions * {opacity:0}
.tweet:hover .stream-item-footer *,.tweet:hover .bottom-tweet-actions * {opacity:1}

/* green retweet icon */
.Icon--retweeted {display:none}
.tweet > .context .with-icn,.tweet > .context .with-icn * {margin-left:0; color:#8899a6!important}

/* image*/
.cards-media-container,.tweet-text + .expanded-content {height:50px!important; overflow:hidden!important}
.open .cards-media-container,.open .expanded-content {overflow:visible!important; height:auto!important}

/* profile */
.GridTimeline {font-family:'Helvetica Neue',Arial,sans-serif!important}
.ProfileTweet-text {font-family:'Helvetica Neue',Arial,sans-serif!important; font-size:14px!important; line-height:18px!important}


}
