css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ==== PANNEAU LATÉRAL & BIBLIOTHÈQUES - OK FIREFOX 69  : DÉBUT ==== */
	/* Barre de titre */
#sidebar-header
{
    -moz-appearance: none !important;
    height: 45px !important;
    margin-bottom: center !important;
    background: LightSkyBlue !important;
    font-weight: bold !important;
    font-family: comic sans ms !important;
    font-size: 22px !important;
    text-align: center !important;
}
#sidebar-title
{
    color: Navy !important;
}
	/* Menu - texte */
toolbarbutton
{
    color:Blue !important;
}
toolbarbutton[checked="true"]:not([disabled="true"])
{
    color: Red !important;
}
toolbarbutton[checked="true"]:not([disabled="true"]):hover
{
    color: Gold !important;
}
	/* Zone de recherche */
#sidebar-search-container
{
    -moz-appearance: none !important;
    margin-bottom: center !important;
    height: 50px !important;
    background-color: LightSkyBlue !important;
}
#search-box
{
    -moz-appearance: none !important;
    height: 50px !important;
    font-size: 18px !important;
    font-weight: bold !important;
    color: Blue !important;
    background-color: PaleTurquoise !important;
}
#search-box[focused="true"]
{
    background-color: Gold !important;
    color: Red !important;
    font-size: 22px !important;
}
	/* Hauteur des cellules */
treechildren::-moz-tree-row
{
    -moz-appearance: none !important;
    height: 36px !important;
    padding-bottom: center !important;
}
	/* Décallage à droite des icônes des dossiers Marque-pages */
#bookmarksPanel treechildren::-moz-tree-image(container)
{
    margin-left: 16px !important;
}
	/* Décallage à droite des Marque-pages */
#bookmarksPanel treechildren::-moz-tree-image(leaf)
{
    margin-left: 16px !important;
}
	/* Décallage à droite du texte des dossiers Marque-pages et des Marque-pages */
#bookmarksPanel treechildren::-moz-tree-cell-text
{
    margin-left: 5px !important;
}
	/* Décalage des branches secondaires de l'arbre */
treechildren:-moz-tree-indentation
{
    width:16px !important;
} 
	/* Couleurs des cellules */
treechildren::-moz-tree-row(even)
{
    background: -moz-linear-gradient(rgb(230,250,250) 25%, rgb(135,206,250) 85%) !important;
}
treechildren::-moz-tree-row(odd)
{
    background: -moz-linear-gradient(rgb(248,248,255) 25%, rgb(211,211,211) 85%) !important;
}
	/* Couleurs des cellules survolés */
treechildren::-moz-tree-cell(even, hover)
{
    background: -moz-linear-gradient(rgb(135,206,250) 25%, rgb(230,250,250) 85%) !important;
}
treechildren::-moz-tree-cell(odd, hover)
{
    background: -moz-linear-gradient(rgb(211,211,211) 25%, rgb(248,248,255) 85%) !important;
}
	/* Couleurs des cellules sélectionnées */
treechildren::-moz-tree-cell(even, selected)
{
    background: -moz-linear-gradient(rgb(255,215,000) 25%, rgb(255,165,000) 85%) !important;
}
treechildren::-moz-tree-cell(odd, selected)
{
    background: -moz-linear-gradient(rgb(255,215,000) 25%, rgb(255,165,000) 85%) !important;
}
	/* Texte des cellules "Dossiers" */
.sidebar-placesTreechildren::-moz-tree-cell-text(title, container)
{
    color: Blue !important;
    font-size: 22px !important;
    font-weight: bold !important;
}
	/* Texte des cellules"Dossiers" survolés */
.sidebar-placesTreechildren::-moz-tree-cell-text(title, container, hover)
{
    color: Red !important;
}
	/* Texte des cellules "Dossiers" sélectionnés */
.sidebar-placesTreechildren::-moz-tree-cell-text(title, container, selected)
{
    border: 	DarkViolet 2px solid !important; 
    color:  	DarkViolet !important;
}
	/* Texte des cellules "Marque-pages" */
#bookmarks-view-children::-moz-tree-cell-text,
treechildren::-moz-tree-cell-text
{
    font-size: 18px !important;
    font-style: normal !important;
    font-weight: bold !important;
    color: Black !important;
}
	/* Texte des cellules "Marque-pages" survolées */
#bookmarks-view-children::-moz-tree-cell-text(hover),
treechildren::-moz-tree-cell-text(hover)
{
    color: Red !important;
}
	/* Texte des cellules "Marque-pages" sélectionnées */
#bookmarks-view-children::-moz-tree-cell-text(selected),
treechildren::-moz-tree-cell-text(selected)
{
    border: DarkViolet 2px solid !important; 
    padding-left: 8px !important;
    padding-right: 8px !important;
    color: DarkViolet !important;
}
	/* Couleur du dossiers des "Marque-pages" */
.bookmark-item[container], treechildren::-moz-tree-image(container)
{
    fill: Blue !important;
}
/* ==== PANNEAU LATÉRAL & BIBLIOTHÈQUES - OK FIREFOX 69 : FIN ==== */
