css:
@-moz-document url-prefix("http://www.google"), url-prefix("https://www.google") {
/* 
    Ozyart Google Theme
 */

img#hplogo,
div#hplogo,
div#pmocntr2 {
    display: none;
}
body {
    background: url(https://s3.amazonaws.com/igd-wp-uploads/2014/03/googlebot.jpg) no-repeat center center fixed !important;
    /*background-size: cover !important; */
}
div[id="leftnav"],
div#appbar,
div#topabar,
div#gb {
    background: white !important;
    opacity: 0.8 !important;
}
div#gbx3 {
    background: lightgrey !important;
}
em,
cite {
    color: navy !important;
}
a,
spam,
p {
    color: blue !important;
}
span[id="gbi4t"] {
    color: black !important;
}
li,
div {
    color: navy !important;
}
DIV#flci a {
    color: blue !important;
    font-weight: bold;
}
DIV#flrs a:after {
    content: " Matrix by Ozyart";
}
.mw {
    max-width: none!important;
    background: rgba(255, 255, 255, 0.7)!important;
}

.hp .tsf-p {
    padding-left: 140px!important;
    padding-top: 327px!important;
}
}
