#navSearch     { border-color:#e1e1e1 !important; }

#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#FF8585 !important; color:#ffffff !important; }

#pageNav a{ color: #dedede !important;
}

.lightblue_box  { background-color:#FCE2E3; border-color:#F6B2B4 !important; }
