.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#FCE2E3 !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#FCE2E3 !important; }

.unread  { background-color:#F6B2B4 !important; }
.line  { color:#DE2B26 !important; }



.uiHeader h2  { color:#DE2B26 !important; }




.current_stuff td  { background-color:#FFF3F4 !important; border-color:#F6B2B4 !important; }




.UIComposer .UIComposer_Box  { background-color:#FFF3F4 !important; border-color:#F6B2B4 !important; }
.profile .box .box_header  { background-color:#FFF3F4 !important; border-color:#F6B2B4 !important; }




.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#F6B2B4 !important; }
