/* --- Whatnext light (by garky) ------------- */
/* --- Website: garky.freeblog.hu ------------ */
/* --- Please do not modify this script! ----- */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("whatnext.eu") {

/* --- Welcome & login page --- */

div#content div#msg {
color: #000  !important;
font-size: 14px  !important;
}

div#box {
background: #e4e4e4  !important;
border: 1px solid #999  !important;
-webkit-border-radius: 5px  !important;
-moz-border-radius: 5px  !important;
border-radius: 5px  !important;
-webkit-box-shadow:  0px 0px 5px 5px #999 !important;
box-shadow:  0px 0px 5px 5px #999 !important;      
}

/* --- Header --- */

div#top1, div#top2 {
background: #E2E2E2 !important;
}

div#top2 {
border-bottom: 2px solid #000 !important;
}

/* logo */

div#top1 {
background: url("http://i.imgur.com/iSmcv.png") no-repeat scroll 0px 0px #E2E2E2 !important;
}

/* hide old logo */
#top1 div img, div#top1 #p_wn img {
opacity: 0 !important;
}

/* users icon right top */
#top1 div:nth-child(3) {
background: url("http://i.imgur.com/k1sQi.png") no-repeat scroll 0px 16px transparent !important;
}

/* Menu */

div#menu {
margin-top: 1px  !important;
}

@-moz-keyframes glow {
	0%   {   text-shadow:
   -1px -1px 0 #E2E2E2,
    1px -1px 0 #E2E2E2,
    -1px 1px 0 #E2E2E2,
     1px 1px 0 #E2E2E2;   }
	50% {   text-shadow:
   -1px -1px 0 #F2F2F2,
    1px -1px 0 #F2F2F2,
    -1px 1px 0 #F2F2F2,
     1px 1px 0 #F2F2F2;  }
	100% {   text-shadow:
   -1px -1px 0 #E2E2E2,
    1px -1px 0 #E2E2E2,
    -1px 1px 0 #E2E2E2,
     1px 1px 0 #E2E2E2;  }
}
@-webkit-keyframes glow {
	0%   {   text-shadow:
   -1px -1px 0 #E2E2E2,
    1px -1px 0 #E2E2E2,
    -1px 1px 0 #E2E2E2,
     1px 1px 0 #E2E2E2;  }
	50% {   text-shadow:
   -1px -1px 0 #F2F2F2,
    1px -1px 0 #F2F2F2,
    -1px 1px 0 #F2F2F2,
     1px 1px 0 #F2F2F2;  }
	100% {   text-shadow:
   -1px -1px 0 #E2E2E2,
    1px -1px 0 #E2E2E2,
    -1px 1px 0 #E2E2E2,
     1px 1px 0 #E2E2E2;  }
}

div#top1 div#menu a.mod-box {
background: transparent !important;
padding: 4px !important;
border: none !important;
-webkit-border-radius: 0px !important;
-moz-border-radius: 0px !important;
border-radius: 0px !important;
color: #1981A3  !important;
font-size: 16px  !important;
-webkit-box-shadow: none !important;
box-shadow: none !important;
}
div#top1 div#menu a.mod-box.mod-active {
border-bottom: 1px dashed #1981A3  !important;
-moz-animation: glow 3s infinite  !important;
-webkit-animation: glow 3s infinite  !important;
-ms-animation: glow 3s infinite  !important;
}

/* User menu right top */

#usr1, #usr2, #usr3 {
background: #E2E2E2 !important;
}
#usr3 a {
color: #444 !important;
}
#usr3 a:hover {
color: #1981A3 !important;
}

/* ---  Page --- */

body {
background: #eee !important;
}
#content {
background: #eee !important;
}
.mainbox {
background: #E2E2E2 !important;
color: #000 !important;
}
.mainbox_title, .mainbox a {
color: #1981A3 !important;
font-size: 13px !important;
}
.mainbox div {
border-bottom: 1px dotted #000 !important;
}

/* --- Koveto --- */

.kovetoinput, .finput {
background: url("http://i.imgur.com/kQOqm.png") no-repeat scroll 5px 3px transparent !important;
-webkit-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
padding: 4px 4px 4px 22px  !important;
color: #000 !important;
text-shadow: none !important;
height: 15px  !important;
-webkit-border-radius: 5px  !important;
-moz-border-radius: 5px  !important;
border-radius: 5px  !important;
outline: none  !important;
}

.kbox {
background: #ccc !important;
color: #000 !important;
padding-left: 0px !important;
-webkit-border-radius: 2px !important;
-moz-border-radius: 2px !important;
border-radius: 2px !important;
}
.kbox div img {
background: #A4A4A4 !important;
}

.aleft, .aleft_on, .aright, .aright_on {
cursor: pointer  !important;
}

.aleft {
background: url("http://i.imgur.com/lLdSU.png") no-repeat scroll 0px 0pt transparent !important;
}
.aleft_on {
background: url("http://i.imgur.com/lLdSU.png") no-repeat scroll -24px 0pt transparent !important;
}
.aright {
background: url("http://i.imgur.com/lLdSU.png") no-repeat scroll -48px 0pt transparent !important;
}
.aright_on {
background: url("http://i.imgur.com/lLdSU.png") no-repeat scroll right center transparent !important;
}

.ep {
-webkit-border-radius: 2px !important;
-moz-border-radius: 2px !important;
border-radius: 2px !important;
margin-right: 2px !important;
padding: 1px !important;
}
.ep:hover .ep2{
background: #1981A3 !important;
}

.ep, .ep span{
background: #D5D5D5 !important;
color: #000 !important;
}

.e {
background: #1981A3 !important;
}
.ep2 {
background: #000 !important;
}

.ep1 {
background: #D5D5D5 !important;
}

.kd img{
background: #A5A5A5 !important;
}

/* kezelo button & gear icon */

div#content .active {
background: #999 url("http://i.imgur.com/WrNsf.png") no-repeat scroll 6px 6px !important;
width: 120px  !important;
height: 20px  !important;
border: none  !important;
color: #fff !important;
padding: 4px 3px 0px 20px  !important;
}

div#content .active:hover {
background: #1981A3 url("http://i.imgur.com/WrNsf.png") no-repeat scroll 6px 6px !important;
}

.xgr10, .xgr11, .xgr20, .xgr21, .xgr30, .xgr31, .xgr40, .xgr41, .xgr50, .xgr51 {
width: 8px !important;
height: 6px !important;
margin-top: 6px !important;
margin-bottom: 6px  !important;
border: 14px solid #e4e4e4 !important;
-webkit-border-radius: 3px  !important;
-moz-border-radius: 3px  !important;
border-radius: 3px  !important;
float: left  !important;
}

.xgr10:hover, .xgr11:hover, .xgr20:hover, .xgr21:hover, .xgr30:hover, .xgr31:hover, .xgr40:hover, .xgr41:hover, .xgr50:hover, .xgr51:hover {
border: 14px solid #999 !important;       
}

.xgr10, .xgr11 {
background: #48d85e !important;
background: -moz-linear-gradient(top,  #48d85e 0%, #39a83e 100%) !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#48d85e), color-stop(100%,#39a83e)) !important;
background: -webkit-linear-gradient(top,  #48d85e 0%,#39a83e 100%) !important;
background: -o-linear-gradient(top,  #48d85e 0%,#39a83e 100%) !important;
background: -ms-linear-gradient(top,  #48d85e 0%,#39a83e 100%) !important;
background: linear-gradient(top,  #48d85e 0%,#39a83e 100%) !important;
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#48d85e', endColorstr='#39a83e',GradientType=0 )!important;
margin-right: 3px !important;
}

.xgr20, .xgr21 {
background: #fff356 !important;
background: -moz-linear-gradient(top,  #fff356 0%, #e8cb4e 100%) !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fff356), color-stop(100%,#e8cb4e)) !important;
background: -webkit-linear-gradient(top,  #fff356 0%,#e8cb4e 100%) !important;
background: -o-linear-gradient(top,  #fff356 0%,#e8cb4e 100%) !important;
background: -ms-linear-gradient(top,  #fff356 0%,#e8cb4e 100%) !important;
background: linear-gradient(top,  #fff356 0%,#e8cb4e 100%) !important;
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fff356', endColorstr='#e8cb4e',GradientType=0 ) !important;
margin-right: 3px !important;
}
.xgr30, .xgr31 {
background: #ff0000 !important;;
background: -moz-linear-gradient(top,  #ff0000 0%, #e21616 100%) !important;;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ff0000), color-stop(100%,#e21616)) !important;;
background: -webkit-linear-gradient(top,  #ff0000 0%,#e21616 100%) !important;;
background: -o-linear-gradient(top,  #ff0000 0%,#e21616 100%) !important;;
background: -ms-linear-gradient(top,  #ff0000 0%,#e21616 100%) !important;;
background: linear-gradient(top,  #ff0000 0%,#e21616 100%) !important;;
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ff0000', endColorstr='#e21616',GradientType=0 ) !important;;
margin-right: 3px !important;
}
.xgr40, .xgr41 {
background: #f9c300 !important;
background: -moz-linear-gradient(top,  #f9c300 0%, #ff9400 100%) !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f9c300), color-stop(100%,#ff9400)) !important;
background: -webkit-linear-gradient(top,  #f9c300 0%,#ff9400 100%) !important;
background: -o-linear-gradient(top,  #f9c300 0%,#ff9400 100%) !important;
background: -ms-linear-gradient(top,  #f9c300 0%,#ff9400 100%) !important;
background: linear-gradient(top,  #f9c300 0%,#ff9400 100%) !important;
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f9c300', endColorstr='#ff9400',GradientType=0 ) !important;
margin-right: 3px !important;
}
.xgr50, .xgr51 {
background: #00a5ff !important;
background: -moz-linear-gradient(top,  #00a5ff 0%, #0032c9 100%) !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#00a5ff), color-stop(100%,#0032c9)) !important;
background: -webkit-linear-gradient(top,  #00a5ff 0%,#0032c9 100%) !important;
background: -o-linear-gradient(top,  #00a5ff 0%,#0032c9 100%) !important;
background: -ms-linear-gradient(top,  #00a5ff 0%,#0032c9 100%) !important;
background: linear-gradient(top,  #00a5ff 0%,#0032c9 100%) !important;
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00a5ff', endColorstr='#0032c9',GradientType=0 ) !important;
margin-right: 3px  !important;
}

.ui-tooltip-titlebar {
background: #1981A3 !important;
color: #fff !important;
}
.ui-tooltip-content {
background: #ccc !important;
color: #000 !important;
text-shadow: none !important;
}

.ui-tooltip-content a {
color: #000  !important;
}

.ui-tooltip-content a:hover {
color: #1981A3  !important;
}


.ui-tooltip-content a.mod-box {
background: #999 !important; 
color: #fff !important;
text-shadow: none !important;
-webkit-box-shadow: none !important;
box-shadow: none !important;
}
.ui-tooltip-content a.mod-box:hover {
background: #1981A3 !important;
color: #fff !important;
}

/* --- Kezelo --- */

.lists {
background: #ccc !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
margin-right: 2px !important;
padding: 5px 5px 100px 5px !important;
}
.green {
-webkit-box-shadow: 0pt 1px 2px #39a83e !important;
box-shadow: 0pt 1px 2px #39a83e !important;
border: 1px solid #39a83e !important;
}
.yellow {
-webkit-box-shadow: 0pt 1px 2px #e8cb4e !important;
box-shadow: 0pt 1px 2px #e8cb4e !important;
border: 1px solid #e8cb4e !important;
}
.red {
-webkit-box-shadow: 0pt 1px 2px #e21616 !important;
box-shadow: 0pt 1px 2px #e21616 !important;
border: 1px solid #e21616 !important;
}
.orange {
-webkit-box-shadow: 0pt 1px 2px #ff9400 !important;
box-shadow: 0pt 1px 2px #ff9400 !important;
border: 1px solid #ff9400 !important;
}
.blue {
-webkit-box-shadow: 0pt 1px 2px #0032c9 !important;
box-shadow: 0pt 1px 2px #0032c9 !important;
border: 1px solid #0032c9 !important;
}
.kkbox {
background: #D5D5D5 !important;
color: #000 !important;
padding-left: 0px !important;
border-radius: 0px 5px 5px 0px !important;
}

/* Vissza a kovetore  */
div#content center a.active {
background: #999 !important;
padding: 5px  !important;
}

div#content center a.active:hover {
background: #1981A3 !important;
padding: 5px  !important;
}

/* --- Sorozatok --- */

#almenu a {
color: #1981A3 !important;
text-shadow: none !important;
padding: 1px !important;
}
#almenu a:hover {
text-decoration: none !important;
border-bottom: 1px dashed #1981A3  !important;
}
#searchbox {
background: transparent !important;
-webkit-box-shadow: none !important;
box-shadow: none !important;
border: 0px solid #ccc !important;
text-align:left!important;
padding: 0px  !important;
color: #000  !important;
}
#searchbox form a.mod-box {
float: left  !important;
color: #fff  !important;
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
text-decoration: none  !important;
background: #BEBEBE !important;
}
#searchbox input.input {
background: transparent  !important;
-webkit-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
padding-left: 5px !important;
color: #000 !important;
text-shadow: none !important;
height: 15px  !important;
outline: none  !important;
}
#searchbox input.input.inpsub, .genrebox input.input.inpsub {
background: #1981A3 !important;
padding: 2px !important;
color: #fff !important;
height: 22px  !important;
text-shadow: none  !important;
}

input#genres.input, input#statuses.input {
background: transparent !important;
text-shadow: none  !important;
color: #000  !important;
}

.genrebox {
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
background: #D5D5D5 !important;
border: 1px solid #999 !important;
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}
.genrebox a {
color: #000 !important;
}
.genrebox h2 {
text-shadow: none  !important;
}

.sorozatbox {
background: #D5D5D5 !important;
-webkit-box-shadow: none !important;
box-shadow: none !important;
border: 1px solid #999 !important;
border-radius: 10px !important;
margin-bottom: 15px !important;
}
.sorozatbox td, .sorozatbox td.SXE, .sorozatbox td.ename {
background: #ccc !important;
border: 1px solid #999 !important;
text-shadow: none !important;
}

span.mod-box2 {
background: #999 !important;
color: #fff !important;
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
}

span.mod-box2.mod-act {
background: #1981A3 !important;
color: #fff !important;
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
}

.wuser {
text-shadow: none !important;
}

.wuser:hover {
color: #1981A3 !important;
}

.sbox_sname {
text-shadow: none !important;
}

.sstat {
text-shadow: none !important;
}

.sbox_content{
background: transparent !important;
}

/* Hidden stuff */

.sbox_content div {
display: none !important;
}

.sactive {
background: url("http://i.imgur.com/C1MFi.png") no-repeat scroll 0% 0% transparent !important;
font-weight: bold !important;
}

.slink.sactive {
color: #fff !important;
}

.slink {
color: #444 !important;
}

/* --- Airdate --- */

.emptyep {
background: #D5D5D5 !important;
}

.emptyep.weekend {
background: #ccc !important;
}

div#searchbox div.airdatepanel .airep, div#searchbox div.airdatepanel .marked {
background: #1981A3 !important;
font-weight: bold !important;
color: #fff !important;
}

.calendar_item {
-webkit-box-shadow: none !important;
box-shadow: none !important;
background: #D5D5D5 !important;
color: #000  !important;
}

.weekendcalendar {
background: #ccc !important;
}

/* --- User page & config --- */

div#searchbox div.kektitle {
background: #D5D5D5 !important;
border: 1px solid #999  !important;
color: #000  !important;
}

div#searchbox div {
background: transparent  !important;
color: #000  !important;
}

div#searchbox table.config, div#searchbox table.config tr:hover {
color:#000  !important;
background: #eee  !important;
border: none !important;
}

div#searchbox a.mod-box {
color: #000  !important;
text-decoration: none  !important;
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
}

div#searchbox a.mod-box:hover {
background: #1981A3 !important;
color: #fff !important;
}

.button {
-webkit-border-radius: 3px  !important;
-moz-border-radius: 3px  !important;
border-radius: 3px  !important;
}

#owns a {
padding: 5px  !important;
color: #fff  !important;
}

#owns a:hover {
background: #1981A3 !important;
}

.title, .title2 {
text-shadow: none  !important;
}
select.input {
background: transparent  !important;
padding-left: 3px  !important;
text-shadow: none  !important;
color: #000  !important;
}
#resp {
display:none!important;
}

div#searchbox table {
background: #E2E2E2  !important;
border: 1px solid #999 !important;
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
border-spacing: 0px 1px !important;
}

div#searchbox table tbody tr:hover {
background: #989898  !important;
color: #fff  !important;
}

/* --- Users list --- */

#userlist {
border: 1px solid #ccc  !important;
border-radius: 5px  !important;
}

div#searchbox div#userlist table#userstable {
border: none  !important;
}

div#searchbox div#userlist table#userstable tbody tr {
background: #D5D5D5 !important;
}

div#searchbox div#userlist table#userstable tbody tr:hover {
background: #D5D5D5 !important;
color: #000  !important;
}

#userstable a {
text-decoration: none  !important;
color: #1981A3  !important;
font-weight: bold  !important;
}

/* --- Stat --- */

div#searchbox div div.mod-box {
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
}

div#searchbox table.stat {
border-spacing: 0px !important;
-webkit-border-radius: 4px !important;
-moz-border-radius: 4px !important;
border-radius: 4px !important;
}

table.stat td, table.stat td:hover {
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
}

div#searchbox table.stat tbody tr:hover {
background: #E2E2E2 !important;
color: #000  !important;
font-weight: normal !important;
}

td.val, th.header {
background: #1981A3 !important;
-webkit-box-shadow: none  !important;
box-shadow: none  !important;
text-shadow: none  !important;
border: none  !important;
color: #fff  !important;
-webkit-border-radius: 0 !important;
-moz-border-radius: 0 !important;
border-radius: 0 !important;
}
table#stat-sort {
text-align: center  !important;
background: #E2E2E2  !important;
}

table#stat-sort.stat.tablesorter {
border-spacing: 0px 1px !important;
}

div#searchbox table#stat-sort  tbody tr:hover {
background: #989898  !important;
color: #fff  !important;
}

/* --- Footer  --- */

.ftr {
background: #E2E2E2 !important;
border-top: 2px solid #000 !important;
}
.ftr a {
color: #1981A3 !important;
text-shadow: none !important;
}
.ftr div {
vertical-align: middle !important;
}

/* Redesign signo */

.ftr div:nth-child(1):after {
content: '\00a0 \003c \00a0 Design/Userstyle: garky.freeblog.hu \003e' !important;
color: #1981A3 !important;
font-size: 10px !important;
}

/* --- end of style --- */
}
