@-moz-document domain("twitter.com") {

/*** SECTIONS FOR YOU TO EDIT ***/ 

/** User, Remove this section to display advertising/promotions **/
/* Note: Advertising allows the internet to be free. If you're not willing to pay for a product or service, it makes sense to accept the adverts that pay for products and services on your behalf. */
  .promoted-account, .promoted-trend, .promoted-tweet{display:none !important;}

/** Highlighting tweets from a particular account, and links to a particular account **/
  [href="/#!//*[[UsernameforLinks]]*/"]{background-color:/*[[ColourforLinks]]*/ !important;}
  [data-element-term="tweet_stats"], .list-link, .pretty-link.js-screen-name{background-color:White !important;}
  .account-group.js-account-group.js-action-profile.js-user-profile-link[href="/#!//*[[UsernameforTweets]]*/"], [data-screen-name="/*[[UsernameforTweets]]*/"]{background-color:/*[[ColourforTweets]]*/ !important;}
  .profile-card-inner.js-actionable-user{background-color:White !important;}
  .twitter-atreply.pretty-link[href="/#!//*[[UsernameforLinks]]*/"]{background-color:/*[[ColourforLinks]]*/ !important;}
  
/*** ONLY EDIT BELOW THIS IF YOU REALLY KNOW WHAT YOU'RE DONG! ***/
/** EDIT AT YOUR OWN RISK **/

/*Remove Elements*/
  .front-welcome, .front-signup, .footer,.global-nav .search-icon, div.profile-banner, .tweet-box-title, .dropdown-menu.typeahead, .dropdown-divider, [href="//support.twitter.com"], .js-keyboard-shortcut-trigger, .module.site-footer{display:none !important;}
  #page-container{background-image: none !important;}

/*Login Screen*/
  .front-signin{position:fixed !important; top:50% !important; left:50% !important; margin-left:-151px !important;}
  .front-card{width:300px !important; height:300px !important; margin:-300px 0 0 -150px !important; background-image:url(data:image/png;base64,) !important;}
  .form-search{display:block !important;}
  .nav.secondary-nav.language-dropdown{visibility:hidden !important}

/*[[SizingAndSidebar]]*/

/*Other*/
  .nav-breaker{display:none !important;}
  a#close-all-button.close-all-tweets{left:50% !important; margin-left:-18px !important; margin-top:10px !important; background-image: url("data:image/png;base64,"); background-repeat:no-repeat;  opacity:0.9 !important;}
  .last-new-tweet {border: solid #C6E4F2 !important; border-width: 2px 0 !important;}
  .avatar.size128:hover{position:absolute !important; width: 40% !important; height: 200% !important; left:0px !important; z-index:36 !important; -moz-transition-duration: 700ms !important;}
  .avatar.js-action-profile-avatar:hover{position:absolute !important; width: 10% !important; height: 100% !important; left:0px !important; z-index:36 !important; -moz-transition-duration: 500ms !important;}
}

/*Logout Screen*/  
@-moz-document url-prefix("https://twitter.com/download"), url-prefix("https://twitter.com/download"), url-prefix("https://www.twitter.com/download"), url-prefix("https://www.twitter.com/download"){
  #page-container{background:none! important;}
  html body.default-theme div#doc.route-mobile-gallery div.topbar div.global-nav div.global-nav-inner div.container ul#session.nav li.dropdown a#signin-link.dropdown-toggle small, .dropdown.js-language-dropdown, .gallery-link, .mobile-gallery-app.mobile-gallery-wp7, .module.site-footer, .gallery, .actions.yes-script, .mobile-footer{display:none !important;}
  .page-canvas{position:fixed !important; top:48% !important; left: 50% !important; margin-left: -418px !important; height: 65px !important}
  html body.default-theme div#doc.route-mobile-gallery div#page-outer div#page-container.wrapper div.page-canvas h1{display:none !important;}  
  .page-canvas:after{content:"You are now signed out of Twitter. \00000a \00000a Thank you for using Better Twitter!" !important; white-space: pre !important; font-size:200% !important; font-weight: bold !important; text-align: center !important; position:fixed !important; top: 49% !important; left: 50% !important; margin-left:-230px !important}
}

