css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hordes.fr") {
    
    #tid_forum table.tid_forumLayout td#tid_forum_left { /* Masque la liste des sujets */
        
        visibility: hidden !important;
        width: 0% !important;
        padding: 0px !important;
    }    
    #tid_forum #tid_forum_left .tid_mainBar .tid_actionBar { /* Affiche les boutons du volet de gauche */
        
        visibility: visible !important;
        position: relative !important;
       /* left: 695px !important; */
        width: 185px !important;
    }
    #tid_forum .tid_mainBar .tid_stack { /* Arbo du forum actuel */
        
        visibility: visible !important;
        width: 882px !important;
    }
    #tid_forum table.tid_forumLayout td#tid_forum_right { /* Marge haute sur la liste des posts pour faire la place */
        
        padding-top: 50px !important;
        border-left: none !important;
        padding-left: 0px !important;
    }
    
    /* RECTIFICATIONS */
    
    #tid_forum.tid_forumHome table.tid_forumLayout td#tid_forum_left { /* Affiche la liste des sections du forum */
        
        visibility: visible !important;
        width: 70% !important;
        
    }
    #tid_forum .tid_dashboard {
        
        margin-top: -50px !important;
        margin-left: 10px !important;
    }
    
}
