css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.filestube.com") {

    /* Main page */

    /* Disable top options */

    #top_options {
    
        display : none !important ;
    }

    /* Disable right feedback button */

    a.fb_image {
    
        display : none !important ;
    }
    
    /* Disable trends */
    
    div.trends {
    
        display : none !important ;
    }
    
    /* Disable Social buttons */
    
    #ac_form + div {
    
        display : none !important ;
    }
    
    /* Disable information */
    
    body > p {
    
        display : none !important ;
    }
    
    /* Query page */
    
    /* Disable google plus */
    
    #___plusone_1 {
    
        display : none !important ;
    }
    
    /* Disable top options */
    
    div.book_sub {
    
        display : none !important ;
    }
    
    /* Disable social buttons */
    
    div.linkbox {
    
        display : none !important ;
    }
    
    /* Centralize logo */
    
    a.logo_sub {
    
        float : none !important ;
        margin : auto !important ;
    }
    
    /* Centralize search bar */
    
    #ac_form {
    
        float : none !important ;
        margin : auto !important ;
    }
    
    /* Centralize content */
    
    div.content {
    
        margin : auto !important ;
        width : 90% !important ;
    }
    
    /* Left column */
    
    div.results_left {
    
        margin : 0px !important ;
        padding : 0px !important ;
        border-width : 0px !important ;
        width : 15% !important ;
    }
    
    /* Right column */
    
    #results_box {
    
        float : right !important ;
        margin : 0px !important ;
        padding : 0px !important ;
        border-width : 0px !important ;
        width : 80% !important ;
    }
    
    #results , .fsResultsBox {
    
        margin : 0px !important ;
        padding : 0px !important ;
        border-width : 0px !important ;
        width : 100% !important ;
    }
    
    /* Disable line feed at the begging of query results */
    
    #results > br {
    
        display : none !important ;
    }
    
    /* Distance between query results */
    
    #newresult , .fsResultsEntry {
    
        margin-bottom : 20px !important ;
    }
    
    /* Make space for shadow on the right of query results */
    
    #newresult , .fsResultsEntry {
    
        margin-right : 5px !important ;
    }
    
    /* Download button position and size */
    
    a.resultsLink + div + div + div + div > a + a , a.resultsLink + div + div + div + div + div > a + a  {
    
        float : right !important ;
        padding : 2px !important ; 
        padding-right : 10px !important ; 
        padding-left : 10px !important ;
    }
    
    /* Space between page navigation buttons */
    
    a.resultsLink2a , span.resultsLink3a {
    
        margin : 3px ;
    }
    
    /* Query result link */
    
    a.resultsLink {
    
        font-weight : bold !important ;
    }
    
    /* Disable "Also try:" */
    
    #results > br + div + div + div + div + div {
    
        display : none !important ;
    }
    
    div#results_box + br + div {
    
        display : none !important ;
    }
    
    /* Disable recent searchs */
    
    #recent {
    
        display : none !important ;
    }
    
    /* Disable information at the bottom */
    
    .content + div + div {
    
        display : none !important ;
    }
    
    /* Disable social button */
    
    #ac_results + div {
    
        display : none !important ;
    }
    
    /* Disable sponsored results */
    
    .nova4box , .nova4box + div {
    
        display : none !important ;
    }
    
    /* Disable advertize */
    
    .resultadv2 {
    
        display : none !important ;
    }
    
    /* Download page */
    
    /* Disable advertize */
    
    .boxpromo , #google_ads_div_FilesTube_Details_637x89_ad_wrapper , #google_ads_div_Filestube_top_728_90_ad_wrapper {
    
        display : none !important ;
    }
    
    /* Remove size from wrapper */
    
    .new_f_profile , .new_f_profile > script + div {
    
        margin : 0px !important ;
        padding : 0px !important ;
        border-width : 0px !important ;
        width : 100% !important ;
    }
    
    .new_f_profile > br {
    
        display : none !important ;
    }
    
    .new_f_profile > br + div {
    
        display : none !important ;
    }
    
    /* Title */
    
    .dotter {
    
        background : none !important ;
    }
    
    .dotter h1 {
    
        width : 100% !important ;
        text-align : center !important ;
    }
    
    /* Disable social buttons */
    
    #fb-root {
    
        display : none !important ;
    }
    
    /* Download box */
    
    .new_f_brd {
    
        width : 100% !important ;
        text-align : center !important ;
        background : none !important ;
    }
    
    /* Space for box shadow */
    
    .new_f_profile {
    
        margin : auto !important ;
        width : 100% !important ;
        padding : 0px !important ;
        border-width : 0px !important ;
    }
    
    .new_f_profile > script + div {
    
        float : none !important ;
        width : 90% !important ;
        margin : auto !important ;
    }
    
    /* Disable: Download now from uploaded */
    
    .new_f_brd > h3 {
    
        display : none !important ;
    }
    
    /* Disable advertize */
    
    .file_details_title {
    
        display : none !important ;
    }
    
    /* Disable right column */
    
    .n_right_detail {
    
        display : none !important ;
    }
    
    /* Disable horizontal separator */
    
    #js_alt_options , .detend {
    
        display : none !important ;
    }
    
    /* Disable social buttons */
    
    .file_details_title + div , .file_details_title + div + div {
    
        display : none !important ;
    }
    
    /* Download button size */
    
    .gobut {
    
        padding : 0px !important ;
    }
    
    /* Throughout the site */
    
    /* Button */
    
    input.BUTTON , a.resultsLink + div + div + div + div > a + a , a.resultsLink + div + div + div + div + div > a + a , a.resultsLink2a , span.resultsLink3a , .gobut {

        font-weight : bold !important ;
        text-decoration : none !important ;
        color : white !important ;
        background : -moz-linear-gradient(right top , #155285 , #008CFF) repeat scroll 0 0 transparent !important;
    }
    
    a.resultsLink + div + div + div + div > a + a , a.resultsLink + div + div + div + div + div > a + a , div.fsUrl > a + a , a.resultsLink2a , span.resultsLink3a , .gobut  {
    
        border : 2px solid #003B6B !important ;
        border-radius : 15px !important ; 
        box-shadow: 2px 2px 4px black;
    }
    
    input.BUTTON:hover , a.resultsLink + div + div + div + div > a + a:hover , a.resultsLink + div + div + div + div + div > a + a:hover , a.resultsLink2a:hover , span.resultsLink3a:hover , .gobut:hover {
    
        background : -moz-linear-gradient(right top , #2A6FA8 , #79C1FC) repeat scroll 0 0 transparent !important;
    }
    
    /* Frame */
    
    div.results_left , #newresult , .fsResultsEntry , .new_f_profile > script + div , #js_files_list {
    
        border : 2px solid #0058A1 !important ;
        border-radius : 10px !important ;
        padding : 10px !important ;
        background-color : white !important ;
        box-shadow: 2px 2px 4px #888888 ;
    }
}
